/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	if ( sServiceName = "common-service:searchCode" ) {			// 공통코드조회후 해당 지점의 상품분류 조회
		fcSearchGoodType(this);
	} else if ( sServiceName = "rem5020-service:searchGoodType" ) {	// 상품분류 조회후 상품 내역 조회
		fcSearch(this);
	} else if ( sServiceName = "rem5070-service:searchYearItem" ) {	// 상품분류 조회후 상품 내역 조회

	}	
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	switch ( obj.id ) {
		case "cboSearchBrncCd":
Trace(cboSearchBrncCd.value);
Trace(gdsUser.GetColumn(0, "BRNC_CD"));
Trace(sApro);

			if (cboSearchBrncCd.Value != fnc_NVL(sMnAuthRmk, gdsUser.GetColumn(0, "BRNC_CD")) && sApro != "T") {
				grdYearItem.Editable = false;
				divBtnRow.Visible = false;
			} else {
				grdYearItem.Editable = true;
				divBtnRow.Visible = true;
			}
			setReg("BRNC_CD", cboSearchBrncCd.Value);

			fcSearchGoodType();			 
			break;
	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {		
		case "dsBrncCd":
			dsCommNo.copy(dsBrncCd);
			if (sApro == "T") {
				dsCommNo.InsertRow(0);
				dsCommNo.SetColumn(0, "CD", "%");
				dsCommNo.SetColumn(0, "CD_NM", "전체");
			} else {
				//cboSearchBrncCd.Value = GdsUser.GetColumn(0, "BRNC_CD");
				dsCommNo.Filter("CD_TERM2='"+GdsUser.GetColumn(0, "TEAM_CD")+"'");	
			}
			//cboSearchBrncCd.index = 0;
			cboSearchBrncCd.Value = getReg("BRNC_CD");

			if (cboSearchBrncCd.Value != fnc_NVL(sMnAuthRmk, gdsUser.GetColumn(0, "BRNC_CD")) && sApro != "T") {
				grdYearItem.Editable = false;
				divBtnRow.Visible = false;
			} else {
				grdYearItem.Editable = true;
				divBtnRow.Visible = true;
			}

		break;		
	}
}




/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	switch( obj.GetForm().id ) {
		case "divBtnRow" :
			nRow = dsYearItem.AddRow();			
			dsYearItem.SetColumn(nRow, "COMM_NO", cboSearchBrncCd.Value);	
			if (nRow == 0) {
				dsYearItem.SetColumn(nRow, "STR_DT", mid(today(),0,4)+"0101");	
				dsYearItem.SetColumn(nRow, "END_DT", mid(today(),0,4)+"1231");	
			} else {
				dsYearItem.SetColumn(nRow, "STR_DT", dsYearItem.GetColumn(nRow -1, "STR_DT"));	
				dsYearItem.SetColumn(nRow, "END_DT", dsYearItem.GetColumn(nRow -1, "END_DT"));	
				dsYearItem.SetColumn(nRow, "GOOD_TYPE_CD", dsYearItem.GetColumn(nRow -1, "GOOD_TYPE_CD"));	
				dsYearItem.SetColumn(nRow, "UNIT", dsYearItem.GetColumn(nRow -1, "UNIT"));	
			}

			break;		
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//직접 버튼을 클릭한 경우
	switch( obj.GetForm().id ) {
		case "divBtnRow" :
			if ( !fnc_DeleteCheck(dsYearItem, "CHK", "1") ) return;
			break;
	}	
}
function fcOnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if (nCell == 1) {
		fnc_SetPopupDivCalendar(obj, nRow, nCell);		
	}
}
