function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;
	/*
	if(Tab0.TabIndex == 0){	

		sServiceName = "rem6011-service:searchTradeSum";
		sInDataSet   = "";
		sOutDataSet  = "dsTradeList=dsTradeList ";

		//조회조건 : 
		fcd_AddParam("STR_DT",		calRaceStrDt.Value);
		fcd_AddParam("END_DT",		calRaceEndDt.Value);
		fcd_AddParam("MEET_CD",		cboGubun.Value);
		fcd_AddParam("COMM_NO", 	cboSearchBrncCd.Value);
		fcd_AddParam("USER_ID", 	sUserID);

    }
    */
    if(Tab0.TabIndex == 0){	

		sServiceName = "rem6011-service:searchTradeKm";
		sInDataSet   = "";
		sOutDataSet  = "dsTradeKm=dsTradeKm ";

		//조회조건 : 
		fcd_AddParam("STR_DT",		calRaceStrDt.Value);
		fcd_AddParam("END_DT",		calRaceEndDt.Value);
		fcd_AddParam("USER_ID", 	sUserID);

    }else if(Tab0.TabIndex == 1){	

		sServiceName = "rem6011-service:searchTradeMsr";
		sInDataSet   = "";
		sOutDataSet  = "dsTradeMsr=dsTradeMsr ";

		//조회조건 : 
		fcd_AddParam("STR_DT",		calRaceStrDt.Value);
		fcd_AddParam("END_DT",		calRaceEndDt.Value);
		fcd_AddParam("USER_ID", 	sUserID);

    }	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


