function fcSaveCheck(obj) {
	/*
	if(Tab0.TabIndex == 0){	
		// 변경된 자료가 있는지 체크
		if ( !fnc_DatasetChangeCheck(dsTradeList) ) {
			// 저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");
			return false;
		}

		// 필수항목체크
		oDataSet          = dsTradeList;
		sMandatoryColID   = "TRADE_DATE|MEET_CD|COMM_NO|CNT";
		sMandatoryColName = "입장일자|구분|지점|무료입장인원";
		oGrid             = Tab0.tab1.grdTradeList;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	}
	*/
	if(Tab0.TabIndex == 0){	
		// 변경된 자료가 있는지 체크
		if ( !fnc_DatasetChangeCheck(dsTradeKm) ) {
			// 저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");
			return false;
		}

		// 필수항목체크
		oDataSet          = dsTradeKm;
		sMandatoryColID   = "TRADE_DATE|CNT";
		sMandatoryColName = "입장일자|입장인원";
		oGrid             = Tab0.tab2.grdTradeKm;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	}else if(Tab0.TabIndex == 1){	
		// 변경된 자료가 있는지 체크
		if ( !fnc_DatasetChangeCheck(dsTradeMsr) ) {
			// 저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");
			return false;
		}

		// 필수항목체크
		oDataSet          = dsTradeMsr;
		sMandatoryColID   = "TRADE_DATE|CNT";
		sMandatoryColName = "입장일자|무료입장인원";
		oGrid             = tab0.tab3.grdTradeMsr;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	}

	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	if(dsTradeList.GetColumn(dsTradeList.currow, "USER_ID") == null){
		for(var i=0; i<dsTradeList.rowcount; i++){
			dsTradeList.SetColumn(i, "USER_ID", sUserID);
		}
	}

	if(dsTradeKm.GetColumn(dsTradeKm.currow, "USER_ID") == null){
		for(var i=0; i<dsTradeKm.rowcount; i++){
			dsTradeKm.SetColumn(i, "USER_ID", sUserID);
		}
	}

	if(dsTradeMsr.GetColumn(dsTradeMsr.currow, "USER_ID") == null){
		for(var i=0; i<dsTradeMsr.rowcount; i++){
			dsTradeMsr.SetColumn(i, "USER_ID", sUserID);
		}
	}

	/*
	if(Tab0.TabIndex == 0){	

		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";

		sServiceName = "rem6011-service:saveTradeList";
		sInDataSet   = "dsTradeList=dsTradeList:u";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);	

	}
	*/
	if(Tab0.TabIndex == 0){	

		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";

		sServiceName = "rem6011-service:saveTradeListKm";
		sInDataSet   = "dsTradeKm=dsTradeKm:u";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	}else if(Tab0.TabIndex == 1){	

		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";

		sServiceName = "rem6011-service:saveTradeListMsr";
		sInDataSet   = "dsTradeMsr=dsTradeMsr:u";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	}
}


