***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if(ErrorCode <0) {      //oracle 17410 error 발생으로 인한 임시조치
		ErrorMSG = "처리시간이 지연되고 있습니다. 다시 조회하세요";
		alert(ErrorMSG);
		return;
	}
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 - 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {

	}

	// 티머니입장인원 - 조회 완료 후
	if ( sServiceName == "rem6011-service:searchTradeSum" ) {

	}

	// 광명 현금 입장인원 - 조회 완료 후
	if ( sServiceName == "rem6011-service:searchTradeSum" ) {

	}

	// 미사리 현금 입장인원 - 조회 완료 후
	if ( sServiceName == "rem6011-service:searchTradeSum" ) {

	}

	// 티머니 입장인원 - 저장 완료 후
	if ( sServiceName == "rem6011-service:saveTradeList" ) {
		alert("저장이 완료되었습니다.");
		fcSearch();
	}

	// 광명 현금 입장인원 - 저장 완료 후
	if ( sServiceName == "rem6011-service:saveTradeListKm" ) {
		alert("저장이 완료되었습니다.");
		fcSearch();
	}

	// 미사리 현금 입장인원 - 저장 완료 후
	if ( sServiceName == "rem6011-service:saveTradeListMsr" ) {
		alert("저장이 완료되었습니다.");
		fcSearch();
	}

}

