/*-------------------------------------------------------------------------------------------------+
>>  티머니 그리드 컬럼 변경 시 발생 이벤트
+-------------------------------------------------------------------------------------------------*/
function dsTradeList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var oldColumn = "";
	var newColumn = "";

	if ( dsTradeList.GetRowType(nRow) == "insert" ) {
		newColumn = "|"+dsTradeList.GetColumn(dsTradeList.currow, "TRADE_DATE")+":"+dsTradeList.GetColumn(dsTradeList.currow, "MEET_CD")+":"+dsTradeList.GetColumn(dsTradeList.currow, "COMM_NO")+"|";

		for(var i=0; i<dsTradeList.rowcount; i++){
			if(i==dsTradeList.currow) continue;
			oldColumn += "|"+dsTradeList.GetColumn(i, "TRADE_DATE")+":"+dsTradeList.GetColumn(i, "MEET_CD")+":"+dsTradeList.GetColumn(i, "COMM_NO")+"|";
		}

		if(IndexOf(oldColumn, newColumn)!=-1){
			alert("중복된 지점코드가 있습니다. \n 다시 확인해 주세요.");
			dsTradeList.SetColumn(dsTradeList.currow, "COMM_NO", "");
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  광명현금 그리드 컬럼 변경 시 발생 이벤트
+-------------------------------------------------------------------------------------------------*/
function dsTradeKm_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var oldColumnKm = "";
	var newColumnKm = "";

	if ( dsTradeKm.GetRowType(nRow) == "insert" ) {
		newColumnKm = "|"+dsTradeKm.GetColumn(dsTradeKm.currow, "TRADE_DATE")+"|";

		for(var i=0; i<dsTradeKm.rowcount; i++){
			if(i==dsTradeKm.currow) continue;
			oldColumnKm += "|"+dsTradeKm.GetColumn(i, "TRADE_DATE")+"|";
		}

		if(IndexOf(oldColumnKm, newColumnKm)!=-1){
			alert("중복된 입장일자가 있습니다. \n 다시 확인해 주세요.");
			dsTradeKm.SetColumn(dsTradeKm.currow, "TRADE_DATE", "");
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  미사리현금 그리드 컬럼 변경 시 발생 이벤트
+-------------------------------------------------------------------------------------------------*/
function dsTradeMsr_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var oldColumnMsr = "";
	var newColumnMsr = "";

	if ( dsTradeMsr.GetRowType(nRow) == "insert" ) {
		newColumnMsr = "|"+dsTradeMsr.GetColumn(dsTradeMsr.currow, "TRADE_DATE")+"|";

		for(var i=0; i<dsTradeMsr.rowcount; i++){
			if(i==dsTradeMsr.currow) continue;
			oldColumnMsr += "|"+dsTradeMsr.GetColumn(i, "TRADE_DATE")+"|";
		}

		if(IndexOf(oldColumnMsr, newColumnMsr)!=-1){
			alert("중복된 입장일자가 있습니다. \n 다시 확인해 주세요.");
			dsTradeMsr.SetColumn(dsTradeMsr.currow, "TRADE_DATE", "");
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  광명현금 그리드 클릭 시 발생 이벤트
+-------------------------------------------------------------------------------------------------*/
function Tab0_tab2_grdTradeKm_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( dsTradeKm.GetRowType(nRow) == "update" ) {
		if(dsTradeKm.GetColumn(nRow, "CHK") == 1){
			Tab0.tab2.grdTradeKm.SetCellProp("body", 2, "Edit", "number");
		}else if(dsTradeKm.GetColumn(nRow, "CHK") != 1){
			Tab0.tab2.grdTradeKm.SetCellProp("body", 2, "Edit", "none");
		}	
	}else if ( dsTradeKm.GetRowType(nRow) == "insert" ) {
		Tab0.tab2.grdTradeKm.SetCellProp("body", 2, "Edit", "number");
	}else{
		Tab0.tab2.grdTradeKm.SetCellProp("body", 2, "Edit", "none");
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  미사리현금 그리드 클릭 시 발생 이벤트
+-------------------------------------------------------------------------------------------------*/
function Tab0_tab3_grdTradeMsr_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( dsTradeMsr.GetRowType(nRow) == "update" ) {
		if(dsTradeMsr.GetColumn(nRow, "CHK") == 1){
			Tab0.tab3.grdTradeMsr.SetCellProp("body", 2, "Edit", "number");
		}else if(dsTradeMsr.GetColumn(nRow, "CHK") != 1){
			Tab0.tab3.grdTradeMsr.SetCellProp("body", 2, "Edit", "none");
		}	
	}else if ( dsTradeMsr.GetRowType(nRow) == "insert" ) {
		Tab0.tab3.grdTradeMsr.SetCellProp("body", 2, "Edit", "number");
	}else{
		Tab0.tab3.grdTradeMsr.SetCellProp("body", 2, "Edit", "none");
	}
}
