	var sDataset = "dsCash";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;

}


/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

}


/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	dsCash.ClearData();	
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);
	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;

	sServiceName = "rem6012-service:searchCash";
	sInDataSet   = "";
	sOutDataSet  = "dsCash=dsCash ";

	//조회조건 : 
	fcd_AddParam("STR_DT",		calRaceStrDt.Value);
	fcd_AddParam("END_DT",		calRaceEndDt.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {	
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsCash) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsCash;
	sMandatoryColID   = "SDATE|CNT";
	sMandatoryColName = "입장일자|입장인원";
	oGrid             = grdCash;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rem6012-service:saveCash";
	sInDataSet   = "dsCash=dsCash:u";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	dsCash.AddRow(); // Record Add

	grdCash.SetCellPos(1);
	grdCash.SetFocus(); // 입력 첫 항목으로 Focus

}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	// DataSet의 Header 정보가 있을 때  Add 처리
	if ( !fnc_DeleteCheck(dsCash, "chk", "1") ) return;

}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {

	var excelNm = "미사리현금입장인원";

	sArg = "objGridId=" + "grdCash";
	sArg += iif(nStartCell!=null, " nStartCell=" + quote(nStartCell), "");
	sArg += iif(bExcYn!=null, " sExcYn=" + quote(bExcYn), "");
	sArg += iif(Length(sExcelFileNm)>0, " sExcelFileNm=" + quote(excelNm), "");

	var dlgRtn = Dialog("common::comExcelDown.xml", sArg, '440','170', true, -1, -1);

}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();

}


