/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if(ErrorCode <0) {      //oracle 17410 error 발생으로 인한 임시조치
		ErrorMSG = "처리시간이 지연되고 있습니다. 다시 조회하세요";
		alert(ErrorMSG);
		return;
	}
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 - 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {

	}

	// 미사리 현금 입장인원 - 조회 완료 후
	if ( sServiceName == "rem6012-service:searchCash" ) {

	}

	// 미사리 현금 입장인원 - 저장 완료 후
	if ( sServiceName == "rem6012-service:saveCash" ) {
		alert("저장이 완료되었습니다.");
		fcSearch();
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 컬럼 변경 시 발생 이벤트
+-------------------------------------------------------------------------------------------------*/
function dsCash_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var oldColumnKm = "";
	var newColumnKm = "";

	if ( dsCash.GetRowType(nRow) == "insert" ) {
		newColumnKm = "|"+dsCash.GetColumn(dsCash.currow, "SDATE")+"|";

		for(var i=0; i<dsCash.rowcount; i++){
			if(i==dsCash.currow) continue;
			oldColumnKm += "|"+dsCash.GetColumn(i, "SDATE")+"|";
		}

		if(IndexOf(oldColumnKm, newColumnKm)!=-1){
			alert("중복된 입장일자가 있습니다. \n 다시 확인해 주세요.");
			dsCash.SetColumn(dsCash.currow, "SDATE", "");
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 컬럼 클릭 시 발생 이벤트
+-------------------------------------------------------------------------------------------------*/
function grdCash_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( dsCash.GetRowType(nRow) == "update" ) {
		if(dsCash.GetColumn(nRow, "CHK") == 1){
			grdCash.SetCellProp("body", 2, "Edit", "number");
		}else if(dsCash.GetColumn(nRow, "CHK") != 1){
			grdCash.SetCellProp("body", 2, "Edit", "none");
		}	
	}else if ( dsCash.GetRowType(nRow) == "insert" ) {
		grdCash.SetCellProp("body", 2, "Edit", "number");
	}else{
		grdCash.SetCellProp("body", 2, "Edit", "none");
	}
}
