/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsEmp     .ClearData();  
    dsAprvStas.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj)
{
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev1050-service:searchEmp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEmp=dsEmp dsConst=dsConst dsConstDept=dsConstDept";     

	fcEstmYearValue();	
	sDeptCd = cboDept.Value;

	fcd_AddParam("ESTM_YEAR" , sEvalYear);	
	fcd_AddParam("ESTM_NUM"  , sEvalNum);	
	fcd_AddParam("DEPT_CD"   , cboDept.Value);	
	fcd_AddParam("JOB_TIT_CD", cboWorkJob.Value);	
	fcd_AddParam("EMP_NM"    , edEmpNm.Text);
	fcd_AddParam("WRK_GBN"   , cboWrkGbn.Value);
	fcd_AddParam("ESTM_ESC_GBN", cboEvEscYn.Value);
	fcd_AddParam("CO_WK_GBN" , cboCoWkGbn.Value);
	fcd_AddParam("AVG_IN"    , cboAplyGbn.Value);
	fcd_AddParam("APT_YN"    , cboMoveDept.Value);
	fcd_AddParam("AVG_GBN"   , cboAvg.Value);
	fcd_AddParam("DIFFDEPT"  , cboDiffDept.Value);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcSearchComboDept(obj)
{
	dsDept.ClearData();

	var sServiceName = "rev1050-service:searchAllDept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDept=dsAllDept dsDeptGrd=dsAllDept";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM", sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcSearchComboWkJob(obj)
{
	dsWorkJob.ClearData();

	var sServiceName = "rev1050-service:searchCbWkJb";
    var sInDataSet   = "";
    var sOutDataSet  = "dsWorkJob=dsWorkJob ";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSave(obj) {

    if (sDeptCd != cboDept.Value) {
		fnc_message("조회시 부서와 저장시의 부서가 다릅니다. 다시 조회후 수정하세요!!", "N");
		return;
    }
    if(fnc_Message(SNIS_COM_1012, "Y"))
    {
        var sServiceName = "rev1050-service:saveEmp";
		var sInDataSet   = "dsEmp=dsEmp:u ";
		var sOutDataSet  = "";    	

		fcEstmYearValue();	
		fcd_AddParam("ESTM_YEAR", sEvalYear);	
		fcd_AddParam("ESTM_NUM" , sEvalNum);	

		if (fnc_IsNotNull(sDeptCd)) {
			var arrDepts = "";
			for (var i=0;i<dsEmp.RowCount();i++) {
				if (substr(ToUpper(dsEmp.GetRowType(i)),0,1) == "U") {
					if (indexof(arrDepts, dsEmp.GetColumn(i, "ESTM_DEPT")) == -1) {
						arrDepts += dsEmp.GetColumn(i, "ESTM_DEPT") + "|";
					}
				}
			}
			sServiceName = "rev1050-service:saveEmpDept";
			fcd_AddParam("ESTM_DEPT", arrDepts);
		}


		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
    }
}

