}

/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/

function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) {
		case "grdList" :

			if( !grdList.Editable ) return;

			if ( dsEmp.GetColumn(nRow,"APT_DT") != "" )
			{

				var sFormId 	= "";
				var sArgs 		= "";
				var arrReturn   = array(2);

				fcEstmYearValue();	

				sEmpNo = dsEmp.GetColumn(nRow, "EMP_NO");
				sEmpNm = dsEmp.GetColumn(nRow, "EMP_NM");

				sFormId = "frm_rev::REV1050P.xml";
				sArgs = "";	

				arrReturn = Dialog(sFormId, sArgs, 334, 443, "");


				if (fnc_IsNull(arrReturn) == false ) {
					dsEmp.SetColumn(nRow, "ESTM_DEPT", arrReturn[0]);
					dsEmp.SetColumn(nRow, "APT_DT", arrReturn[1]);
				}
			}
			break;
	}
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1050-service:searchEmp" ) {

		//화면 수정 막기
		var bEnable = true;
		if (dsEmp.RowCount() == 0) bEnable = false;
		else {
			if (fnc_IsNull(sDeptCd) == true) {							//  조회시점의 부서가 전체인 경우
				if( dsConst.GetColumn(0, "UPDATE_YN") == "N" )     bEnable = false;
			} else {										// 특정부서 조회인 경우
				if( dsConstDept.GetColumn(0, "CNT_5") == "완료" )  bEnable = false;
			}		
		}
		divBtnLst.btnSave.Enable = bEnable;
		grdList.Editable = bEnable;

		for ( var i = 0; i<dsEmp.RowCount(); i++) {
			if( fnc_IsNull(dsEmp.GetColumn(i, "ESTM_ESC_GBN") )) {
				dsEmp.SetColumn(i, "ESTM_ESC_GBN", "Y");
			}
		}
		dsEmp.row = -1;
	}	

	//  부서 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchAllDept" ) {

		dsDept.InsertRow(0);
		dsDept.SetColumn(0, "DEPT_NM", "전체");
		cboDept.index = 0;
	}

	//  보직 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchCbWkJb" ) {
		dsWorkJob.InsertRow(0);
		dsWorkJob.SetColumn(0, "WK_JOB_NM", "전체");
		cboWorkJob.index = 0;

	}
	// 저장 완료 후
	if ( sServiceName == "rev1050-service:saveEmp" ||
	     sServiceName == "rev1050-service:saveEmpDept" ) {
		fcSearch();		
	}	
	// 승인관리 완료 후
	if ( sServiceName == "rev1050-service:saveAprvMana" ) {
		fcSearch();		
	}	
}

function fcEstmYearValue(obj)
{
	/* 콤보박스에서 값 가져오기 */
    var nCbValue =cbEstmYear.Value;
	var nCbRow = dsEstmYear.SearchRow("CD=="+nCbValue);

	sEvalYear = dsEstmYear.GetColumn(nCbRow,"ESTM_YEAR");
	sEvalNum = dsEstmYear.GetColumn(nCbRow,"ESTM_NUM");

}

function fcCheckCombo()
{
	fcSearchComboDept();
	fcSearchComboWkJob();
}

//컬럼이 변경될 때
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch( obj.id ) {
		case "dsEmp" :
			if( strColumnID == "AVG_IN" ) {
				if( varNewValue == "Y" )
					obj.SetColumn(nRow, "ESTM_ESC_GBN", "Y"); //평균값 적용을 선택하면 평가 대상자로 강제변경
			}
		break;
	}	
}
