Window.Script:
/************************
*  공통 Script Include  *
************************/
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";

var sDivNo  = "";
var sDivNm  = "";
var sCommNo = "";

/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){
	dsDiv.ClearData();
    fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearch(obj) {	
	//필수 조회 조건 체크

	var sServiceName = "rev1070-service:searchDiv";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDiv=dsDiv ";

    fcd_AddParam("ESTM_YEAR", sEvalYear);
    fcd_AddParam("ESTM_NUM" , sEvalNum);
	fcd_AddParam("DEPT_CD"  , DEPT_CD);
	fcd_AddParam("DIV_NO"   , Trim(edDivCd.Text));
	fcd_AddParam("DIV_NM"   , Trim(edDivNm.Text));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1070-service:searchDiv" ) {
		edDivNm.SetFocus();
	}
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj)
{
	for( var i=0;i<dsDiv.rowcount();i++ ) {
		if (dsDiv.GetColumn(i,"CHK") == '1') {

			sDivNo  = dsDiv.GetColumn(i,"DIV_NO");
			sDivNm  = dsDiv.GetColumn(i,"DIV_NM");
			sCommNo = dsDiv.GetColumn(i,"COMM_NO");
		}
	}

	var arrReturn = array(3);               //결과 반환 할 Array	

	arrReturn[0] = sDivNo;   //근무지코드
	arrReturn[1] = sDivNm;   //근무지명
	arrReturn[2] = sCommNo;  //지점코드

    Close(arrReturn);                                                   //반환 후 조회
}

function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch (obj.id) 
	{
		case "grdDiv" :
			var arrReturn    = array(3); 

			arrReturn[0] = dsDiv.GetColumn(nRow, "DIV_NO"); 
			arrReturn[1] = dsDiv.GetColumn(nRow, "DIV_NM");   
			arrReturn[2] = dsDiv.GetColumn(nRow, "COMM_NO");   

			Close(arrReturn);
		break;
	}
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}

/*---------------------------------+
|  Enter Key Press 시 조회 실행 !  |
+----------------------------------*/
function edSearch_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam){

	if (nChar==13) fcSearch();

}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch (obj.id) 
	{
		case "dsDiv" :
			if(dsWkJob.GetColumn(nRow,"CHK") == 1)     //체크박스 선택
			{  

			   for(var i = 0; i<nRow; i++)             //내가 선택한 체크박스 로우에  윗 로우부분과 아래 로우부분은 무조건 0으로 세팅
			   {                                       //예를 들어 내가선택한 로우가  10줄 중에 3번째 줄이면 첫번째줄부터 2번째 줄까지, 4번째 줄에서 10번째 줄까지 0으로 셋팅해줌 
				   dsDiv.SetColumn(i,"CHK",0); 
			   }
			   for(var i = nRow+1; i<dsDiv.GetRowCount(); i++)
			   {
				  dsDiv.SetColumn(i,"CHK",0);
			   }
			}
		break;
	}
}
