var sFormId       = this.GetForm().id;
var sBUTTONLIST   = 'FFFFTFT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

var sMenuGrpNum     = "0";                         	   // 메뉴그룹의 갯수보관
var sMenuListNum    = "0";                             // 메뉴리스트의 갯수보관
var sMenuUpId       = "";
var sMenuUpNm       = "";
var sPAGENUM        = "20";

var sEvalYear = "";
var sEvalNum  = "";
var sEmpNo = "";
var sEmpNm = "";

/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTFFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //버튼이동
    cancleBtn.Top  = cfnBtn.Top;
    cancleBtn.Left = cfnBtn.Left;

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	dsWrkGbn.InsertRow(0);
	dsWrkGbn.SetColumn(0, "CD_NM", "전체");
	cboWrkGbn.index = 0;

    fcSearchComboDept();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsEmp";
	if (!fnc_FormUnloadCheck(sDataset  , sFormId)) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
	//Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsEmp     .ClearData();  
    dsAprvStas.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	if( fnc_IsNull(cboDept.Value) ) return false;

	return true;
}

function fcSearch(obj)
{
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev1050-service:searchEmp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEmp=dsEmp dsAprvStas=dsAprvStas dsCfm=dsCfm ";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR" , sEvalYear);	
	fcd_AddParam("ESTM_NUM"  , sEvalNum);	
	fcd_AddParam("DEPT_CD"   , cboDept.Value);	
	fcd_AddParam("JOB_TIT_CD", cboWorkJob.Value);	
	fcd_AddParam("EMP_NM"    , edEmpNm.Text);
	fcd_AddParam("WRK_GBN"   , cboWrkGbn.Value);
	fcd_AddParam("APRV_DEPT" , cboDept.Value);	
	fcd_AddParam("APRV_SEQ"  , "4");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcSearchComboDept(obj)
{
	dsDept.ClearData();

	var sServiceName = "rev1050-service:searchCbDept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDept=dsDept dsDeptGrd=dsDept";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM", sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcSearchComboWkJob(obj)
{
	dsWorkJob.ClearData();

	var sServiceName = "rev1050-service:searchCbWkJb";
    var sInDataSet   = "";
    var sOutDataSet  = "dsWorkJob=dsWorkJob ";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM", sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "grdList";
    fcd_OpenExcelListExport(sExcelList,0);
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}


