*                                      공통 Function 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1050-service:searchEmp" ) {		
		//확정권한에 따른 버튼처리
		var sReqEmpNo  = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_REQ_NO");	//요청자
		var sAprvCd    = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_STAS");	    //상태값
		var sLoginUser = gdsUser.GetColumn(gdsUser.currow, "USER_ID");

		var arrBtn = array(2);
		arrBtn[0] = cancleBtn;
		arrBtn[1] = cfnBtn;

		fnc_BtnVisibleInit(arrBtn);

		if( sReqEmpNo == sLoginUser ) {
			if( sAprvCd == "001" ) {
				cfnBtn.Visible = true;
			} else if( sAprvCd == "003" ) {
				cancleBtn.Visible = true;
			}
		}
		for ( var i = 0; i<dsEmp.RowCount(); i++)
		{
			if( fnc_IsNull(dsEmp.GetColumn(i, "AVG_IN") ))
			{
				dsEmp.SetColumn(i, "AVG_IN", "N");
			}
		}
	}			
	//  부서 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchCbDept" ) {
		cboDept.Index = dsDept.FindRow("DEPT_CD", 
		                                substr(gdsUser.GetColumn(0, "TEAM_DETL_CD"), length(gdsUser.GetColumn(0, "TEAM_DETL_CD"))-4, 4));
		fcSearchComboWkJob();
	}
	//  보직 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchCbWkJb" ) {
		dsWorkJob.InsertRow(0);
		dsWorkJob.SetColumn(0, "WK_JOB_NM", "전체");
		cboWorkJob.index = 0;
		fcSearch();
	}
	// 저장 완료 후
	if ( sServiceName == "rev1050-service:saveEmp" ) {
		fcSearch();		
	}	
	// 승인관리 완료 후
	if ( sServiceName == "rev1050-service:saveAprvMana" ) {
		fcSearch();		
	}	
}

function fcEstmYearValue(obj)
{
	/* 콤보박스에서 값 가져오기 */
    var nCbValue =cbEstmYear.Value;
	var nCbRow = dsEstmYear.SearchRow("CD=="+nCbValue);

	sEvalYear = dsEstmYear.GetColumn(nCbRow,"ESTM_YEAR");
	sEvalNum = dsEstmYear.GetColumn(nCbRow,"ESTM_NUM");

}

function fcBtnOnClick(obj) {

	switch( obj.id ) {
		case "cfnBtn" :	//확정
			if(fnc_Message(SNIS_RBM_F032, "Y", "확정")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	
				var sCurDate = fcd_GetSysdate("YYYYMMDD");

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1090");
				fcd_AddParam("APRVCD"   , "01");			//확정요청	
				fcd_AddParam("APRVDATE" , sCurDate);		//승인날짜
				fcd_AddParam("APRVDEPT" , cboDept.Value);	//부서

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "cancleBtn" :	//확정취소

			if( dsCfm.GetColumn(0, "APRV_STAS") != "001" ) {
				fnc_Message(SNIS_RBM_F009,"N");
				return;
			}

			if(fnc_Message(SNIS_RBM_F035, "Y", "확정취소")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	
				var sCurDate = fcd_GetSysdate("YYYYMMDD");

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1090");
				fcd_AddParam("APRVCD"   , "03");			//확정요청	
				fcd_AddParam("APRVDATE" , sCurDate);		//승인날짜
				fcd_AddParam("APRVDEPT" , cboDept.Value);	//부서

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;

	}
}
