/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormOnLoadCompleted(obj) 
{
}
function fcFormLoadSetting(obj) 
{  
    sBUTTONLIST   = 'FTTFFFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	stDeptNm.Text = gdsUser.GetColumn(0, "DEPT_NM");
	stFloc.Text = gdsUser.GetColumn(0, "FLOC");

	stUserId.Text = gdsUser.GetColumn(0, "USER_ID");
	stUserNm.Text = gdsUser.GetColumn(0, "USER_NM");

	sPubDeptText = stPublicDept.Text;
	//공통코드 조회
	fcd_GetComboDs(dsInComCd);
	stDate.Text = dsYearNum.GetColumn(0, "ESTM_YEAR") + "년 "	//(2011.01.01~2011.11.31)
				 + dsYearNum.GetColumn(0, "ESTM_NUM")  + "회차 평가 "
				 + "(" + substr(dsYearNum.GetColumn(0, "ESTM_STR_DT"), 0, 4) + "." 
				 + substr(dsYearNum.GetColumn(0, "ESTM_STR_DT"), 4, 2) + "." 
				 + substr(dsYearNum.GetColumn(0, "ESTM_STR_DT"), 6, 2) + " ~ "
				 + substr(dsYearNum.GetColumn(0, "ESTM_END_DT"), 0, 4) + "." 
				 + substr(dsYearNum.GetColumn(0, "ESTM_END_DT"), 4, 2) + "." 
				 + substr(dsYearNum.GetColumn(0, "ESTM_END_DT"), 6, 2) + ")";
	fcSearchDept();

	// 화면그리드 리사이즈
	fcSetResize(this,this.width,this.height);
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) 
{
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if (!fnc_FormUnloadCheck(sDataset  , sFormId)) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  초기화
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) 
{
    dsEmpCnt1  .ClearData(); 
	dsEmpRpm   .ClearData(); 
	dsDistr2   .ClearData(); 
	dsEmpCnt2  .ClearData(); 
	dsEmpFstSnd.ClearData(); 

	var objDsDistr;
	var objPrmDt;
	for (var i=1;i<=nLitemTypeTotCnt;i++) {
		objDsDistr = Object("dsDistr1_"+i);		
		objPrmDt   = Object("dsPrmDt_"+i);
		objDsDistr.ClearData(); 
		objPrmDt  .ClearData();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) 
{
	if(dsDept.GetRowCount() <=0 ){
		return false;	
	}
    fcInit(obj);
	return true;
}

//조회버튼 클릭시
function fcSearch(obj) 
{
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev2010-service:searchEmpFstSnd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEmpFstSnd=dsEmpFstSnd dsEstmEnd=dsEstmEnd ";

	fcd_AddParam("DEPT_CD"  , cboDeptNm.Value);	
	fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));
	fcd_AddParam("EMP_NO"   , gdsUser.GetColumn(0, "USER_ID"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

//조회조건 부서조회
function fcSearchDept(obj) 
{
	var sServiceName = "rev2010-service:searchDept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDept=dsDept";

    fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
    fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));
    fcd_AddParam("EMP_NO"   , gdsUser  .GetColumn(0, "USER_ID"));

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

// 배분표 가져오기 위한 인원체크
function searchEmpCnt(obj) 
{
	var sServiceName = "";

	if ( dsEmpFstSnd.GetColumn(0, "FST_SND_GBN") == "001" ) {
		//1차평가자
		if ( dsEmpFstSnd.GetColumn(0, "PERM_TEMP_GBN") == "002" ){	
			//비정규직 -> 투표소장
			sServiceName = "rev2010-service:searchEmpCnt";
		}
		if ( dsEmpFstSnd.GetColumn(0, "PERM_TEMP_GBN") == "001" && dsEmpFstSnd.GetColumn(0, "SNACK_ESTM_YN") == "Y" ){
			//정규직 -> 1차평가자(매점판매원 평가자)
			sServiceName = "rev2010-service:searchEmpCnt02";
		}

		if ( dsEmpFstSnd.GetColumn(0, "PERM_TEMP_GBN") == "001" && dsEmpFstSnd.GetColumn(0, "SNACK_ESTM_YN") == "N" ){
			//정규직 -> 1차평가자(매점판매원 제외한 평가자)
			sServiceName = "rev2010-service:searchEmpCnt03";
		}
	} else if ( dsEmpFstSnd.GetColumn(0, "FST_SND_GBN") == "002" ) { 
		//2차평가자(지점장)
		tab.tab2.grdDistr2.Visible = false;
		tab.tab2.grdTckRslt.Top    = 18;
		//tab.tab2.grdTckRslt.Bottom = 418;
		sServiceName = "rev2010-service:searchEmpCnt01";
	}

	/*
		dsEmpCnt1 : 1. 투표소장 배분표인원수 조회, 
					2. 1차평가자의 배분표인원수 조회
					3. 2차평가자의 배분표인원수 조회(계약직 전체를 가져옴)
		dsEmpCnt2 : 1, NULL
		            2. 전체 발매실적 배분표인원수 조회
		            3. 2전체 발매실적 배분표인원수 조회
	*/
	var sInDataSet   = "";
    var sOutDataSet  = "dsEmpCnt1=dsEmpCnt1 dsEmpCnt2=dsEmpCnt2 dsLitemInfo=dsLitemInfo";

	fcd_AddParam("ESTM_DEPT", cboDeptNm.Value);	
	fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM", dsYearNum.GetColumn(0, "ESTM_NUM"));
	fcd_AddParam("EMP_NO", gdsUser.GetColumn(0, "USER_ID"));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function searchDistr1(obj) 
{
	//업무수행평가 배분표 조회
    var nRowCnt = 0;
	nDistr1QueyCnt = 0;
	for (var i=1;i<=nLitemTypeTotCnt;i++) {
		//nRowCnt = 0;
		object("dsDistr1_"+i).cleardata();
		for (var j=0;j<dsEmpCnt1.rowcount();j++) {
			if (i == 5 && j == (dsEmpCnt1.rowcount()-1)) bLitemTypeLast = true;
			if (ToNumber(dsEmpCnt1.GetColumn(j, "ESTM_LITEM_TYPE")) == i) {
				//nRowCnt ++;
				searchDistr1Dtl("dsDistr1_"+i,  dsEmpCnt1.GetColumn(j, "EMP_CNT"));
			}
		}
		//if (nRowCnt == 0) object("tab.tab1.tabPrm.tab"+i).enable = false;
	}
}

function searchDistr1Dtl(dsObj, EmpCnt) 
{
	//업무수행평가 배분표 조회
	var sServiceName = "rev2010-service:searchDistr1";
    var sInDataSet   = "";
    var sOutDataSet  = dsObj + "=dsDistr1 ";

	fcd_AddParam("ESTM_OBJ_NUM", EmpCnt);	
	fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM",  dsYearNum.GetColumn(0, "ESTM_NUM"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function searchDistr2(obj)
{
	// 발매실적 예외부서인 경우 조회하지 않음
	var nRow = -1;
	nRow = dsDept.FindRow("CD", cboDeptNm.Value);
	if (nRow >= 0) {
		if (dsDept.GetColumn(nRow, "TELLER_RELEA_ESC_YN") == "Y") {
			Trace("발매실적 예외부서이라서 발매실적을 조회하지 않습니다.");
			return;
		}
	}

	//발매실적 배분표 조회
	var sServiceName = "rev2010-service:searchDistr2";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDistr2=dsDistr2 ";

	fcd_AddParam("ESTM_OBJ_NUM", dsEmpCnt2.GetColumn(0, "EMP_CNT"));	
	fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM", dsYearNum.GetColumn(0, "ESTM_NUM"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function searchPrmDt()
{
	http.Sync = true;
	for (var i=1;i<=nLitemTypeTotCnt;i++) {
		object("dsPrmDt_"+i).cleardata();
		nLitemTypeNum = i;
		for (var j=0;j<dsEmpCnt1.rowcount();j++) {
			if (ToNumber(dsEmpCnt1.GetColumn(j, "ESTM_LITEM_TYPE")) == i) {
				searchPrmDtDtl("dsPrmDt_"+i,  "00"+i);				
			}
		}
	}
	http.Sync = false;
}

function searchPrmDtDtl(dsObj, sEstmLitemType)
{
	var sServiceName = "";

	if ( dsEmpFstSnd.GetColumn(0, "FST_SND_GBN") == "001" ) {
		//1차담당자,투표소장 업무수행평가 조회
		sServiceName = "rev2010-service:searchPrmDt";
	} else if ( dsEmpFstSnd.GetColumn(0, "FST_SND_GBN") == "002" ) {
		//2차담당자 업무수행평가 조회
		sServiceName = "rev2010-service:searchPrmDt2";
	}

    var sInDataSet   = "";
    var sOutDataSet  = dsObj + "=dsPrmDt ";

	fcd_AddParam("ESTM_YEAR"  , dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM"   , dsYearNum.GetColumn(0, "ESTM_NUM"));
	fcd_AddParam("ESTM_DEPT"  , cboDeptNm.Value);
	fcd_AddParam("ESTM_EMP"   , gdsUser.GetColumn(0, "USER_ID"));
	fcd_AddParam("FST_SND_GBN", dsEmpFstSnd.GetColumn(0, "FST_SND_GBN"));
	fcd_AddParam("ESTM_LITEM_TYPE", sEstmLitemType);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}
