/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj)
{
	if(dsDept.GetRowCount() <=0 ){
		return false;	
	}

	for (var i=1;i<=nLitemTypeTotCnt;i++) {
	   if (fcCheckSum(i) == false) return false;
	}

	// 개인별 배분표과 합산 자료를 다시 계산한다.
	var sDistr = "";
	var sEmpNo = "";
	var objDsDistr;
	var objDsPrmDt;
	// 배정표 문자열 세팅 : 각 그리드별 배부코드값을 '|'로 연결한다.
	for (var i=1;i<=nLitemTypeTotCnt;i++) {
		for (var j=0;j<dsEmpCnt1.rowcount();j++) {
			if (ToNumber(dsEmpCnt1.GetColumn(j, "ESTM_LITEM_TYPE")) == i) {
				objDsDistr = Object("dsDistr1_"+i);				
				var nRow = objDsDistr.FindRow("CHK", "1"); 
				sDistr += objDsDistr.GetColumn(nRow, "DISTR_CD");
			}
		}
		sDistr += "|";
	}

	// 전체 점수를 계산
	var sEmpNo = "";
	var nRateVal = 0;	
	var objDsFrmDt;
	var gRow;
	for (var k=0;k<dsPrmDt.RowCount();k++) {
		nRateVal = 0;
		sEmpNo = dsPrmDt.GetColumn(k, "EMP_NO");
		for (var i=1;i<=nLitemTypeTotCnt;i++) {
			objDsFrmDt = Object("dsPrmDt_"+i);
			gRow = objDsFrmDt.findRow("EMP_NO", sEmpNo);
			if (gRow != -1) {
				nRateVal +=  fcCalcRowSum(objDsFrmDt, gRow);
			}
		}
		dsPrmDt.SetColumn(k, "RATE",     nRateVal);
		dsPrmDt.SetColumn(k, "DISTR_CD", sDistr);
	}

	return true;
}

function fcSave(obj)
{
	if ( !fcSaveCheck(obj) ) return;

	var confirmMsg = "저장하시겠습니까?";
    if(fnc_Message(SNIS_CRA_B999, "Y", confirmMsg))
    {   
		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";    

		sServiceName = "rev2010-service:savePrmRslt";
		sInDataSet = "dsPrmDt=dsPrmDt:u dsPrmDt_1=dsPrmDt_1:U dsPrmDt_2=dsPrmDt_2:U dsPrmDt_3=dsPrmDt_3:U dsPrmDt_4=dsPrmDt_4:U dsPrmDt_5=dsPrmDt_5:U ";

		//발매실적 저장
		if ( dsEmpFstSnd.GetColumn(0, "PERM_TEMP_GBN") == "001" 
		   && dsEmpFstSnd.GetColumn(0, "FST_SND_GBN") == "001") {
		   sInDataSet += "dsEmpRpm=dsEmpRpm:a ";   
		} 
		sOutDataSet  = "";		

		fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));
		fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));
		fcd_AddParam("ESTM_DEPT", cboDeptNm.Value);
		fcd_AddParam("ESTM_EMP" , gdsUser.GetColumn(0, "USER_ID"));
		fcd_AddParam("FST_SND_GBN", dsEmpFstSnd.GetColumn(0, "FST_SND_GBN"));

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
    } 
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) 
{

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if (sServiceName == "common-service:searchCode") {
		nLitemTypeTotCnt = dsLitemType.RowCount();
		var objTab;
		for (var i=0;i<nLitemTypeTotCnt;i++) {
			objTab = Object("tab.tab1.TabPrm.tab"+(i+1));
			objTab.text = dsLitemType.GetColumn(i, "CD_NM");
		}
		//항목에 따라 달라질 수 있음
		//if (gdsUser.GetColumn(0, "FLOC") == "투표소장") {
		//	tab.tab1.TabPrm.DeleteTab(3);
		//	tab.tab1.TabPrm.DeleteTab(2);
		//}
	}

	// 조회 완료 후
	if ( sServiceName == "rev2010-service:searchDept" ) {
		// 발매실적 제외 지점인 경우 발매실적평가 탭을 삭제한다.
		/*
		var sDeptCd = cboDeptNm.Value;
		var nRow = dsDept.FindRow("CD='"+sDeptCd+"'");
		if (nRow >= 0) {
			if (dsDept.GetColumn(nRow, "TELLER_RELEA_ESC_YN") == "Y") {
				tab.DeleteTab(1);
			}
		}
		*/
		//발매실적은 부서실적을 따라가기 때문에 발매실적 탭을 사용하지 않음
		tab.DeleteTab(1);

		fcSearch();   
		return;
	}

	// 1차평가자(투표소장) 업무수행평가 배분표인원수 조회 완료후
	if ( sServiceName == "rev2010-service:searchEmpCnt" ) {
		dsLitemInfo.row = -1;
		searchDistr1();
		return;
	}

	//2차평가자(지점장) 업무수행평가,발매실적 배분표인원수 조회 완료후
	if ( sServiceName == "rev2010-service:searchEmpCnt01" ) {
		dsLitemInfo.row = -1;
		searchDistr1();
		//searchDistr2(); //발매실적배분표 조회 생략 2016.11.02 by chojob
		return;
	}

	//1차평가자(담당자) 업무수행평가,발매실적 배분표인원수 조회 완료후
	if ( sServiceName == "rev2010-service:searchEmpCnt02" ) {
		dsLitemInfo.row = -1;
		searchDistr1();
		//searchDistr2(); //발매실적배분표 조회 생략 2016.11.02 by chojob
		return;
	}

	//1차평가자(담당자) 업무수행평가,발매실적 배분표인원수 조회 완료후
	if ( sServiceName == "rev2010-service:searchEmpCnt03" ) {
		dsLitemInfo.row = -1;
		searchDistr1();
		//searchDistr2(); //발매실적배분표 조회 생략 2016.11.02 by chojob
		return;
	}

	//업무수행평가 배분표 조회 후
	if ( sServiceName == "rev2010-service:searchDistr1" ) {
		nDistr1QueyCnt ++;
	    if (nDistr1QueyCnt == dsEmpCnt1.rowcount()) {
			searchItmGrd();
		}
		return;
	}
	if ( sServiceName == "rev2010-service:searchItmGrd" ) {
		searchPrmDt();
		return;
	}


	//발매실적 배분표 조회 후
	if ( sServiceName == "rev2010-service:searchDistr2" ) {
		if (dsDistr2.rowcount() == 0) tab.tab2.grdTckRslt.visible = false;
		searchReleaRate(); 	
		return;
	}

	if ( sServiceName == "rev2010-service:searchPrmDt" 
	  || sServiceName == "rev2010-service:searchPrmDt2")
	{

		var objGrd = Object("tab.tab1.tabPrm.tab"+nLitemTypeNum+".grdPrmDt");
		var objDsPrmDt  = Object("dsPrmDt_"+nLitemTypeNum);
		var objDsDistr  = Object("dsDistr1_"+nLitemTypeNum);
		objGrd.Redraw = false;

		fcResizeGrdPrmDt(nLitemTypeNum);	
		for ( var w=0; w<objDsPrmDt.RowCount(); w++) {
			objDsPrmDt.SetColumn(w, "ESTM_EMP", gdsUser.GetColumn(0, "USER_ID"));
			objDsPrmDt.SetColumn(w, "ESTM_DEPT", cboDeptNm.Value);
		}
		if (nLitemTypeNum == 1) {
			dsPrmDt.copy(dsPrmDt_1);	// 개인별 점수저장용으로 복사한다.
		}

		if ( !fnc_IsNull( objDsPrmDt.GetColumn(0, "DISTR_CD" ) ) ) {
			objDsDistr.SetColumn(objDsDistr.Findrow( "DISTR_CD" , objDsPrmDt.GetColumn(0, "DISTR_CD")) , "CHK" , "1");
		} else {
			if( objDsDistr.RowCount() != 0 ) objDsDistr.SetColumn(0, "CHK" , 1);
		}

		objGrd.Redraw = true;
		return;
	}

	if ( sServiceName == "rev2010-service:searchEmpFstSnd" ) {
			//평가마감시 수정 불가능
		if( dsEstmEnd.GetColumn(0, "ESTM_END_YN") == "Y" ) {
			divBtnLst.btnSave.Enable = false;

			for (var i=1;i<=nLitemTypeTotCnt;i++) {
				var objGrd = Object("tab.tab1.tabPrm.tab"+i+".grdPrmDt");
				var objDsDistr = Object("dsDistr1_"+i);
				objGrd.Editable = false;
				objDsDistr.Editable = false;
			}
			tab.tab2.grdDistr2.Editable = false;
		}

		//공동근무자의 경우, 선택되지 않은 지점의 완료 여부를 뿌려줌
		stPublicDept.Text = sPubDeptText;
		if( dsDept.rowcount > 1 ) {
			for(var i=0; i < dsDept.rowcount; i++) {
				if( dsDept.GetColumn(i, "CD") != cboDeptNm.Value &&
					dsDept.GetColumn(i, "EV_GBN") == "N" ) {
					stPublicDept.Text += "(" + dsDept.GetColumn(i, "CD_NM") + " 미완료)";
				}
			}
			stPublicDept.Visible = true;
		}

		//비정규직 로그인시, 발매실적 않보여줌
		if( dsEmpFstSnd.GetColumn(0, "PERM_TEMP_GBN") == "002" ) {
			tab.DeleteTab(1);
		} 
		searchEmpCnt();
		return;
	}

	if ( sServiceName == "rev2010-service:savePrmRslt" ) {
		dsDept.SetColumn(cboDeptNm.Index, "EV_GBN", "Y");
		fcSearch();
		return;
	}

	if ( sServiceName == "rev2010-service:searchTckRslt2" ) {

		if ( !fnc_IsNull( dsEmpRpm.GetColumn(0,"RELEA_DISTR") ) ) {
			//배분표를 저장한 적이 있다면
			var sReleaDistr = dsEmpRpm.getcolumn(0, "RELEA_DISTR");	//발매배분표코드
			dsDistr2.SetColumn(dsDistr2.FindRow("DISTR_CD",  sReleaDistr), "CHK" , "1");
		} else {
			//배분표를 저장한 적이 없다면
			if ( dsEmpFstSnd.GetColumn(0, "FST_SND_GBN") == "002" ) return;

			if( dsDistr2.RowCount() != 0 ) dsDistr2.SetColumn(0, "CHK" , 1);
			for ( var s=0; s<dsEmpRpm.RowCount(); s++)
			{
				dsEmpRpm.SetColumn(s, "RELEA_DISTR", dsDistr2.GetColumn(dsDistr2.currow , "DISTR_CD"));
			}
			fcRankMeasure();
		}
		return;
	}
	if ( sServiceName == "rev2010-service:searchTckRslt" )
	{
		if ( !fnc_IsNull( dsEmpRpm.GetColumn(0,"RELEA_DISTR") ) )
		{
			dsDistr2.SetColumn(dsDistr2.FindRow("DISTR_CD", dsEmpRpm.getcolumn(0, "RELEA_DISTR") ) , "CHK" , "1");
		} else {
			if( dsDistr2.RowCount() != 0 ) dsDistr2.SetColumn(0, "CHK" , 1);
			for ( var s=0; s<dsEmpRpm.RowCount(); s++)
			{
				dsEmpRpm.SetColumn(s, "RELEA_DISTR", dsDistr2.GetColumn(dsDistr2.currow , "DISTR_CD"));
				dsEmpRpm.SetColumn(s, "RELEA_RATE", dsRelea.GetColumn(0, "RELEA_RATE"));
			}
			fcRankMeasure();
		}
		return;
	}

	if ( sServiceName == "rev2010-service:searchReleaRate" ) {
		searchTckRslt();
		return;
	}

}

function fsDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch ( obj.id )
	{
		case "dsDistr1_1" :
		case "dsDistr1_2" :
		case "dsDistr1_3" :
		case "dsDistr1_4" :
		case "dsDistr1_5" :

			var objGrd     = Object("tab.tab1.tabPrm.tab"+substr(obj.id, 9,1)+".grdPrmDt");
			var objDsPrmDt = Object("dsPrmDt_"+substr(obj.id, 9,1));
			var objDsDistr = Object(obj.id);
			if(objDsDistr.GetColumn(nRow,"CHK") == 1) {  //체크박스 선택

				for(var i = 0; i<nRow; i++) {                                  
					objDsDistr.SetColumn(i,"CHK",0); 
				}
				for(var i = nRow+1; i<objDsDistr.GetRowCount(); i++) {
					objDsDistr.SetColumn(i,"CHK",0);
				}

				if( objDsDistr.FindRow("CHK", "1") == -1 ) {
				   	objDsDistr.SetColumn(nRow,"CHK",1);
				}

				if( objDsDistr.FindRow("CHK", "1") == -1 ) {
					objDsDistr.SetColumn(0, "CHK", 1);
				}

				for ( var w=0; w<objDsPrmDt.RowCount(); w++) {
					objDsPrmDt.SetColumn(w, "DISTR_CD", objDsDistr.GetColumn(objDsDistr.FindRow("CHK", "1"), "DISTR_CD"));
				}
				var sRow = objDsDistr.Findrow("CHK" , "1");

				objGrd.SetCellProp("summ",1,"text", objDsDistr.GetColumn(sRow , "S_GRD_NUM"));
				objGrd.SetCellProp("summ",2,"text", objDsDistr.GetColumn(sRow , "A_GRD_NUM"));
				objGrd.SetCellProp("summ",3,"text", objDsDistr.GetColumn(sRow , "B_GRD_NUM"));
				objGrd.SetCellProp("summ",4,"text", objDsDistr.GetColumn(sRow , "C_GRD_NUM"));
				objGrd.SetCellProp("summ",5,"text", objDsDistr.GetColumn(sRow , "D_GRD_NUM"));

				objGrd.SetCellProp("summ",7,"text", objDsDistr.GetColumn(sRow , "S_GRD_NUM"));
				objGrd.SetCellProp("summ",8,"text", objDsDistr.GetColumn(sRow , "A_GRD_NUM"));
				objGrd.SetCellProp("summ",9,"text", objDsDistr.GetColumn(sRow , "B_GRD_NUM"));
				objGrd.SetCellProp("summ",10,"text", objDsDistr.GetColumn(sRow , "C_GRD_NUM"));
				objGrd.SetCellProp("summ",11,"text", objDsDistr.GetColumn(sRow , "D_GRD_NUM"));		

				objGrd.SetCellProp("summ",13,"text", objDsDistr.GetColumn(sRow , "S_GRD_NUM"));
				objGrd.SetCellProp("summ",14,"text", objDsDistr.GetColumn(sRow , "A_GRD_NUM"));
				objGrd.SetCellProp("summ",15,"text", objDsDistr.GetColumn(sRow , "B_GRD_NUM"));
				objGrd.SetCellProp("summ",16,"text", objDsDistr.GetColumn(sRow , "C_GRD_NUM"));
				objGrd.SetCellProp("summ",17,"text", objDsDistr.GetColumn(sRow , "D_GRD_NUM"));	

				objGrd.SetCellProp("summ",19,"text", objDsDistr.GetColumn(sRow , "S_GRD_NUM"));
				objGrd.SetCellProp("summ",20,"text", objDsDistr.GetColumn(sRow , "A_GRD_NUM"));
				objGrd.SetCellProp("summ",21,"text", objDsDistr.GetColumn(sRow , "B_GRD_NUM"));
				objGrd.SetCellProp("summ",22,"text", objDsDistr.GetColumn(sRow , "C_GRD_NUM"));
				objGrd.SetCellProp("summ",23,"text", objDsDistr.GetColumn(sRow , "D_GRD_NUM"));		

				objGrd.SetCellProp("summ",25,"text", objDsDistr.GetColumn(sRow , "S_GRD_NUM"));
				objGrd.SetCellProp("summ",26,"text", objDsDistr.GetColumn(sRow , "A_GRD_NUM"));
				objGrd.SetCellProp("summ",27,"text", objDsDistr.GetColumn(sRow , "B_GRD_NUM"));
				objGrd.SetCellProp("summ",28,"text", objDsDistr.GetColumn(sRow , "C_GRD_NUM"));
				objGrd.SetCellProp("summ",29,"text", objDsDistr.GetColumn(sRow , "D_GRD_NUM"));		
			}

		break;

		case "dsDistr2" :
			if(dsDistr2.GetColumn(nRow,"CHK") == 1)     //체크박스 선택
			{  
			   for(var j = 0; j<nRow; j++)             //내가 선택한 체크박스 로우에  윗 로우부분과 아래 로우부분은 무조건 0으로 세팅
			   {                                       //예를 들어 내가선택한 로우가  10줄 중에 3번째 줄이면 첫번째줄부터 2번째 줄까지, 4번째 줄에서 10번째 줄까지 0으로 셋팅해줌 
				   dsDistr2.SetColumn(j,"CHK",0); 
			   }
			   for(var j = nRow+1; j<dsDistr2.GetRowCount(); j++)
			   {
				  dsDistr2.SetColumn(j,"CHK",0);
			   }
			}

			var xRow = dsDistr2.Findrow("CHK" , "1");
			for ( var q=0; q<dsEmpRpm.RowCount(); q++)
			{
				dsEmpRpm.SetColumn(q, "RELEA_DISTR", dsDistr2.GetColumn(xRow , "DISTR_CD"));
			}
			fcRankMeasure();
		break;

		case "dsPrmDt_1" :
		case "dsPrmDt_2" :
		case "dsPrmDt_3" :
		case "dsPrmDt_4" :
		case "dsPrmDt_5" :		
			var objDsPrmDt = Object(obj.id);
			fcCalcRowSum(objDsPrmDt, nRow);

			/* *********************
			var nRateVal = fcCalcRowSum(objDsPrmDt, nRow);
			var sEmpNo = "";
			var nEmpRowNum = -1;
			sEmpNo = objDsPrmDt.GetColumn(nRow, "EMP_NO");
			// 다른 그리드에 있는 값도 조사함
			var thistabpage = substr(obj.id,8,1);
			for (var i=1;i<=nLitemTypeTotCnt;i++) {
				var objds = Object("dsPrmDt_"+i);
				if (thistabpage <> toString(i)) {
					var gRow = objDs.findRow("EMP_NO", sEmpNo);
					if (gRow != -1) {
						nRateVal +=  fcCalcRowSum(objDs, gRow);
					}
				}				
			}
			nEmpRowNum = dsPrmDt.FindRow("EMP_NO", sEmpNo);
			if (nEmpRowNum >= 0) dsPrmDt.SetColumn(nEmpRowNum, "RATE", nRateVal);
			************************ */
		break;
	}
}

function fcCalcRowSum(objDsPrmDt, nRow)
{
	objDsPrmDt.FireEvent = false;			
	var nResult = 0;
	var nRate   = 0;
	var nTgtRow = 0;
	nResult =
	  toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE"))
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE"));

	nRate =
	  toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_1")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_1")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_1")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_1")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_1")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_1")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_2")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_2")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_2")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_2")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_2")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_2")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_3")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_3")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_3")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_3")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_3")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_3")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_4")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_4")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_4")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_4")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_4")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_4")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "S_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "S_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_5")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "A_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "A_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_5")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "B_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "B_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_5")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "C_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "C_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_5")) /100)
	+ toInteger(objDsPrmDt.GetColumn(nRow, "D_GRD_5")) * toInteger(dsItmGrd.GetColumn(0, "D_RATE")) * (toInteger(objDsPrmDt.GetColumn(nRow, "RATE_5")) /100);
	objDsPrmDt.SetColumn(nRow, "RESULT", nResult);
	objDsPrmDt.SetColumn(nRow, "RATE", nRate);			
	objDsPrmDt.FireEvent = true;
	return nRate;
}
function fcDsCanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var objPrmDt = obj;
	objPrmDt.FireEvent = false;
	if (strColumnID =="S_GRD_1" ||strColumnID =="A_GRD_1" ||strColumnID =="B_GRD_1" ||strColumnID =="C_GRD_1" ||strColumnID =="D_GRD_1" )	{
		objPrmDt.SetColumn(nRow, "S_GRD_1", 0);
		objPrmDt.SetColumn(nRow, "A_GRD_1", 0);
		objPrmDt.SetColumn(nRow, "B_GRD_1", 0);
		objPrmDt.SetColumn(nRow, "C_GRD_1", 0);
		objPrmDt.SetColumn(nRow, "D_GRD_1", 0);
	} else if (strColumnID =="S_GRD_2" ||strColumnID =="A_GRD_2" ||strColumnID =="B_GRD_2" ||strColumnID =="C_GRD_2" ||strColumnID =="D_GRD_2" )	{
		objPrmDt.SetColumn(nRow, "S_GRD_2", 0);
		objPrmDt.SetColumn(nRow, "A_GRD_2", 0);
		objPrmDt.SetColumn(nRow, "B_GRD_2", 0);
		objPrmDt.SetColumn(nRow, "C_GRD_2", 0);
		objPrmDt.SetColumn(nRow, "D_GRD_2", 0);
	} else if (strColumnID =="S_GRD_3" ||strColumnID =="A_GRD_3" ||strColumnID =="B_GRD_3" ||strColumnID =="C_GRD_3" ||strColumnID =="D_GRD_3" )	{
		objPrmDt.SetColumn(nRow, "S_GRD_3", 0);
		objPrmDt.SetColumn(nRow, "A_GRD_3", 0);
		objPrmDt.SetColumn(nRow, "B_GRD_3", 0);
		objPrmDt.SetColumn(nRow, "C_GRD_3", 0);
		objPrmDt.SetColumn(nRow, "D_GRD_3", 0);
	} else if (strColumnID =="S_GRD_4" ||strColumnID =="A_GRD_4" ||strColumnID =="B_GRD_4" ||strColumnID =="C_GRD_4" ||strColumnID =="D_GRD_4" )	{
		objPrmDt.SetColumn(nRow, "S_GRD_4", 0);
		objPrmDt.SetColumn(nRow, "A_GRD_4", 0);
		objPrmDt.SetColumn(nRow, "B_GRD_4", 0);
		objPrmDt.SetColumn(nRow, "C_GRD_4", 0);
		objPrmDt.SetColumn(nRow, "D_GRD_4", 0);
	} else if (strColumnID =="S_GRD_5" ||strColumnID =="A_GRD_5" ||strColumnID =="B_GRD_5" ||strColumnID =="C_GRD_5" ||strColumnID =="D_GRD_5" )	{
		objPrmDt.SetColumn(nRow, "S_GRD_5", 0);
		objPrmDt.SetColumn(nRow, "A_GRD_5", 0);
		objPrmDt.SetColumn(nRow, "B_GRD_5", 0);
		objPrmDt.SetColumn(nRow, "C_GRD_5", 0);
		objPrmDt.SetColumn(nRow, "D_GRD_5", 0);
	}
	objPrmDt.FireEvent = true;
}

function fcRankMeasure(obj)
{
	var nCnt     = 0;
	var nSame    = 1;
	var nTetmp   = 0;
	var sDongJum = "";
	var nFindRow = dsDistr2.FindRow("CHK", "1");
	var nReleaEscCnt = 0;

	var nS = dsDistr2.GetColumn(nFindRow, "S_GRD_NUM");
	var nA = dsDistr2.GetColumn(nFindRow, "A_GRD_NUM");
	var nB = dsDistr2.GetColumn(nFindRow, "B_GRD_NUM");
	var nC = dsDistr2.GetColumn(nFindRow, "C_GRD_NUM");
	var nD = dsDistr2.GetColumn(nFindRow, "D_GRD_NUM");

	dsEmpRpm.FireEvent = false;

	dsEmpRpm.Sort("RNK");	// Rank로 Sorting
	//Grd 초기화
	for ( var j=0; j<dsEmpRpm.rowcount;j++) dsEmpRpm.SetColumn(j, "GRD", "");


	// 발매실적 제외자 조회 및 B등급 인원수에서 제외자인원수 빼기 ----------
	nReleaEscCnt = 0;
	for ( var j=0; j<dsEmpRpm.rowcount;j++) {
		if (dsEmpRpm.GetColumn(j, "RELEA_ESC_YN") == "Y") {
			dsEmpRpm.SetColumn(j, "GRD", "B");
			nReleaEscCnt ++;
		}
	}
	if (toInteger(nB) < nReleaEscCnt) {
		fnc_message("발매실적 제외인원이 B등급 배분인원보다 많습니다. \n전산실로 문의바랍니다.","N");
		divBtnLst.btnSave.Enable = false;
		return;
	}
	if (nReleaEscCnt>0) {
		nB = ToString(toInteger(nB) - nReleaEscCnt);		
		dsEmpRpm.Filter("RELEA_ESC_YN="+quote("N"));
	}		
	//-----------------------------------------------------------------------
	if( nS > 0 ) {
		//배푼표대로 S랭크 Setting
		for ( var l = 0; l<toInteger(nS); l++) {
			dsEmpRpm.SetColumn(l, "GRD", "S");
		}

		//S랭크 동점자 처리
		sDongJum = dsEmpRpm.GetColumn(toInteger(nS)-1, "RNK");
		nTetmp   = 0;

		for(var i=0;i<toInteger(nS);i++) {
			if( dsEmpRpm.GetColumn(i, "RNK") == sDongJum ) {
				nTetmp++;
			}
		}

		nSame = 1;

		while(true) {
			if( dsEmpRpm.FindRow("RNK", toInteger(sDongJum)+toInteger(nSame)+toInteger(nTetmp)-1 ) == -1 ) {
				dsEmpRpm.SetColumn(toInteger(nS)+toInteger(nSame)-1, "GRD", "S");
				nCnt++;
				nSame++;
			} else {
				break;
			}
		}
	}

	if( nA > 0 ) {
		//배푼표대로 A랭크 Setting
		for ( var k = toInteger(nS); k<(toInteger(nS)+toInteger(nA)); k++) {	
			if( fnc_IsNull( dsEmpRpm.GetColumn(k, "GRD")) ) {
				dsEmpRpm.SetColumn(k, "GRD", "A");
			}
		}

		//A랭크 동점자 처리
		sDongJum = dsEmpRpm.GetColumn(toInteger(nS)+toInteger(nA)-1, "RNK");
		nTetmp   = 0;

		for(var i=0;i<toInteger(nS)+toInteger(nA);i++) {
			if( dsEmpRpm.GetColumn(i, "RNK") == sDongJum ) {
				nTetmp++;
			}
		}

		nSame = 1;

		while(true) {
			if( dsEmpRpm.FindRow("RNK", toInteger(sDongJum)+toInteger(nSame)+toInteger(nTetmp)-1 ) == -1 ) {
				dsEmpRpm.SetColumn(toInteger(nS)+toInteger(nA)+toInteger(nSame)-1, "GRD", "A");
				nCnt++;
				nSame++;
			} else {
				break;
			}
		}
	}

	if( nB > 0 ) {
		//배푼표대로 B랭크 Setting
		for ( var j = (toInteger(nS)+toInteger(nA)); j<(toInteger(nS)+toInteger(nA)+toInteger(nB)); j++) {
			if( fnc_IsNull( dsEmpRpm.GetColumn(j, "GRD")) )
				dsEmpRpm.SetColumn(j, "GRD", "B");
		}

		//B랭크 동점자 처리
		sDongJum = dsEmpRpm.GetColumn(toInteger(nS)+toInteger(nA)+toInteger(nB)-1, "RNK");
		nTetmp   = 0;

		for(var i=0;i<toInteger(nS)+toInteger(nA)+toInteger(nB);i++) {
			if( dsEmpRpm.GetColumn(i, "RNK") == sDongJum ) {
				nTetmp++;
			}
		}

		nSame = 1;

		while(true) {
			if( dsEmpRpm.FindRow("RNK", toInteger(sDongJum)+toInteger(nSame)+toInteger(nTetmp)-1 ) == -1 ) {
				dsEmpRpm.SetColumn(toInteger(nS)+toInteger(nA)+toInteger(nB)+toInteger(nSame)-1, "GRD", "B");
				nCnt++;
				nSame++;
			} else {
				break;
			}
		}
	}

	if( nC > 0 ) {
		//배푼표대로 C랭크 Setting
		for ( var h = (toInteger(nS)+toInteger(nA)+toInteger(nB)); h<(toInteger(nS)+toInteger(nA)+toInteger(nB)+toInteger(nC)); h++) {
			if( fnc_IsNull( dsEmpRpm.GetColumn(h, "GRD")) )
				dsEmpRpm.SetColumn(h, "GRD", "C");
		}

		//C랭크 동점자 처리
		sDongJum = dsEmpRpm.GetColumn(toInteger(nS)+toInteger(nA)+toInteger(nB)+toInteger(nC)-1, "RNK");
		nTetmp   = 0;

		for(var i=0;i<toInteger(nS)+toInteger(nA)+toInteger(nB)+toInteger(nC);i++) {
			if( dsEmpRpm.GetColumn(i, "RNK") == sDongJum ) {
				nTetmp++;
			}
		}

		nSame = 1;

		while(true) {
			if( dsEmpRpm.FindRow("RNK", toInteger(sDongJum)+toInteger(nSame)+toInteger(nTetmp)-1 ) == -1 ) {
				dsEmpRpm.SetColumn(toInteger(nS)+toInteger(nA)+toInteger(nB)+toInteger(nC)+toInteger(nSame)-1, "GRD", "C");
				nCnt++;
				nSame++;
			} else {
				break;
			}
		}
	}


	//배푼표대로 D랭크 Setting
	for ( var g = (toInteger(nS)+toInteger(nA)+toInteger(nB)+toInteger(nC)); g<(toInteger(nS)+toInteger(nA)+toInteger(nB)+toInteger(nC)+toInteger(nD)); g++) {
		if( fnc_IsNull( dsEmpRpm.GetColumn(g, "GRD")) )
			dsEmpRpm.SetColumn(g, "GRD", "D");
	}
	dsEmpRpm.UnFilter();
	dsEmpRpm.Sort("RNK");	// Rank로 Sorting
	dsEmpRpm.FireEvent = true;
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex) 
{
	switch( obj.id ) {
		case "cboDeptNm" :
				sDeptCd = obj.Value;
			break;
	}
}

function fcGridSort(obj,nCell,nX,nY) 
{
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	switch( obj.id ) {
		case "dsPrmDt_1" :
		case "dsPrmDt_2" :
		case "dsPrmDt_3" :
		case "dsPrmDt_4" :
		case "dsPrmDt_5" :
			var objPhoto = Object("tab.tab1.tabPrm.tab"+(tab.tab1.tabPrm.tabindex +1)+".divPhoto");
			objPhoto.fcPhotoView(obj.GetColumn(nRow, "EMP_NO"));
		break;

		case "dsEmpRpm" :
			tab.tab2.divPhoto.fcPhotoView(obj.GetColumn(nRow, "EMP_NO"));
		break;
	}
}

// 해당 폼사이즈 변경시
function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy);
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX,nY)
{	
	var nHeight = nY;
	var objGrdPrmDt;
	if( nY <= 580){
		for (var i=1;i<=nLitemTypeTotCnt;i++) {
			objGrdPrmDt = Object("tab.tab1.tabPrm.tab"+i+".grdPrmDt");
			objGrdPrmDt.Height = 297;
		}
		tab.tab2.grdTckRslt.Height = 320;

		tab.height = 475;
		tab.tab1.tabPrm.height = 432;		
	} else {
		for (var i=1;i<=nLitemTypeTotCnt;i++) {
			objGrdPrmDt = Object("tab.tab1.tabPrm.tab"+i+".grdPrmDt");
			objGrdPrmDt.Height = nHeight - 285;
		}
		tab.tab2.grdTckRslt.Height = nHeight - tab.tab2.grdTckRslt.top - 132;

		tab.height = nHeight - tab.top;
		tab.tab1.tabPrm.height = nHeight - tab.top - 43;
	}
}

function fcResizeGrdPrmDt(nLitemTypeNum)
{
	var objGrd = Object("tab.tab1.tabPrm.tab"+nLitemTypeNum+".grdPrmDt");
	var objDs = Object("dsPrmDt_"+nLitemTypeNum);
	var objDsDistr = Object("dsDistr1_"+nLitemTypeNum);
	var colNum = 33;

	//업무수행평가 체크 않했을 시, 체크 않된 상태로 만듬
	if( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_5" )) == true )
	{
		objGrd.SetColProp(colNum--, "width" , 0);
		objGrd.SetColProp(colNum--, "width" , 0);
		objGrd.SetColProp(colNum--, "width" , 0);
		objGrd.SetColProp(colNum--, "width" , 0);
		objGrd.SetColProp(colNum--, "width" , 0);
		objGrd.SetColProp(colNum--, "width" , 0);
		if( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_4" )) == true )
		{
			objGrd.SetColProp(colNum--, "width" , 0);
			objGrd.SetColProp(colNum--, "width" , 0);
			objGrd.SetColProp(colNum--, "width" , 0);
			objGrd.SetColProp(colNum--, "width" , 0);
			objGrd.SetColProp(colNum--, "width" , 0);
			objGrd.SetColProp(colNum--, "width" , 0);
			if( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_3" )) == true )
			{
				objGrd.SetColProp(colNum--, "width" , 0);
				objGrd.SetColProp(colNum--, "width" , 0);
				objGrd.SetColProp(colNum--, "width" , 0);
				objGrd.SetColProp(colNum--, "width" , 0);
				objGrd.SetColProp(colNum--, "width" , 0);
				objGrd.SetColProp(colNum--, "width" , 0);
				if( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_2" )) == true )
				{
					objGrd.SetColProp(colNum--, "width" , 0);
					objGrd.SetColProp(colNum--, "width" , 0);
					objGrd.SetColProp(colNum--, "width" , 0);
					objGrd.SetColProp(colNum--, "width" , 0);
					objGrd.SetColProp(colNum--, "width" , 0);
					objGrd.SetColProp(colNum--, "width" , 0);
				}
			}
		}
	}



	if( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_1" )) == false )
	{
		objGrd.SetCellProp("head",4,"text", objDs.GetColumn(objDsDistr.currow , "ESTM_LITEM_NM_1"));
		if( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_2" )) == false )
		{
			objGrd.SetCellProp("head",6,"text", objDs.GetColumn(objDsDistr.currow , "ESTM_LITEM_NM_2"));
			if( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_3" )) == false )
			{
				objGrd.SetCellProp("head",8,"text", objDs.GetColumn(objDsDistr.currow , "ESTM_LITEM_NM_3"));
				if( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_4" )) == false )
				{
					objGrd.SetCellProp("head",10,"text", objDs.GetColumn(objDsDistr.currow , "ESTM_LITEM_NM_4"));
					if( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_5" )) == false )
					{
						objGrd.SetCellProp("head",12,"text", objDs.GetColumn(objDsDistr.currow , "ESTM_LITEM_NM_5"));
					}
				}
			}
		}
	}

}

function fcCheckSum(nLitemTypeNum) 
{	
	var objDsDistr1 = Object("dsDistr1_"+nLitemTypeNum);
	var objGrd = Object("tab.tab1.tabPrm.tab"+nLitemTypeNum+".grdPrmDt");
	var objDs = Object("dsPrmDt_"+nLitemTypeNum);
	var colNo = 1;
	var colNo2 = 33;

	// 배분표 선택 여부 체크
	if (objDsDistr1.RowCount()==0) return true;

	if( objDsDistr1.FindRow("CHK", 1) == -1 ) {
		tab.tab1.TabPrm.TabIndex = nLitemTypeNum -1;
		fnc_Message(SNIS_RBM_F055, "N");
		return false;
	}

	for (var nItm=1;nItm<5;nItm++) {
		if ( fnc_isNull(objDs.GetColumn(0, "ESTM_LITEM_CD_"+nItm )) == false ) {
			if (colNo%6 == 0) {
				colNo++;
				if (colNo2 > 40) colNo2++;
			}
			if( objGrd.GetCellProp("summ",colNo++,"text") == objGrd.GetCellText("summ", -1, colNo2++)
				&& objGrd.GetCellProp("summ",colNo++,"text") == objGrd.GetCellText("summ", -1, colNo2++)
				&& objGrd.GetCellProp("summ",colNo++,"text") == objGrd.GetCellText("summ", -1, colNo2++)
				&& objGrd.GetCellProp("summ",colNo++,"text") == objGrd.GetCellText("summ", -1, colNo2++)
				&& objGrd.GetCellProp("summ",colNo++,"text") == objGrd.GetCellText("summ", -1, colNo2++) ) {

			} else {
				tab.tab1.TabPrm.TabIndex = nLitemTypeNum -1;
				var sMesg = "";
				sMesg = "평가항목의 선택된 갯수가 배분기준과 다릅니다.\\n\n["+objDs.GetColumn(0, "ESTM_LITEM_NM_"+nItm) + "]" ;
				fnc_Message(sMesg, "N");
				return false;
			}
		}		
	}
}
function tab_tab1_TabPrm_OnChanged(obj,nOldIndex,nNewindex)
{
	// 자료 없는 경우 그리드를 disable 한다.
	var objDs = Object("dsDistr1_"+(nNewindex +1));
	var objGrdPrmDt = Object("tab.tab1.tabPrm.tab"+(nNewindex+1)+".grdPrmDt");
	if (objDs.RowCount() == 0) {
		objGrdPrmDt.visible = false;
		Object("tab.tab1.tabPrm.tab"+(nNewindex+1)).divLitemDesc.visible = false;
		return;
	}

	// 역량항목 설정
	var objDsPrmDt = Object("dsPrmDt_"+(nNewindex+1));
	Object("tab.tab1.tabPrm.tab"+(nNewindex+1)).divLitemDesc.stLitemNm.text = objDsPrmDt.GetColumn(0, "ESTM_LITEM_NM_"+1);
	objGrd.UserData = 0;	
	var filter_str = "ESTM_LITEM_TYPE='00"+(nNewindex+1)+"' && ESTM_LITEM_NO=1";
	dsLitemInfo.filter(filter_str);
	dsLitemInfo.row = -1;
}


function fcGrdOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sEstmLitemNo = Truncate((nCell +2) / 6);
	if (sEstmLitemNo < 1 || sEstmLitemNo > 5) return;

	var objGrd = Object("tab.tab1.tabPrm.tab"+(tab.tab1.tabPrm.TabIndex+1));
	if (objGrd.UserData != sEstmLitemNo) {
		var objDsPrmDt = Object("dsPrmDt_"+(tab.tab1.tabPrm.TabIndex+1));
		Object("tab.tab1.tabPrm.tab"+(tab.tab1.tabPrm.TabIndex+1)).divLitemDesc.stLitemNm.text = objDsPrmDt.GetColumn(nRow, "ESTM_LITEM_NM_"+sEstmLitemNo);
		objGrd.UserData = sEstmLitemNo;

		var filter_str = "ESTM_LITEM_TYPE='00"+(tab.tab1.tabPrm.TabIndex+1)+"' && ESTM_LITEM_NO="+sEstmLitemNo;
		dsLitemInfo.filter(filter_str);
		dsLitemInfo.row = -1;
	}
}
