/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsEmp.ClearData();
    dsMnrDt.ClearData();	//dt를 반드시 먼저 Clear
    dsMnr.ClearData();
    dsUploadFile.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj)
{
	//화면 종료
	Close();	
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function searchChkEmp(obj)
{
	if ( !fcSearchCheck(obj) ) return;

   	var sServiceName = "rev2020-service:searchChkEmp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEmp=dsEmp ";    

	fcd_AddParam("DEPT_CD"  , nDeptCd);	
	fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));
	fcd_AddParam("EMP_NO"   , gdsUser.GetColumn(0, "USER_ID") );

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj)
{
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev2020-service:searchMnr";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMnr=dsMnr dsAprvStas=dsAprvStas dsEstmEnd=dsEstmEnd "
                     + "dsConst=dsConst ";

    fcd_AddParam("ESTM_DEPT", nDeptCd);	
	fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));	
	fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));
	fcd_AddParam("EMP_NO"   , gdsUser.GetColumn(0, "USER_ID"));
	fcd_AddParam("APRV_DEPT", nDeptCd);
	fcd_AddParam("ESTM_EMP" , dsEmp.GetColumn(0, "MNR_FST_SND"));
	fcd_AddParam("APRV_SEQ" , "2");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

