/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {    
    if(fnc_Message(SNIS_COM_1012, "Y"))  {    
		fcSaveData();
    }
}

function fcSaveData() {
	var sServiceName = "rev2020-service:saveMnrDt";
	var sInDataSet   = "dsMnrDt=dsMnrDt:U "
					 + "dsMnr=dsMnr:A "
					 + "dsUploadFile=dsUploadFile:U ";
	var sOutDataSet  = "dsUploadFile=dsUploadFile ";		

	//첨부파일 서버 업로드 
	if( dsUploadFile.GetRowCount() > 0 ) {
		for (var i = 0 ; i < dsUploadFile.GetRowCount() ; i++ ) {
			if(dsUploadFile.GetRowType(i) == "insert" ) {
				var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
											, dsUploadFile.GetColumn(i, "FILE_PATH" )
											, dsUploadFile.GetColumn(i, "FILE_NM" ) 
											,"");

				var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

				if ( rtn_arr[0] != "SUCC" ) {
					fnc_Message(SNIS_COM_1050, "N", rtn_arr[1]);
					return false;
				}

				dsUploadFile.SetColumn(i, "FILE_PATH", rtn_arr[1] );
				dsUploadFile.SetColumn(i, "FILE_SIZE", fileSize );

			} // end if
		}
	} // end 

	nMWorkRow = dsMnr.currow;

	//첨부파일 변경시
	fcd_AddParam("ESTM_YEAR"    , dsMnrDt.GetColumn(dsMnrDt.currow,"ESTM_YEAR"));
	fcd_AddParam("ESTM_NUM"     , dsMnrDt.GetColumn(dsMnrDt.currow,"ESTM_NUM"));
	fcd_AddParam("ESTM_DEPT"    , dsMnrDt.GetColumn(dsMnrDt.currow,"ESTM_DEPT"));
	fcd_AddParam("ESTM_LITEM_CD", dsMnrDt.GetColumn(dsMnrDt.currow,"ESTM_LITEM_CD"));
	fcd_AddParam("ESTM_EMP"     , dsMnrDt.GetColumn(dsMnrDt.currow,"ESTM_EMP"));
	fcd_AddParam("EMP_NO"       , dsMnrDt.GetColumn(dsMnrDt.currow,"EMP_NO"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

