/***************************************************************************************************
*                                      Grid 삽입, 추가, 삭제 처리                                  *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	switch ( obj.GetForm().id ) {
		case "fileBtnRow" :	// 첨부파일 추가
			if( dsAprvStas.GetColumn(0, "APRV_STAS") != "001" ) {
				fnc_Message(SNIS_RBM_F033, "N");
				return;
			}

			var sResponse = fcd_GetSelectFile();

			if ( fnc_IsNull(sResponse) ) return;

			var nRow = dsUploadFile.addRow();

			dsUploadFile.SetColumn(nRow, "ATT_FILE_KEY" , dsMnrDt.GetColumn(dsMnrDt.currow,"ATT_FILE_KEY"));

			if ( !fnc_IsNull(sResponse) ) {
				dsUploadFile.SetColumn(nRow, "FILE_PATH", sResponse[0] + "\\" + sResponse[1]);
				dsUploadFile.SetColumn(nRow, "FILE_NM"  , sResponse[1]);
			}
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	switch ( obj.GetForm().id ) {		
		case "fileBtnRow" :
			if( dsAprvStas.GetColumn(0, "APRV_STAS") != "001" ) {
				fnc_Message(SNIS_RBM_F033, "N");
				return;
			}

			if ( !fnc_DeleteCheck(dsUploadFile, "CHK", "1") ) return;
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev2020-service:searchChkEmp" ) {
		fcSearch();
	}

	if ( sServiceName == "rev2020-service:searchMnr" )
	{
		if( nMWorkRow >= 0 ) dsMnr.row = nMWorkRow;
		nMWorkRow = -1;
		// 평가개시 전, 평가마감 후
		if( dsConst.GetColumn(0, "ESTM_OPN_YN") == "N"   ||							// 평가 개시여부
			dsEstmEnd.GetColumn(0, "ESTM_END_YN") == "Y") {							// 평가 완료여부
			//dsAppr.FindRow("EMP_NO", gdsUser.GetColumn(0, "USER_ID")) > 0) {		// 평가관리부서장 및 관리자인경우 평가 가능(2013년부터)
			divBtnLst.btnSave.Enable = false;

			grdMnr  .Editable    = false;
			grdMnrDt.Editable    = false;
			fileBtnRow.Visible   = false;
			aprvReqBtn   .Enable = false;
			aprvCanCelBtn.Enable = false;
		}

		//승인권한에 따른 버튼처리
		var sAprvEmpNo = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_OFIR_NO");	//승인자
		var sReqEmpNo  = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_REQ_NO");	//요청자
		var sAprvCd    = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_STAS");	    //상태값
		var sLoginUser = gdsUser.GetColumn(0, "USER_ID");

		var arrBtn = array(5);
		arrBtn[0] = aprvCanCelBtn;
		arrBtn[1] = aprvOkBtn;
		arrBtn[2] = aprvBackBtn;
		arrBtn[3] = aprvReqBtn;
		arrBtn[4] = aprvCallBtn;

		fnc_BtnVisibleInit(arrBtn);
		fnc_AprvBtnLoc(sAprvEmpNo, sReqEmpNo, sLoginUser, sAprvCd, btnConduct.Top, toInteger(btnConduct.left)-84 );
	}

	if ( sServiceName == "rev2020-service:searchMnrDt" )
	{
		if ( sInpt = "F" )
		{
			grdMnrDt.Editable = false;
		}
	}

	if ( sServiceName == "rev2020-service:saveMnrDt" )
	{
		if( sReq == "Y" ) {
			if ( fnc_DatasetChangeCheck(dsMnr)   ||
				 fnc_DatasetChangeCheck(dsMnrDt) ||
				 fnc_DatasetChangeCheck(dsUploadFile) ) {
				//저장 할 자료가 없음
				fnc_Message(SNIS_RBM_F015, "N");	
				return false;
			}

			sReq = "N";
			var sServiceName = "rev1050-service:saveAprvMana";
			var sInDataSet   = "";
			var sOutDataSet  = "";    	

			fcd_AddParam("ESTM_YEAR", sEvalYear);	
			fcd_AddParam("ESTM_NUM" , sEvalNum);
			fcd_AddParam("MENU_ID"  , "REV2020");
			fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
			fcd_AddParam("APRVCD"   , "01");		//승인요청	

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
		} else {
			searchChkEmp();
		}
	}

	// 승인관리 완료 후
	if ( sServiceName == "rev1050-service:saveAprvMana" ) {
		fcSearch();		
	}	
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
	fcSearchDetail();
	divPhoto.fcPhotoView(dsMnr.GetColumn(dsMnr.currow, "EMP_NO"));
}

function fcOnRowPosChanged(obj,nOldRow,nRow)
{
	switch ( obj.id ) {
		case "dsMnr" :
			fcSearchDetail();	//세부 평가항목보기
			divPhoto.fcPhotoView(obj.GetColumn(nRow, "EMP_NO"));	//사진
		break;
	}
}

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch ( obj.id )
	{
		case "dsMnrDt" :
			if ( strColumnID == "CNT" ) {
				if( !fnc_IsNull(dsMnrDt.GetColumn(nRow, "HI_CNT")) ) {
					if ( toInteger(dsMnrDt.GetColumn(nRow, "CNT")) > toInteger(dsMnrDt.GetColumn(nRow, "HI_CNT")) ) {
						fnc_Message(SNIS_RBM_F006, "N");
						dsMnrDt.SetColumn(nRow, "CNT", "");
					}
				}

				var pCal = 0;
				var mCal = 0;
				var xData = dsMnrDt.FindRow("FST_SND_GBN", "001");
				var yData = dsMnrDt.FindRow("FST_SND_GBN", "002");

				for ( var i=0; i<dsMnrDt.RowCount(); i++) {
					if ( dsMnrDt.GetColumn(i, "FST_SND_GBN") == "001") {
						pCal = toNumber(pCal) + toNumber(dsMnrDt.GetColumn(i,"ESTM_SCR")) * toNumber(dsMnrDt.GetColumn(i,"CNT"))   ; 
					}
				}

				if ( toNumber(dsMnrDt.GetColumn(xData, "BEST_SCR")) < toNumber(pCal) ) {
					fnc_Message(SNIS_RBM_F007, "N", "가점");
					dsMnrDt.SetColumn(nRow, "CNT", "0");
					return;
				}

				for ( var j=0; j<dsMnrDt.RowCount(); j++) {
					if ( dsMnrDt.GetColumn(j, "FST_SND_GBN") == "002") {
						mCal = toNumber(mCal) + toNumber(dsMnrDt.GetColumn(j,"ESTM_SCR")) * toNumber(dsMnrDt.GetColumn(j,"CNT"))   ; 
					}
				}

				if ( toNumber(dsMnrDt.GetColumn(yData, "BEST_SCR")) < toNumber(mCal) )
				{
					fnc_Message(SNIS_RBM_F007, "N", "감점");
					dsMnrDt.SetColumn(nRow, "CNT", "0");
					return;
				}

				var nTotalAcc = ( toFloat(dsMnrDt.GetColumn(0, "STND_SCR" ) )
				                + toFloat(pCal) 
				                - toFloat(mcal) );

				if( nTotalAcc < 0 )	nTotalAcc = 0;

				dsMnr.SetColumn(dsMnr.currow, "ESTM_SCR", nTotalAcc);			
				grdMnrDt.SetCellProp("summ",3,"text", toFloat(pCal) - toFloat(mcal));
			}
		break;
	}
}

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	switch ( obj.id ) {
		case "dsMnrDt" : // 첨부파일 조회	
			if(dsMnrDt.GetRowType(nRow) ==  "insert") {
				dsUploadFile.ClearData();
			} else {
				fcSearchFile(dsMnrDt.GetColumn(nRow, "ATT_FILE_KEY"));
			}
		break;
	}
}

// 첨부파일 조회 시, 파일 저장
function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) {

		case "grdUploadFile" :
			var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFile.GetColumn(nRow, "FILE_NM"));
			var sServerFileName = dsUploadFile.GetColumn(nRow, "FILE_PATH");
			var sFileSize       = dsUploadFile.GetColumn(nRow, "FILE_SIZE");
			if ( sLocalFileName == null ) return;

			fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsUploadFile);

			break;
	}
}

//평가대상생성 Check
function fcBtnCheck() {
	//작업중인 데이터 존재 시 return
	if ( fnc_DatasetChangeCheck(dsMnr) &&
		 fnc_DatasetChangeCheck(dsMnrDt) &&
		 fnc_DatasetChangeCheck(dsUploadFile)) {
		fnc_Message(SNIS_COM_1052, "N");
		return false;
	}
	/*
	//데이터 없을 시
	if( dsMnr.rowcount == 0 ) {
		fnc_Message(SNIS_COM_1053, "N");
		return false;
	}
	*/
	//필수항목
	for(var i = 0; i < dsMnr.rowcount; i++) {
		if( fnc_IsNull( dsMnr.GetColumn(i, "ESTM_SCR")) ) {
			fnc_Message(SNIS_RBM_D001, "N", i+1, "점수");
			dsMnr.row = i;
			return false;
		}
	}

	return true;
}

function fcBtnOnClick(obj)
{	
	sEvalYear = dsYearNum.GetColumn(0, "ESTM_YEAR");
	sEvalNum  = dsYearNum.GetColumn(0, "ESTM_NUM");

	switch ( obj.id ) {
		case "btnConduct" :
			var sFormId 	= "";
			var sArgs 		= "";
			var arrReturn   = array(2);

			sFormId = "frm_rev::REV2020P.xml";
			sArgs = "";	

			sEmpNo = dsMnr.GetColumn(dsMnr.currow, "EMP_NO");
			sEmpNm = dsMnr.GetColumn(dsMnr.currow, "EMP_NM");

			arrReturn = Dialog(sFormId, sArgs, 334, 443, "");
			break;
		case "aprvReqBtn" :	//승인요청
			if ( !fcBtnCheck() ) return;

			if(fnc_Message(SNIS_RBM_F032, "Y","승인요청")) {  
				sReq = "Y";
				fcSaveData();
			}
			break;

		case "aprvCallBtn" :	//회수
			if(fnc_Message(SNIS_RBM_F032, "Y","회수")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV2020");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "02");		//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvOkBtn" :	//승인
			if(fnc_Message(SNIS_RBM_F032, "Y", "승인")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				var sCurDate = fcd_GetSysdate("YYYYMMDD");

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV2020");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "03");			//승인요청	
				fcd_AddParam("APRVDATE" , sCurDate);		//승인날짜

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvBackBtn" :	//반려
			if(fnc_Message(SNIS_RBM_F032, "Y", "반려")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV2020");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "04");							//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvCanCelBtn" :	//승인취소
			if(fnc_Message(SNIS_RBM_F032, "Y", "승인취소")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV2020");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "05");		//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
	}
}

function fcDsCanRowPosChange(obj,nOldRow,nRow)
{	
	switch ( obj.id )
	{
		case "dsMnr" :
			if ( !fnc_DatasetChangeCheck(dsUploadFile) ) {
				if ( !fnc_DatasetChangeCheck(dsMnrDt) ) {
					//저장 할 자료가 없음
					return;
				}
				fcSaveRowChg();
				return;
			} else {
				if( fnc_Message(SNIS_RBM_F047, "Y") ) {
					if ( !fnc_DatasetChangeCheck(dsMnrDt) ) {
					//저장 할 자료가 없음
					return;
					}
					fcSaveRowChg();
					return;
				} else {
					return false;
				}
			}
		break;

		case "dsMnrDt" :
			if ( !fnc_DatasetChangeCheck(dsUploadFile) ) {
				return;
			} else {
				if( fnc_Message(SNIS_RBM_F047, "Y") ) {
					return;
				} else {
					return false;
				}
			}
		break;
	}
}


function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id )
	{
		case "dsMnrDt" :
			var pCal = 0;
			var mCal = 0;
			var xData = dsMnrDt.FindRow("FST_SND_GBN" , "001");
			var yData = dsMnrDt.FindRow("FST_SND_GBN" , "002");
			for ( var i=0; i<dsMnrDt.RowCount(); i++)
			{
				if ( dsMnrDt.GetColumn(i, "FST_SND_GBN") == "001")
				{
					pCal = toNumber(pCal) + toNumber(dsMnrDt.GetColumn(i,"ESTM_SCR")) * toNumber(dsMnrDt.GetColumn(i,"CNT"))   ; 
				}

			}
			for ( var j=0; j<dsMnrDt.RowCount(); j++)
			{
				if ( dsMnrDt.GetColumn(j, "FST_SND_GBN") == "002")
				{
					mCal = toNumber(mCal) + toNumber(dsMnrDt.GetColumn(j,"ESTM_SCR")) * toNumber(dsMnrDt.GetColumn(j,"CNT"))   ; 
				}
			}
			grdMnrDt.SetCellProp("summ",3,"text", toFloat(pCal) - toFloat(mcal));
			fcSearchFile(dsMnrDt.GetColumn(dsMnrDt.currow, "ATT_FILE_KEY"));
		break;
	}
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	nDeptCd = strCode;
	if (strCode != substr(gdsuser.GetColumn(0, "TEAM_CD"),4,4)) {
		divBtnLst.btnSave.Enable = false;
	} else {
		divBtnLst.btnSave.Enable = true;
	}
}
