Window.Script:
/***************************************************************************************************
*                                         공통 Script Include                                      *
***************************************************************************************************/
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";

/***************************************************************************************************
*                                          화면 변수 선언부                                        *
***************************************************************************************************/
var sFormId       = this.GetForm().id;
var sBUTTONLIST   = 'FFFFTFT';                         //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       = "";                                //현재 LOST FOCUS 오브젝트

var sMenuGrpNum     = "0";                         	   // 메뉴그룹의 갯수보관
var sMenuListNum    = "0";                             // 메뉴리스트의 갯수보관
var sMenuUpId       = "";
var sMenuUpNm       = "";
var sMsg            = "";								// 삭제 불가능 row 보관

var sEvalYear = "";
var sEvalNum  = "";
var sLastEstmEndYn = "";

/***************************************************************************************************
*                                         Form 처리 부분                                           *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTFFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
    fcd_GetComboDs(dsInComCd);

    http.sync = true;
	fcSearchMng();
	http.sync = false;

    dsFlag.InsertRow(0);
    dsFlag.SetColumn(0, "CD_NM", "전체");
    cboFlag.Index = 0;

    fcSearchComboDept();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset, sFormId) ) return false;
}

/***************************************************************************************************
*                                      업무화면 공통 버튼 Event 처리 부분                          *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  초기화 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit() {
    dsEvEmp.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
	edEmpNmSearch.Text = Trim(edEmpNmSearch.Text);
	fcInit();	

	if( length(cboDept.Text) > 0 ) {
		return true;
	} else {
		return false;
	}
}

function fcSearch(obj) {
	if ( !fcSearchCheck() ) return;

	var sServiceName = "rev3100-service:searchEvEmpMnr";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEvEmp=dsEvEmp ";     

    fcEstmYearValue();

    //조회조건

    fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	
	fcd_AddParam("DEPT_CD"  , cboDept.Value);
	fcd_AddParam("EMP_NM"   , edEmpNmSearch.Text);
	fcd_AddParam("FLAG"     , cboFlag.Value);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

//부서코드 조회
function fcSearchComboDept(obj)
{
	dsDept.ClearData();

	var sServiceName = "rev1050-service:searchCbDept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDept=dsDept ";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//평가 담당자/팀장 조회
function fcSearchMng() {
	var sServiceName = "rev1070-service:searchCheckAppr";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAppr=dsAppr ";     

    fcEstmYearValue();

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "grdEvEmp";
    fcd_OpenExcelListExport(sExcelList, 0);
}

/*-------------------------------------------------------------------------------------------------+
>>  프린트 인쇄 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev3100-service:searchEvEmpMnr" ) {

	}

	//  부서 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchCbDept" ) {

		cboDept.Value = substr(gdsUser.GetColumn(0, "TEAM_DETL_CD"), length(gdsUser.GetColumn(0, "TEAM_DETL_CD"))-4, 4);

		if( dsAppr.FindRow("EMP_NO", gdsUser.GetColumn(0, "USER_ID")) == -1 ) {
			//cboEstmYear.Enable = false;
			cboDept    .Enable = false;
		} else {
			dsDept.InsertRow(0);
			dsDept.SetColumn(0, "DEPT_NM", "전체");
			cboDept.index = 0;
			//cboEstmYear.Enable = true;
			cboDept    .Enable = true;
		}

		fcSearch();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  Enter Key
+-------------------------------------------------------------------------------------------------*/
function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	switch( obj.id ) {
		case "edEmpNmSearch" :
			if ( nChar == ENTER_KEY )	fcSearch();
			break;
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  콤보박스에서 값 가져오기
+-------------------------------------------------------------------------------------------------*/
function fcEstmYearValue(obj)
{
    var nCbValue = cboEstmYear.Value;
	var nCbRow   = dsEstmYear.SearchRow("CD=="+nCbValue);

	sEvalYear = dsEstmYear.GetColumn(nCbRow,"ESTM_YEAR");
	sEvalNum  = dsEstmYear.GetColumn(nCbRow,"ESTM_NUM");	
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	http.sync = true;
	fcSearchMng();
	http.sync = false;
	fcSearchComboDept();
}
