/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsEmp       .ClearData();
    dsSvrDt     .ClearData();	//dt를 반드시 먼저 Clear
    dsSvr       .ClearData();  
	dsUploadFile.ClearData();
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj)
{
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev3110-service:searchSvr";
	var sInDataSet   = "";
	var sOutDataSet  = "dsSvrDt=dsSvrDt ";

	fcd_AddParam("ESTM_YEAR", dsYearNum.GetColumn(0, "ESTM_YEAR"));		
	fcd_AddParam("ESTM_NUM" , dsYearNum.GetColumn(0, "ESTM_NUM"));
	fcd_AddParam("ESTM_DEPT", cboDept.Value);
	fcd_AddParam("EMP_NM",    edUserNm.Text);
	fcd_AddParam("ALL_YN",    chkAll.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//첨부파일 조회
