/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSave(obj) {

    if(fnc_Message(SNIS_COM_1012, "Y")) {  		
		fcSaveData();
    }
}

function fcSaveData() {
	var sServiceName = "rev2030-service:saveSvrDt";
	var sInDataSet   = "dsSvrDt=dsSvrDt:U "
					 + "dsSvr=dsSvr:A "
					 + "dsUploadFile=dsUploadFile:U ";
	var sOutDataSet  = "dsUploadFile=dsUploadFile ";

	//첨부파일 서버 업로드 
	if( dsUploadFile.GetRowCount() > 0 ) {
		for (var i = 0 ; i < dsUploadFile.GetRowCount() ; i++ ) {
			if(dsUploadFile.GetRowType(i) == "insert" ) {
				var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
											, dsUploadFile.GetColumn(i, "FILE_PATH" )
											, dsUploadFile.GetColumn(i, "FILE_NM" ) 
											,"");

				var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

				if ( rtn_arr[0] != "SUCC" ) {
					fnc_Message(SNIS_COM_1050, "N", rtn_arr[1]);
					return false;
				}

				dsUploadFile.SetColumn(i, "FILE_PATH", rtn_arr[1] );
				dsUploadFile.SetColumn(i, "FILE_SIZE", fileSize );

			} // end if
		}
	} // end 

	nMWorkRow = dsSvr.currow;

	//첨부파일 변경시
	fcd_AddParam("ESTM_YEAR"    , dsSvrDt.GetColumn(dsSvrDt.currow,"ESTM_YEAR"));
	fcd_AddParam("ESTM_NUM"     , dsSvrDt.GetColumn(dsSvrDt.currow,"ESTM_NUM"));
	fcd_AddParam("ESTM_DEPT"    , dsSvrDt.GetColumn(dsSvrDt.currow,"ESTM_DEPT"));
	fcd_AddParam("ESTM_LITEM_CD", dsSvrDt.GetColumn(dsSvrDt.currow,"ESTM_LITEM_CD"));
	fcd_AddParam("ESTM_EMP"     , dsSvrDt.GetColumn(dsSvrDt.currow,"ESTM_EMP"));
	fcd_AddParam("EMP_NO"       , dsSvrDt.GetColumn(dsSvrDt.currow,"EMP_NO"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcSaveRowChg() {
	var sServiceName = "rev2030-service:saveSvrDt";
	var sInDataSet   = "dsSvr=dsSvr:u "
					 + "dsSvrDt=dsSvrDt:U ";
	var sOutDataSet  = "";    

	fcd_AddParam("ESTM_YEAR", dsSvrDt.GetColumn(dsSvrDt.currow,"ESTM_YEAR"));
	fcd_AddParam("ESTM_NUM" , dsSvrDt.GetColumn(dsSvrDt.currow,"ESTM_NUM"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "", false);
}
/***************************************************************************************************
*                                      Grid 삽입, 추가, 삭제 처리                                  *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	switch ( obj.GetForm().id ) {
		case "fileBtnRow" :	// 첨부파일 추가
			if( dsAprvStas.GetColumn(0, "APRV_STAS") != "001" ) {
				fnc_Message(SNIS_RBM_F033, "N");
				return;
			}

			var sResponse = fcd_GetSelectFile();	

			if ( fnc_IsNull(sResponse) ) return;

			var nRow = dsUploadFile.addRow();

			dsUploadFile.SetColumn(nRow, "ATT_FILE_KEY" , dsSvrDt.GetColumn(dsSvrDt.currow,"ATT_FILE_KEY"));

			if ( !fnc_IsNull(sResponse) ) {
				dsUploadFile.SetColumn(nRow, "FILE_PATH", sResponse[0] + "\\" + sResponse[1]);
				dsUploadFile.SetColumn(nRow, "FILE_NM"  , sResponse[1]);
			}
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	switch ( obj.GetForm().id ) {		
		case "fileBtnRow" :
			if( dsAprvStas.GetColumn(0, "APRV_STAS") != "001" ) {
				fnc_Message(SNIS_RBM_F033, "N");
				return;
			}
			if ( !fnc_DeleteCheck(dsUploadFile, "CHK", "1") ) return;
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if (sServiceName == "rev1050-service:searchAllDept") {
		dsDept.InsertRow(0);
		dsDept.SetColumn(0, "DEPT_NM", "전체");
		cboDept.index = 0;
	}

	if ( sServiceName == "rev2030-service:searchSvr" )
	{
		if( nMWorkRow >= 0 ) dsSvr.row = nMWorkRow;
		nMWorkRow = -1;
		divBtnLst.btnSave.Enable = false;

		grdSvr  .Editable    = false;
		grdSvrDt.Editable    = false;
		fileBtnRow.Visible   = false;
		aprvReqBtn   .Enable = false;
		aprvCanCelBtn.Enable = false;

		//승인권한에 따른 버튼처리
		var sAprvEmpNo = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_OFIR_NO");	//승인자
		var sReqEmpNo  = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_REQ_NO");	//요청자
		var sAprvCd    = dsAprvStas.GetColumn(dsAprvStas.currow, "APRV_STAS");	    //상태값
		var sLoginUser = gdsUser.GetColumn(0, "USER_ID");

		var arrBtn = array(5);
		arrBtn[0] = aprvCanCelBtn;
		arrBtn[1] = aprvOkBtn;
		arrBtn[2] = aprvBackBtn;
		arrBtn[3] = aprvReqBtn;
		arrBtn[4] = aprvCallBtn;

		fnc_BtnVisibleInit(arrBtn);
		fnc_AprvBtnLoc(sAprvEmpNo, sReqEmpNo, sLoginUser, sAprvCd, grdSvrDt.Top-aprvReqBtn.Height-2, grdSvrDt.right-aprvReqBtn.width-1);
	}

	if ( sServiceName == "rev2030-service:searchSvrDt" )
	{
		grdSvrDt.Editable = false;
		if( sReq == "Y" ) {
			if ( fnc_DatasetChangeCheck(dsSvr)   ||
				 fnc_DatasetChangeCheck(dsSvrDt) ||
				 fnc_DatasetChangeCheck(dsUploadFile) ) {
				//저장 할 자료가 없음
				fnc_Message(SNIS_RBM_F015, "N");	
				return false;
			}

			sReq = "N";
			var sServiceName = "rev1050-service:saveAprvMana";
			var sInDataSet   = "";
			var sOutDataSet  = "";    	

			fcd_AddParam("ESTM_YEAR", sEvalYear);	
			fcd_AddParam("ESTM_NUM" , sEvalNum);
			fcd_AddParam("MENU_ID"  , "REV2030");
			fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
			fcd_AddParam("APRVCD"   , "01");		//승인요청	

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
		} else {
			searchChkEmp();
		}
	}

	// 승인관리 완료 후
	if ( sServiceName == "rev1050-service:saveAprvMana" ) {
		fcSearch();		
	}	
}

