function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	switch ( obj.id ) {
		case "dsSvrDt" :
			if ( strColumnID == "CNT" ) {
				if( !fnc_IsNull(dsSvrDt.GetColumn(nRow, "HI_CNT")) ) {
					if ( toInteger(dsSvrDt.GetColumn(nRow, "CNT")) > toInteger(dsSvrDt.GetColumn(nRow, "HI_CNT")) ) {
						fnc_Message(SNIS_RBM_F006, "N");
						dsSvrDt.SetColumn(nRow, "CNT", "");
					}
				}

				var pCal = 0;
				var mCal = 0;
				var xData = dsSvrDt.FindRow("FST_SND_GBN" , "001");
				var yData = dsSvrDt.FindRow("FST_SND_GBN" , "002");
				for ( var i=0; i<dsSvrDt.RowCount(); i++)
				{
					if ( dsSvrDt.GetColumn(i, "FST_SND_GBN") == "001")
					{
						pCal = toNumber(pCal) + toNumber(dsSvrDt.GetColumn(i,"ESTM_SCR")) * toNumber(dsSvrDt.GetColumn(i,"CNT"))   ; 
					}

				}

				if ( toNumber(dsSvrDt.GetColumn(xData, "BEST_SCR")) < toNumber(pCal) ) {
					fnc_Message(SNIS_RBM_F007, "N", "가점");
					dsSvrDt.SetColumn(nRow, "CNT", "0");
					return;
				}

				for ( var j=0; j<dsSvrDt.RowCount(); j++) {
					if ( dsSvrDt.GetColumn(j, "FST_SND_GBN") == "002") {
						mCal = toNumber(mCal) + toNumber(dsSvrDt.GetColumn(j,"ESTM_SCR")) * toNumber(dsSvrDt.GetColumn(j,"CNT")); 
					}
				}

				if ( toNumber(dsSvrDt.GetColumn(yData, "BEST_SCR")) < toNumber(mCal) ) {
					fnc_Message(SNIS_RBM_F007, "N", "감점");
					dsSvrDt.SetColumn(nRow, "CNT", "0");
					return;
				}

				var nTotalAcc = (toFloat(dsSvrDt.GetColumn(0, "STND_SCR" ) ) 
				               + toFloat(pCal) 
				               - toFloat(mcal) );

				if( nTotalAcc < 0 )	nTotalAcc = 0;

				dsSvr.SetColumn(dsSvr.currow, "ESTM_SCR", nTotalAcc);
				grdSvrDt.SetCellProp("summ",3,"text", toFloat(pCal) - toFloat(mcal));
			}
		break;
	}
}

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	switch ( obj.id ) {
		case "dsSvrDt" : // 첨부파일 조회	
			if(dsSvrDt.GetRowType(nRow) ==  "insert") {
				dsUploadFile.ClearData();
			} else {
				fcSearchFile(dsSvrDt.GetColumn(nRow, "ATT_FILE_KEY"));
			}
			break;
	}
}

// 첨부파일 조회 시, 파일 저장
function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) {

		case "grdUploadFile" :
			var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFile.GetColumn(nRow, "FILE_NM"));
			var sServerFileName = dsUploadFile.GetColumn(nRow, "FILE_PATH");
			var sFileSize       = dsUploadFile.GetColumn(nRow, "FILE_SIZE");
			if ( sLocalFileName == null ) return;

			fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsUploadFile);

			break;
	}
}

function fcBtnCheck() {
	//작업중인 데이터 존재 시 return
	if ( fnc_DatasetChangeCheck(dsSvr) &&
		 fnc_DatasetChangeCheck(dsSvrDt) &&
		 fnc_DatasetChangeCheck(dsUploadFile)) {
		fnc_Message(SNIS_COM_1052, "N");
		return false;
	}
	/*
	//데이터 없을 시
	if( dsSvr.rowcount == 0 ) {
		fnc_Message(SNIS_COM_1053, "N");
		return false;
	}
	*/
	//필수항목
	for(var i = 0; i < dsSvr.rowcount; i++) {
		if( fnc_IsNull( dsSvr.GetColumn(i, "ESTM_SCR")) ) {
			fnc_Message(SNIS_RBM_D001, "N", i+1, "점수");
			dsSvr.row = i;
			return false;
		}
	}

	return true;
}

function fcBtnOnClick(obj)
{
	sEvalYear = dsYearNum.GetColumn(0, "ESTM_YEAR");
	sEvalNum  = dsYearNum.GetColumn(0, "ESTM_NUM");

	switch ( obj.id ) {
		case "aprvReqBtn" :	//승인요청

			if ( !fcBtnCheck() ) return;

			if(fnc_Message(SNIS_RBM_F032, "Y","승인요청")) { 
				sReq = "Y";
				fcSaveData();	
			}
			break;

		case "aprvCallBtn" :	//회수
			if(fnc_Message(SNIS_RBM_F032, "Y","회수")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV2030");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "02");		//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvOkBtn" :	//승인
			if(fnc_Message(SNIS_RBM_F032, "Y", "승인")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				var sCurDate = fcd_GetSysdate("YYYYMMDD");

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV2030");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "03");			//승인요청	
				fcd_AddParam("APRVDATE" , sCurDate);		//승인날짜

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvBackBtn" :	//반려
			if(fnc_Message(SNIS_RBM_F032, "Y", "반려")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV2030");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "04");							//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvCanCelBtn" :	//승인취소
			if(fnc_Message(SNIS_RBM_F032, "Y", "승인취소")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV2030");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "05");		//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
	}
}


function fcDsCanRowPosChange(obj,nOldRow,nRow)
{
	switch ( obj.id )
	{
		case "dsSvr" :
			if ( !fnc_DatasetChangeCheck(dsUploadFile) ) {
				if ( !fnc_DatasetChangeCheck(dsSvrDt) ) {
					//저장 할 자료가 없음
					return;
				}
				fcSaveRowChg();
				return;
			} else {
				if( fnc_Message(SNIS_RBM_F047, "Y") ) {
					if ( !fnc_DatasetChangeCheck(dsSvrDt) ) {
						//저장 할 자료가 없음
						return;
					}
					fcSaveRowChg();
					return;
				} else {
					return false;
				}
			}
		break;

		case "dsSvrDt" :
			if ( !fnc_DatasetChangeCheck(dsUploadFile) ) {
				return;
			} else {
				if( fnc_Message(SNIS_RBM_F047, "Y") ) {
					return;
				} else {
					return false;
				}
			}
		break;
	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id )
	{
		case "dsSvrDt" :
			var pCal = 0;
			var mCal = 0;
			var xData = dsSvrDt.FindRow("FST_SND_GBN" , "001");
			var yData = dsSvrDt.FindRow("FST_SND_GBN" , "002");
			for ( var i=0; i<dsSvrDt.RowCount(); i++)
			{
				if ( dsSvrDt.GetColumn(i, "FST_SND_GBN") == "001")
				{
					pCal = toNumber(pCal) + toNumber(dsSvrDt.GetColumn(i,"ESTM_SCR")) * toNumber(dsSvrDt.GetColumn(i,"CNT"))   ; 
				}

			}
			for ( var j=0; j<dsSvrDt.RowCount(); j++)
			{
				if ( dsSvrDt.GetColumn(j, "FST_SND_GBN") == "002")
				{
					mCal = toNumber(mCal) + toNumber(dsSvrDt.GetColumn(j,"ESTM_SCR")) * toNumber(dsSvrDt.GetColumn(j,"CNT"))   ; 
				}
			}
			grdSvrDt.SetCellProp("summ",3,"text", toFloat(pCal) - toFloat(mcal));
			fcSearchFile(dsSvrDt.GetColumn(dsSvrDt.currow, "ATT_FILE_KEY"));
		break;
	}
}


function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	nDeptCd = strCode;
	if (strCode != substr(gdsuser.GetColumn(0, "TEAM_CD"),4,4)) {
		divBtnLst.btnSave.Enable = false;
	} else {
		divBtnLst.btnSave.Enable = true;
	}
}

function fcOnClick(obj,strValue)
{
	if (obj.id == "chkAll") {
		if (chkAll.Value == "1") chkAll.Text = "평가";
		else                     chkAll.Text = "전체";
	}
}
