/*-------------------------------------------------------------------------------------------------+
>>  초기화 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsGrd   .ClearData();  
	dsMaster.ClearData();
	dsEvEmp .ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev4010-service:searchMaster";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMaster=dsMaster ";     

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcSearchEvEmp(sEvalYear, sEvalNum) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev4010-service:searchEvEmp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEvEmp=dsEvEmp ";    

	fcd_AddParam("ESTM_YEAR", sEvalYear);
	fcd_AddParam("ESTM_NUM" , sEvalNum);    

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

