/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev4010-service:searchMaster" ) {
		var nRow = dsMaster.currow;

		sEvalYear = dsMaster.GetColumn(nRow, "ESTM_YEAR");
		sEvalNum  = dsMaster.GetColumn(nRow, "ESTM_NUM");

		stDate.Text = sEvalYear + "년 "	//(2011.01.01~2011.11.31)
		             + sEvalNum  + "회차 평가 "
					 + "(" + substr(dsMaster.GetColumn(nRow, "PRM_STR_DT"), 0, 4) + "." 
					 + substr(dsMaster.GetColumn(nRow, "PRM_STR_DT"), 4, 2) + "." 
					 + substr(dsMaster.GetColumn(nRow, "PRM_STR_DT"), 6, 2) + " ~ "
					 + substr(dsMaster.GetColumn(nRow, "PRM_END_DT"), 0, 4) + "." 
					 + substr(dsMaster.GetColumn(nRow, "PRM_END_DT"), 4, 2) + "." 
					 + substr(dsMaster.GetColumn(nRow, "PRM_END_DT"), 6, 2)
					 + ")";

		fcSearchEvEmp(sEvalYear, sEvalNum);

		//최종평점 및 등급산출이 실행되었다면 
		if( dsMaster.GetColumn(nRow, "LAST_ESTM_END_YN") == "Y" ) {
			stGrade .Text = SNIS_RBM_F023;
			gradeBtn.Text = "최종평점 및 등급재산출";
			cfmBtn.Enable = true;
		} else {
			gradeBtn.Text = "최종평점 및 등급산출";
			stGrade.Text  = SNIS_RBM_F024;
			cfmBtn.Enable = false;
		}

		//평가결과확정이 실행되었다면 
		if( dsMaster.GetColumn(nRow, "RSLT_CFM_YN") == "Y" ) {
			cfmBtn.Text = "평가결과확정 취소";
			stCfm.Text  = SNIS_RBM_F023;
			resultBtn.Enable = true;
			gradeBtn.Enable  = false;
		} else {
			cfmBtn.Text      = "평가결과확정";

			if( dsMaster.GetColumn(nRow, "LAST_ESTM_END_YN") == "Y" ) {
				stCfm.Text = SNIS_RBM_F024;
			} else {
				stCfm.Text = SNIS_RBM_F025;
			}
			resultBtn.Enable = false;
			gradeBtn .Enable = true;
		}

		//결과개시 실행되었다면 
		if( dsMaster.GetColumn(nRow, "OPEN_YN") == "Y" ) {
			resultBtn.Text = "결과개시 취소";
			stResult .Text = SNIS_RBM_F023;
			stSms.Enable   = true;
			cfmBtn.Enable  = false;
			stSms          = "";
			smsBtn.Enable = true;
		} else {
			resultBtn.Text = "결과개시";
			if( dsMaster.GetColumn(nRow, "RSLT_CFM_YN") == "Y" ) {
				stResult.Text = SNIS_RBM_F024;
			} else {
				stResult.Text = SNIS_RBM_F025;
			}
			stSms   .Text = SNIS_RBM_F025;
			//smsBtn.Enable = false;
			smsBtn.Enable = true;
		}


	}

	// 최종평점 및 등급산출 완료 후
	if ( sServiceName == "rev4010-service:updateGrade" ) {
		fcSearch();
	}
	// 평가결과확정 완료 후
	if ( sServiceName == "rev4010-service:updateCfm" ) {
		fcSearch();
	}
	// 결과개시 완료 후
	if ( sServiceName == "rev4010-service:updateResult" ) {
		fcSearch();
	}
}

function fcBtnOnClick(obj) {	
	switch( obj.id ) {
		case "gradeBtn" :		//최종평점 및 등급산출
			if(fnc_Message(SNIS_RBM_F022, "Y", gradeBtn.Text)) {           
				var sServiceName = "rev4010-service:updateGrade";
				var sInDataSet   = "";
				var sOutDataSet  = "";  	

				//서버로 보낼 값
				fcd_AddParam("ESTM_YEAR", sEvalYear);
				fcd_AddParam("ESTM_NUM" , sEvalNum);

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;

		case "cfmBtn" :	//평가결과확정

			if(fnc_Message(SNIS_RBM_F022, "Y", cfmBtn.Text)) {           
				var sServiceName = "rev4010-service:updateCfm";
				var sInDataSet   = "";
				var sOutDataSet  = "";

				//서버로 보낼 값
				if( dsMaster.GetColumn(dsMaster.currow, "RSLT_CFM_YN") == "Y" ) {
					fcd_AddParam("RSLT_CFM_YN", "N");
				} else {
					fcd_AddParam("RSLT_CFM_YN", "Y");
				}
				fcd_AddParam("ESTM_YEAR", sEvalYear);
				fcd_AddParam("ESTM_NUM" , sEvalNum);

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
			}
			break;
		case "resultBtn" :		//결과개시
			if(fnc_Message(SNIS_RBM_F022, "Y", resultBtn.Text)) {           
				var sServiceName = "rev4010-service:updateResult";
				var sInDataSet   = "";
				var sOutDataSet  = "";  	

				//서버로 보낼 값
				if( dsMaster.GetColumn(dsMaster.currow, "OPEN_YN") == "Y" ) {
					fcd_AddParam("OPEN_YN", "N");
				} else {
					fcd_AddParam("OPEN_YN", "Y");
				}
				fcd_AddParam("ESTM_YEAR", sEvalYear);
				fcd_AddParam("ESTM_NUM" , sEvalNum);

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
			}
			break;
		case "smsBtn" :		//SMS전송
			if(fnc_Message(SNIS_RBM_F022, "Y", smsBtn.Text))
			{           
				var sSendPhoneNumber 	= gdsUser.GetColumn(0,"HP_NO");	 //송신자이동전화번호 
				var sSendUserId 	 	= gdsUser.GetColumn(0,"USER_ID");//송신자 사번
				var sSendUserName 		= gdsUser.GetColumn(0,"USER_NM");//송신자명 
				var sReceivePhoneNumber = "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
				var sReceiveName 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
				var sReceiveId          = "";
				var sTitle   	= "최종종합평가";//제목 
				var sLinkUrl 	= "";							 //
				var sDesc   	= "";							 //
				var sCompId     = "";
				var sTitle              = "종사원평가(부서별 진행현황)";

				for(var i = 0; i <dsEvEmp.GetRowCount();i++){
					if(dsEvEmp.GetColumn(i,"CHK") == "1"){
						sReceivePhoneNumber += dsEvEmp.GetColumn(i,"HP_NO")+";";					
						sReceiveName        += dsEvEmp.GetColumn(i,"EMP_NM")+";";
						sReceiveId          += dsEvEmp.GetColumn(i,"EMP_NO")+";";					

					}			
				}

				sRecvUser = substr(sRecvUser,0,LENGTH(sRecvUser)-1);

				sParams = "sSendPhoneNumber="+quote(sSendPhoneNumber);
				sParams += " sSendUserId="+quote(sSendUserId);
				sParams += " sSendUserName="+quote(sSendUserName);
				sParams += " sReceivePhoneNumber="+quote(sReceivePhoneNumber);
				sParams += " sReceiveName="+quote(sReceiveName);
				sParams += " sReceiveId="+quote(sReceiveId);
				sParams += " sTitle="+quote(sTitle);
				sParams += " sLinkUrl="+quote(sLinkUrl);
				sParams += " sDesc="+quote(sDesc);
				sParams += " sCompId="+quote(sCompId);
			    sParams += " sTitle="+quote(sTitle);			    
				sParams += " sRecvrUserType="+quote("002");

				nHandle = Open("common::comSendSms.xml", sParams, 240, 328, "Title=false");
			}
			break;

		case "reSmsBtn" :	//전화번호 가져오기
			if(fnc_Message(SNIS_RBM_F061, "Y")) {           
				var sServiceName = "rev4010-service:updateHp";
				var sInDataSet   = "";
				var sOutDataSet  = "";  	

				fcd_AddParam("ESTM_YEAR", sEvalYear);
				fcd_AddParam("ESTM_NUM" , sEvalNum);

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
	}
}
