/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	// 조회 완료 후
	if ( sServiceName == "rev4020-service:searchLastEstmEndYn" ) {
	    if(dsLastEstmEnd.GetRowCount() > 0){
			sLastEstmEndYn = dsLastEstmEnd.GetColumn(0,"LAST_ESTM_END_YN");
	    }else{
			sLastEstmEndYn = "N";
	    }

		fcSearchEvEmp();

	}


	// 조회 완료 후
	if ( sServiceName == "rev4020-service:searchEvEmp" ) {
		if (sEvalYear == "2011") {
			grdEVCreate.SetCellProp("head", 10, "text", "보직분야");
		} else {
			grdEVCreate.SetCellProp("head", 10, "text", "부서그룹");
		}
	}

	//  부서 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchCbDept" ) {

		dsDept.InsertRow(0);
		dsDept.SetColumn(0, "DEPT_NM", "전체");
		cboDept.index = 0;
	}

	//  보직 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchCbWkJb" ) {
		dsWorkJob.InsertRow(0);
		dsWorkJob.SetColumn(0, "WK_JOB_NM", "전체");
		cboWorkJob.index = 0;	
	}
}

