/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    // LIS FILE 날짜 검색
	var sServiceName = "rsm1010-service:searchRaceDayFileInfo";
    var sInDataSet   = "";
    var sOutDataSet  = "dsRaceDayFileInfo=dsRaceDayFileInfo ";

	fcd_AddParam("STND_YEAR", cboStndYear.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


function fcSearchSubCheck(obj) {
	return true;
}

// 상세 내용 조회
function fcSearchSub(obj) {


	if ( !fcSearchSubCheck(obj) ) return;

	var sServiceName = "rsm1010-service:searchCntntList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCntntList=dsCntntList ";
		sOutDataSet  += "dsCntntListSum=dsCntntListSum ";
		sOutDataSet  += "dsMyCat=dsMyCat ";    	


	var RACE_DAY = dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.row , "RACE_DAY");
	var DT=substr(RACE_DAY,4,4);


	fcd_AddParam("STND_YEAR" , dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.row, "STND_YEAR" ));
	fcd_AddParam("RACE_DAY" , RACE_DAY);
	fcd_AddParam("DT", DT);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	var RACE_DAY = dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.currow,"RACE_DAY");
	var FILE_LOCAL_PATH = decode(dsLISFile.GetColumn(0, "FILE_LOCAL_PATH" ),null,'',dsLISFile.GetColumn(0, "FILE_LOCAL_PATH" ));
	var FILE_NM			= decode(dsLISFile.GetColumn(0, "FILE_NM" ),null,'',dsLISFile.GetColumn(0, "FILE_NM" ));
	if(FILE_NM.indexOf(RACE_DAY)<0)
	{		
//		fnc_Message("해당 일자의 파일이 아닙니다.", "N");	
//		return;
	}


    if(length(RACE_DAY)<=0)
	{	
		fnc_Message("일자를 등록해 주십시오.", "N");	
		return;
	}

	if(substr(dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.Row,"RACE_DAY"),0,4) <> cboStndYear.Value){
		fnc_Message("조회조건이 년도와 일자가 일치하지 않습니다.", "N");	
		return;
	}

	if(FILE_NM=="")
	{	
		fnc_Message("LIS파일을 등록해 주십시오.", "N");	
		return;
	}
	if(FILE_LOCAL_PATH=="")
	{	
		fnc_Message("LIS파일이 이미 업로드 되어 있습니다.", "N");	
		return;
	}



	return true;
}


