/*-------------------------------------------------------------------------------------------------+
>>  전송하려는 데이터셋 건수 체크해서 트랜잭션 발생여부 체크
+-------------------------------------------------------------------------------------------------*/
function fcDsCheck(nRow) {
    var sFileName = trim(dsLISFile.GetColumn(nRow, "FILE_LOCAL_PATH"));    
    if(sFileName == null || sFileName == "") {
        return false;        
    }else{
        return true;
    }    
}


