/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {

   //DataSet의 Header 정보가 있을 때  Add 처리
	if ( fnc_DatasetChangeCheck(dsRaceDayFileInfo) ) {
		fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
		return;
	}


    if(dsRaceDayFileInfo.FindRow("DT",fcd_GetSysdate("MMDD") ) >= 0 ){
		fnc_Message(SNIS_RBM_D009,"N");
		return;
    }

	nRow= dsRaceDayFileInfo.InsertRow();

	dsRaceDayFileInfo.SetColumn(nRow,"STND_YEAR",substr(fcd_GetSysdate("YYYYMMDD"), 0, 4));
	dsRaceDayFileInfo.SetColumn(nRow,"RACE_DAY",fcd_GetSysdate("YYYYMMDD"));
	dsRaceDayFileInfo.SetColumn(nRow,"MEET_CD","999");
	dsRaceDayFileInfo.SetColumn(nRow,"MEET_DESC","평일");
	dsRaceDayFileInfo.SetColumn(nRow,"MEET_GUBUN","999");

	dsCntntList.ClearData();
	dsCntntListSum.ClearData();
	dsMyCat.ClearData();


	dsLISFile.SetColumn(0, "FILE_PATH","");
	dsLISFile.SetColumn(0, "FILE_NM", "");

	fcSetData(nRow);


}

