/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if(sServiceName == "rsm1010-service:searchRaceDayFileInfo")
    {
		if (dsRaceDayFileInfo.RowCount() > 1) {
			dsRaceDayFileInfo.SetRowType(0);
			fcSearchSub(this);
		}
    } else if(sServiceName == "rsm1010-service:searchCntntList")  {
		fcSetData(dsRaceDayFileInfo.currow); // 파일 정보 값
    }


	if ( sServiceName == "rsm1010-service:saveLISFileUpload" ) 
	{
		var ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE,"^","=");		
		var FILE_CD	= fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"FILE_CD"); // 파일 결과
		var RESULT	= fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"RESULT"); // 결과

		if(FILE_CD!="")
		{		
			dsRaceDayFileInfo.SetColumn(dsRaceDayFileInfo.currow,"FILE_CD",FILE_CD);
			if(FILE_CD=="001")
			{
				dsRaceDayFileInfo.SetColumn(dsRaceDayFileInfo.currow,"CD_NM","존재");
			}
			else
			{
				dsRaceDayFileInfo.SetColumn(dsRaceDayFileInfo.currow,"CD_NM","미존재");
			}
		}


		if(RESULT!="")
		{
			var RESULT_DESC = decode(RESULT,'T','파일 업로드가 완료되었습니다.','실패하였습니다.');
			fnc_Message(RESULT_DESC, "N");	

			if(RESULT=="T")
			{				
				dsLISFile.SetColumn(0, "FILE_LOCAL_PATH","");	// 파일 업로드 초기화
				fcSearchSub();
				fcSetData(dsRaceDayFileInfo.currow); // 파일 정보 값

				fcSearch();
			}

		}
	}

	if ( sServiceName == "rsm1010-service:saveDivstatrpt" ) 
	{
		fcSearch();
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  파일 찾기
+-------------------------------------------------------------------------------------------------*/
function fcPcUpOnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{

	divBtnLst.fldImage.FileName = "";               //다이얼로그 파일명 초기화

	divBtnLst.fldImage.Open();

    var FILE_LOCAL_PATH = divBtnLst.fldImage.FilePath + "\\" + divBtnLst.fldImage.FileName;
    var FILE_NM = divBtnLst.fldImage.FileName;
	divBtnLst.flImage.Open ( "t+" ); // t: text 모드, +: 읽기/쓰기

	SetWaitCursor(true); // 시스템의 Mouse Cursor를 모래시계(Wait Cursor)로 설정하거나 해제하는 Form API 입니다
	SetCapture();		// 마우스의 이동/ 눌림을 가로채서 Script와 화면에 도달하지 못하도록 하는 Form API 입니다.


	dsLISFile.SetColumn(nRow, "FILE_LOCAL_PATH", FILE_LOCAL_PATH);
	dsLISFile.SetColumn(nRow, "FILE_NM", FILE_NM);

	divBtnLst.flImage.Close();

	SetWaitCursor(false);
    ReleaseCapture();

}
/*-------------------------------------------------------------------------------------------------+
>>  그리드 선택 값
+-------------------------------------------------------------------------------------------------*/
function fcSetData(nRow)
{

	dsLISFile.ClearData();
	dsLISFile.AddRow();


	stRaceDay.Text = "("+dsRaceDayFileInfo.GetColumn(nRow,"RACE_DAY_DESC") +")";

	dsLISFile.SetColumn(0, "MEET_CD", dsRaceDayFileInfo.GetColumn(nRow,"MEET_GUBUN"));
	dsLISFile.SetColumn(0, "STND_YEAR", dsRaceDayFileInfo.GetColumn(nRow,"STND_YEAR"));
	dsLISFile.SetColumn(0, "DT", substr(dsRaceDayFileInfo.GetColumn(nRow,"RACE_DAY"),4,4) );
	dsLISFile.SetColumn(0, "FILE_PATH", dsRaceDayFileInfo.GetColumn(nRow,"FILE_PATH"));
	dsLISFile.SetColumn(0, "FILE_NM", dsRaceDayFileInfo.GetColumn(nRow,"FILE_NM"));
	dsLISFile.SetColumn(0, "RACE_DAY", dsRaceDayFileInfo.GetColumn(nRow,"RACE_DAY"));

}


