//조회내역 클릭이 아닌 키보드 이동일 경우 클릭과 같은 이벤트 발생
function fcGrdOnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	switch ( obj.id ) 
	{	
		case "grdRaceInfo" : // pc 파일 업로드일 경우 파일을 저장한다
			fcGrdOnCellClick(obj, nRow);

			break;
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  파일 다운받기
+-------------------------------------------------------------------------------------------------*/

function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) 
	{	
		case "grdLISFile" : // pc 파일 업로드일 경우 파일을 저장한다
			var sLocalFileName  = fcd_GetSelectFile("Save", dsLISFile.GetColumn(nRow, "FILE_NM")); // 파일명
			var sServerFileName = dsLISFile.GetColumn(nRow, "FILE_PATH");	// 파일 경로
			var sFileSize       = 0;
			if ( sLocalFileName == null ) return;

			fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsLISFile);
			break;
	}
}

function fcOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	
	switch ( obj.id ) 
	{	
		case "dsRaceDayFileInfo" : // pc 파일 업로드일 경우 파일을 저장한다
			if(obj.getRowType(nRow) == "insert" && strColumnID="RACE_DAY"){


				if(varOldValue != varNewValue){
					for(var i=0;i<dsRaceDayFileInfo.GetRowCount();i++)
					{
						if(i != nRow && dsRaceDayFileInfo.GetColumn(i,"RACE_DAY")  == dsRaceDayFileInfo.GetColumn(nRow, "RACE_DAY")){
							fnc_Message(SNIS_RBM_D009,"N");

							dsRaceDayFileInfo.SetColumn(nRow,"RACE_DAY",varOldValue);
							return;
						}

					}

				}


				stRaceDay.Text = "("+dsRaceDayFileInfo.GetColumn(nRow,"RACE_DAY_DESC") +")";

				dsLISFile.SetColumn(0, "MEET_CD", dsRaceDayFileInfo.GetColumn(nRow,"MEET_GUBUN"));
				dsLISFile.SetColumn(0, "STND_YEAR",substr(dsRaceDayFileInfo.GetColumn(nRow,"RACE_DAY"),0,4));
				dsLISFile.SetColumn(0, "DT", substr(dsRaceDayFileInfo.GetColumn(nRow,"RACE_DAY"),4,4) );
				dsLISFile.SetColumn(0, "RACE_DAY", dsRaceDayFileInfo.GetColumn(nRow,"RACE_DAY"));
			}
			break;
	}

}

function fcDsOnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if( nCell == 0) {  
		//제외시작일자, 제외종료일자
		fnc_SetPopupDivCalendar(obj, nRow, nCell);		
	}
}

function fcdsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) 
	{	
		case "dsCntntListSum" :
			if (dsCntntListSum.RowCount() > 3 ) {
				grdCntntListSum.Height = 113;
			} else {
				grdCntntListSum.Height = 95;
			}
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  가져오기 버튼 클릭 시 발생 이벤트
+-------------------------------------------------------------------------------------------------*/
function btnDivstatrpt_OnClick(obj)
{
	if(dsRaceDayFileInfo.GetRowCount() > 0){
		var stnd_year = substr(dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.currow, "RACE_DAY"),0,4);
		var stnd_mm = substr(dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.currow, "RACE_DAY"),4,2);
		var stnd_dd = substr(dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.currow, "RACE_DAY"),6,2);

		var confirmMsg = stnd_year + "-" + stnd_mm + "-" + stnd_dd + "일 데이터를 가져오시겠습니까?";

		if(fnc_Message(SNIS_CRA_B999, "Y", confirmMsg)) {  
			var sServiceName = "rsm1010-service:saveDivstatrpt";
			var sInDataSet   = "";
			var sOutDataSet  = "";

			fcd_AddParam("RACE_DAY", dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.currow, "RACE_DAY"));

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
		}	
	}else{
		fnc_Message("일자를 선택해 주십시오.", "N");
		return;
	}

}
