/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
    if ( sServiceName == "rsm1040-service:searchVeriFstSDLDTPT" ) { // TMS 값 검색

		dsVeriSDLDTPT.row = -1;
		dsVeriSDLPTPayoff.row = -1;
		dsSDLDTSUM.row = -1;
		dsVeriFstSDLDTGrnSell.row = -1;
		dsVeriFstSDLDTGrnSellComm.row = -1;
		return;
	}
    if ( sServiceName == "rsm1040-service:searchTms" ) { // TMS 값 검색

		if(dsTms.GetRowCount()==0)
		{		
			dsTms.SetColumn(0,"TMS","");
			dsTms.SetColumn(0,"TMS_DESC","없음");
			cboTms.Index=0;
		}
		else
		{
			if(TMS != "")
			{
				cboTms.Value = TMS;
				TMS="";
			}
			else
			{		
				cboTms.Value = dsTms.GetColumn(0,"MAX_TMS");		
			}
		}
		fcSearchDayOrd();
		return;
	}
    if ( sServiceName == "rsm1040-service:searchDayOrd" ) {		

		if(dsDayOrd.GetRowCount()==0)
		{
			dsDayOrd.AddRow();
			dsDayOrd.SetColumn(0,"DAY_ORD","");
			dsDayOrd.SetColumn(0,"DAY_DESC","없음");
			cboDayOrd.Index=0;
		}
		else
		{
			if(DAY_ORD != "")
			{
				cboDayOrd.Value = DAY_ORD;
				DAY_ORD="";
			}
			else
			{
				cboDayOrd.Value = dsDayOrd.GetColumn(0,"MAX_DAY_ORD");
			}
		}
		fcSearch();
		return;
	}

	if ( sServiceName == "rsm1040-service:searchVeriFstSDLPTPayoff" )
	{
	}
	grdVeriSDLDTPT.SetFocus();

	stRaceInfo.Text = "("+dsRaceDayInfo.GetColumn(0,"GUBUN_DESC") +" "+dsRaceDayInfo.GetColumn(0,"STND_YEAR")+"년 "+dsRaceDayInfo.GetColumn(0,"RACE_MM")+"월 "+dsRaceDayInfo.GetColumn(0,"RACE_DD")+"일 "+dsRaceDayInfo.GetColumn(0,"TMS")+"회차 "+dsRaceDayInfo.GetColumn(0,"DAY_ORD")+" 일차)";

}

/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.ID ) {	
		case "cboStndYear" :	// 년도 바꿀 경우
			fcSearchTms();		
		break;
		case "rdoMeetCd" :	// 경륜 경정 바꿀 경우
			fcSearchTms();		
		break;
		case "cboTms" :		// 회차 바꿀 경우
			fcSearchDayOrd();		
		break;

		case "cboDayOrd" :	// 일차 바꿀 경우
			fcSearch();		
		break;
	}
}
