var ARRAY2_GBL_RET_VALUE = Array();
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'TTFFFFT';     // T초기화,T조회,F저장,F삭제,T엑셀,F인쇄,T닫기

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	calEventDt.Value = today();

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);	

	var sPrtrType = GetReg("7ExPrtrType","");	
	if (fnc_isnull(sPrtrType)) {
		var nRow = dsBrnc.FindRow("CD", gdsUser.GetColumn(0, "BRNC_CD"));
		if (nRow > -1) {
			rdoPrtrType.value = dsBrnc.GetColumn(nRow, "CD_TERM5");
		}
	} else {
		rdoPrtrType.value = sPrtrType;
	}
	edGrnCdNo.SetFocus();		
}

