function fcInit(obj) {
    dsList.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리

	fcInit(obj);    //기존 데이터셋 초기화

	if (length(trim(edGrnCdNo.text)) < 3) {
		fnc_message("그린카드번호를 최소한 3개이상 입력해야 합니다","N");
		return false;
	}
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm1120-service:searchEventPrti"; 	// 이벤트 응모현황

    var sInDataSet   = "";
    var sOutDataSet  = "dsList=dsList";		    

    fcd_AddParam("EVENT_DT",  	calEventDt.Value);			// 이벤트일자
	fcd_AddParam("GRN_CARD_NO", edGrnCdNo.Value );			// 그린카드번호

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 저장가능유무처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsList)&&!fnc_DatasetChangeCheck(dsIW) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}	

	return true;
}

