/*-------------------------------------------------------------------------------------------------+
>>  인쇄
+-------------------------------------------------------------------------------------------------*/
function fcPrint(){

	ds_reportParam.DeleteAll();

	// 필드 값을 셋팅한다.
	var paramField = [
							"EVENT_DT"								// 기준년도
						,	"CARD_NO"							// 카드번호
						,	"CARD_SEQ"							// 카드연번
						,   "MIN_SLIP_SEQ"

					 ];

	// 값을 셋팅한다.
	var paramValue = [
							dsList.GetColumn(dsList.row, "EVENT_DT")
						,	dsList.GetColumn(dsList.row, "CARD_NO")
						,	dsList.GetColumn(dsList.row, "CARD_SEQ")
						,	dsList.GetColumn(dsList.row, "MIN_SLIP_SEQ")
					 ];


	var rowCnt = paramField.length;	
	for(var row = 0 ; row < rowCnt; row++)
	{
		nRnum = ds_reportParam.AppendRow();
		ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
		ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
	}

	/* 레포트 배율 추가 - 시작 */
	iRow = ds_reportParam.AddRow();

	// Default Scale 파라미터명 추가(고정)
	ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
	ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

	//레포트 트랙잭션을 태운다.
	var sRptNm = "rsm1120";
	if (calEventDt.Value > "20141102") sRptNm = "rsm1120_2";
	sRptNm = "rsm1120_2";
	fcRecptPrint(sRptNm, ds_reportParam.ID);	

	fcSavePrintCnt();	// 출력결과를 저장한다.

	edGrnCdNo.SetFocus();
	edGrnCdNo.SetSel(0, edGrnCdNo.GetLength());
}

