/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	if ( sServiceName == "rsm1120-service:searchEventPrti" )
	{
		if (dsList.rowcount() == 0) {
			stRsltMesg.Text = "해당 카드의 응모내역이 존재하지 않습니다."; //하단 상태바에 메시지 처리
			edGrnCdNo.SetFocus();
			edGrnCdNo.SetSel(0, edGrnCdNo.GetLength());
		} else {
			stRsltMesg.Text = "";
			var sMinRaceNo = dsList.GetColumn(0, "MIN_RACE_NO");
			for (var i=5;i<=11;i++) {
				grdListDetl.SetCellProp("head", i, "text", toString(ToNumber(sMinRaceNo) + i - 5) + "경주");
			}	

			if (chkAutoPrinit.Value == "1") {
				var nCardcnt = 0;
				var sCardNo = "";
				for (var i=0;i<dsList.rowcount();i++) {
					if (sCardNo != dsList.GetColumn(i, "CARD_NO")) {
						nCardCnt ++;
						sCardNo = dsList.GetColumn(i, "CARD_NO");
					}
				}
				if (nCardCnt == 1) fcPrint();
			} 

		}

		return;
	}

}


/*-------------------------------------------------------------------------------------------------+
>>  엔터 조회 처리
+-------------------------------------------------------------------------------------------------*/
function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// 종료일자에서 ENTER_KEY 눌렀을 경우
		case "calEventDt" :	
			if ( nChar == ENTER_KEY ) {
				edGrnCdNo.SetFocus();
				return;
			}
		break;
	}
}



function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	switch ( obj.id ) {
		case "edGrnCdNo" :
			if ( nChar == 13 ) {		
				fcSearch();				
			}
			break;
	}
}

function fcgrdOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nRow >= 0) fcPrint();
}


/*-------------------------------------------------------------------------------------------------+
>>  출력후 출력횟수를 저장
+-------------------------------------------------------------------------------------------------*/
function fcSavePrintCnt(obj) {

	var sServiceName = "rsm1120-service:savePrintCnt"; 	// 이벤트 응모현황		
    var sInDataSet   = "";
    var sOutDataSet  = "";	

    fcd_AddParam("EVENT_DT",     calEventDt.Value);										// 이벤트일자
	fcd_AddParam("CARD_NO", 	 dsList.GetColumn(dsList.row, "CARD_NO"));				// 그린카드 일련번호
	fcd_AddParam("CARD_SEQ", 	 dsList.GetColumn(dsList.row, "CARD_SEQ"));				// 그린카드 - 연번
	//fcd_AddParam("MIN_SLIP_SEQ", dsList.GetColumn(dsList.row, "MIN_SLIP_SEQ"));			// 응모번호

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/**
 * 레포트 미리보기 팝업 오픈(GateWay 방식 - SQL을 레포트에 포함)
 *
 * param rptFile   - 레포트 파일(ID)
 * param dsParamId - 파라미터 정의 Dataset ID
 * param fullSize  - 팝업 호출시 Screen Size에 꽉찬 상태로 호출할것인지 여부
 * return void
 */
function fcRecptPrint(rptFile, paramDsId)
{
    var nWidth = 320;
    var nHeight = 350;
    var nPosX = 0;
    var nPosY = 0;
    var bFullSize;
    var sFileName="";

    var fSQLCallFlag;

    if(rptFile == null || fnc_IsNull(rptFile)) {
        fnc_Message(SNIS_RBM_0007, "rptFile");
        return;
    }

    var sArg = "sRptNm=" + quote(rptFile);

    if(fnc_IsNotNull(paramDsId)) {
        sArg += " paramDsId=" + quote(paramDsId);
    }

    sArg += " sSubUrl=" + quote(mid(RptFile,0,3));	
    sArg += " fSQLCallFlag=true";        
    if (chkAutoPrinit.Value == "1") {
		sArg += " AutoPrint=Y";    	//바로 출력
	} else {
		sArg += " AutoPrint=N";    	//바로 출력 아님
	}
	sArg += " PrtrType=" + rdoPrtrType.Value;    	//바로 출력

	open("common::comVrtRptViewer.xml", sArg, nWidth, nHeight, "AutoSize=false Resize=true", nPosX, nPosY);

    return;
}




function rdoPrtrType_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	SetReg("7ExPrtrType", rdoPrtrType.value);	
}
