/*-------------------------------------------------------------------------------------------------+
>>  √Îº“ πˆ∆∞ ≈¨∏Ø „¡ √≥∏Æ
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsRaceInfo.ClearData();
    dsSales.ClearData();
    dsBranchSalesAggregate.ClearData();
    dsBeforeCompare.ClearData();
    dsDivGreenSalesAmt.ClearData();
    dsDivGreenSalesCnt.ClearData();

    fcCleartmpDs();  // «•Ω√ µ•¿Ã≈Õº¬ √ ±‚»≠
}



/*-------------------------------------------------------------------------------------------------+
>>  ¡∂»∏ πˆ∆∞ ≈¨∏Ø „¡ √≥∏Æ
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	var sServiceName = "rsm2010-service:searchMeetCd";  // Grid ¡∂»∏
	var sInDataSet   = "";
	var sOutDataSet  = "dsMeetCd=dsMeetCd";     


	fcd_AddParam("RACE_DAY", calRaceDay.Value);    
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  ¡∂»∏ πˆ∆∞ ≈¨∏Ø „¡ √º≈© √≥∏Æ
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //¡∂»∏¡∂∞« √º≈©π◊ trim√≥∏Æ

	fcInit(obj);    //±‚¡∏ µ•¿Ã≈Õº¬ √ ±‚»≠	
	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  ¡∂»∏ πˆ∆∞ ≈¨∏Ø „¡ √≥∏Æ
+-------------------------------------------------------------------------------------------------*/
function fcSearchInfo(obj) {

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2010-service:searchRaceInfo";  // Grid ¡∂»∏
	var sInDataSet   = "";
	var sOutDataSet  = "dsRaceInfo=dsRaceInfo ";     
		sOutDataSet  += "dsSales=dsSales ";     
		sOutDataSet  += "dsBranchSalesAggregate=dsBranchSalesAggregate ";     
		sOutDataSet  += "dsBeforeCompare=dsBeforeCompare ";     
		sOutDataSet  += "dsMaxRaceNo=dsMaxRaceNo ";
		sOutDataSet  += "dsSalesTotalDiv=dsSalesTotalDiv ";
		sOutDataSet  += "dsDivGreenSalesAmt=dsDivGreenSalesAmt ";
		sOutDataSet  += "dsDivGreenSalesCnt=dsDivGreenSalesCnt ";

	if( calRaceDay.Value == fcd_GetSysdate("YYYYMMDD"))
	{
		sServiceName = "rsm2010-service:searchRaceInfoToday";  // Grid ¡∂»∏
	}


	fcd_AddParam("RACE_DAY", calRaceDay.Value);        
	if (calRaceDay.Value == "20140808" || calRaceDay.Value == "20140809" || calRaceDay.Value == "20140810") {
		fcd_AddParam("MEET_CD",  "%");        
		stBrncInfo.text = "°ÿ ºº∞Ë¡÷¥œæÓº±ºˆ±«¥Î»∏±‚∞£ø°¥¬ ±≥¬˜∏≈√‚ø° ¥Î«— ¡ˆ¡°∫∞ ∏≈√‚æ◊¿‘¥œ¥Ÿ.";		
	} else if (calRaceDay.Value == "20160923" || calRaceDay.Value == "20160924" || calRaceDay.Value == "20160925") {
		fcd_AddParam("MEET_CD",  "%");        
		stBrncInfo.text = "°ÿ ∞Ê∑˚ »ﬁ¿Â±‚∞£ ±≥¬˜∏≈√‚ø° ¥Î«— ¡ˆ¡°∫∞ ∏≈√‚æ◊¿‘¥œ¥Ÿ.";		
	} 
	else {
		fcd_AddParam("MEET_CD",  rdoMeetCd.Value);        
		stBrncInfo.text = "";
	}
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



// ∞Ê∑È ±∏∫– ƒ⁄µÂ πŸ≤Ÿ∏È ∞Àªˆ
function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.ID ) {

		case "rdoMeetCd" : // ±‚¡ÿ≥‚µµ ∫Ø∞Ê
			fcSearchInfo();
			break;


		case "rdoAutoOPT" : // ºˆµø ¿⁄µø ±∏∫–
			if(strCode=="2")
			{
				cboTimeBox.Enable = true;
				SetTimer(1, parseInt(cboTimeBox.Value) *60 * 1000 ); // Timer±∏µø
			}
			else
			{
				cboTimeBox.Enable = false;
				KillTimer(1); // Timer«ÿ¡¶
			}

			break;

		case "cboTimeBox":
			KillTimer(1); // Timer«ÿ¡¶
			SetTimer(1, parseInt(strCode) *60 * 1000 ); // Timer±∏µø	
			break;
	}


//	fcSearch();
}


