		sExcelList += "|grdGreenSalesCnt1:3";			
		sExcelList += "|grdGreenSalesCnt2:3"; 
//		sExcelList += "|grdBranchSalesAggregate2:2"; // :º˝¿⁄¥¬ + head +summary+ «œ¥‹ ∂ÁøÏ∞Ì ΩÕ¿∫ ªÁ¿Ã ∞£∞›
    fnc_OpenMultiExcelExport(sExcelList,"∏≈√‚«ˆ»≤");
}

