function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
    if(sServiceName = "rsm2010-service:searchMeetCd")
    {
		rdoMeetCd.Value = dsMeetCd.GetColumn(0,"CD");			
		if(rdoMeetCd.Value == "003")
		{
			grdSales.SetColProp(10,"width","0px");
			grdSales.SetColProp(11,"width","100px");
		}
		else
		{
			grdSales.SetColProp(10,"width","100px");
			grdSales.SetColProp(11,"width","0px");
		}
		if(dsMeetCd.rowcount > 0) // ∞Ê¡÷¿œ¿Ã ¿÷¿ª ∞ÊøÏ∏∏ ∞Àªˆ
		{
			fcSearchInfo();
		}
		else
		{
			fcInit();
		}
		return;
    }
    else if ( sServiceName == "rsm2010-service:searchRaceInfo" ||sServiceName == "rsm2010-service:searchRaceInfoToday" )
    {
		stRaceInfo.Text = "∏≈√‚«ˆ»≤ "+dsRaceInfo.GetColumn(0,"RACE_INFO");
		grdGreenSalesCnt1.Visible = "True";
		grdGreenSalesCnt2.Visible = "True";
		if(dsDivGreenSalesCnt.rowcount() == 0) {
			grdGreenSalesCnt1.Visible = "False";
			grdGreenSalesCnt2.Visible = "False";
		} else {
			grdGreenSalesCnt1.Visible = "True";
			grdGreenSalesCnt2.Visible = "True";
		}
		for(var i=2; i<=10; i++) {
			grdGreenSalesAmt1.SetCellProp("body",i,"font","expr:Decode(IDX, '1', '±º∏≤,9,Bold', '±º∏≤,9')");	
			grdGreenSalesAmt2.SetCellProp("body",i,"font","expr:Decode(IDX, '1', '±º∏≤,9,Bold', '±º∏≤,9')");	
		}	
		/*
		for(var i=0; i<dsDivGreenSalesAmt.rowcount(); i++) {
			grdGreenSalesAmt1.SetCellProp("body",0,"Expr","\"" + dsDivGreenSalesAmt.GetColumn(i,"GUBUN") + "\"");
			grdGreenSalesAmt2.SetCellProp("body",0,"Expr","\"" + dsDivGreenSalesAmt.GetColumn(i,"GUBUN") + "\"");
		}
		for(var i=0; i<dsDivGreenSalesCnt.rowcount(); i++) {
			grdGreenSalesCnt1.SetCellProp("body",0,"Expr","\"" + dsDivGreenSalesCnt.GetColumn(i,"GUBUN") + "\"");
			grdGreenSalesCnt2.SetCellProp("body",0,"Expr","\"" + dsDivGreenSalesCnt.GetColumn(i,"GUBUN") + "\"");

		}
		*/
		/*
		if(dsSales.GetRowCount() <=2){
			grdBranchSalesAggregate.SetCellProp("head",0,"text","¡ˆ¡°∫∞ «ˆ±› ∏≈√‚æ◊¥©∞Ë(∞Ê¡÷±Ó¡ˆ)");
			grdSaleGreenDiv.SetCellProp("head",0,"text","¡ˆ¡°∫∞ ±◊∏∞ƒ´µÂ ∏≈√‚æ◊¥©∞Ë(∞Ê¡÷±Ó¡ˆ)");
			grdSalesTotalDiv.SetCellProp("head",0,"text","¡ˆ¡°∫∞ √— ∏≈√‚æ◊¥©∞Ë(∞Ê¡÷±Ó¡ˆ)");
		}else{
			var nMaxRace =dsSales.GetColumn((dsSales.GetRowCount()-3),"RACE_NO");
		    var nMaxRow =(dsSales.GetRowCount()-3);

			if (sServiceName == "rsm2010-service:searchRaceInfoToday") {
				if (dsMaxRaceNo.rowcount() > 0) {
					nMaxRace = dsMaxRaceNo.GetColumn(0, "MAX_RACE_NO");
				}
			}

			if(dsSales.GetColumn(nMaxRow,"MEET_NM") == "±§∏Ì" || dsSales.GetColumn(nMaxRow,"MEET_NM") == "πÃªÁ∏Æ" ){				
				grdBranchSalesAggregate.SetCellProp("head",0,"text","¡ˆ¡°∫∞ «ˆ±› ∏≈√‚æ◊¥©∞Ë("+nMaxRace+"∞Ê¡÷±Ó¡ˆ)");
				grdSaleGreenDiv.SetCellProp("head",0,"text","¡ˆ¡°∫∞ ±◊∏∞ƒ´µÂ ∏≈√‚æ◊¥©∞Ë("+nMaxRace+"∞Ê¡÷±Ó¡ˆ)");
				grdSalesTotalDiv.SetCellProp("head",0,"text","¡ˆ¡°∫∞ √— ∏≈√‚æ◊¥©∞Ë("+nMaxRace+"∞Ê¡÷±Ó¡ˆ)");
			}else{
				grdBranchSalesAggregate.SetCellProp("head",0,"text","¡ˆ¡°∫∞ «ˆ±› ∏≈√‚æ◊¥©∞Ë(∞Ê¡÷±Ó¡ˆ)");
				grdSaleGreenDiv.SetCellProp("head",0,"text","¡ˆ¡°∫∞ ±◊∏∞ƒ´µÂ ∏≈√‚æ◊¥©∞Ë(∞Ê¡÷±Ó¡ˆ)");
				grdSalesTotalDiv.SetCellProp("head",0,"text","¡ˆ¡°∫∞ √— ∏≈√‚æ◊¥©∞Ë(∞Ê¡÷±Ó¡ˆ)");
			}
		}
		*/

		/*  ±∏º“Ω∫		
		var sShaNm = "πÃªÁ∏Æ";
		if (dsRaceInfo.GetColumn(0,"MEET_CD") == "003") sShaNm = "±§∏Ì";

		grdSaleGreenDiv.SetCellProp("head", 8, "text", sShaNm);

		if (dsSaleGreenDiv.rowcount() > 0) {
			if (dsSaleGreenDiv.GetColumn(0, "SHA_CAT") > 0) {
				grdSaleGreenDiv.SetColProp(7,"Width", 120);
			} else {
				grdSaleGreenDiv.SetColProp(7, "Width", 0);
			}
			if (dsSaleGreenDiv.GetColumn(0, "OLM_CAT") > 0) {
				grdSaleGreenDiv.SetColProp(3,"Width", 120);
			} else {
				grdSaleGreenDiv.SetColProp(3, "Width", 0);
			}			
		} else {
			grdSaleGreenDiv.SetColProp(7, "Width", 0);
			grdSaleGreenDiv.SetColProp(3, "Width", 0);
		}
		*/		


		//grdSaleGreenDiv.AutoFit = true;

		fcGridPosition();
		return;

	}

}



/*-------------------------------------------------------------------------------------------------+
>>  Transaction »ƒ Post Process!
+-------------------------------------------------------------------------------------------------*/

function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{	
	switch ( obj.ID ) {		
		// ∞Ê¡÷¿œ¿⁄ø°º≠ ENTER_KEY ¥≠∑∂¿ª ∞ÊøÏ
		case "calRaceDay" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;

	}
}



/*-------------------------------------------------------------------------------------------------+
>>  2¬˜ø¯ πËø≠¿« ∫Øºˆ ∞™ »Æ¿Œ
+-------------------------------------------------------------------------------------------------*/


function fcArray2Value(arr2Param,strParam)
{
	var strResult = "";
	for(var i=0;i<arr2Param.length;i++)
	{	
		for(var j=0;j<arr2Param[i].length;j++)
		{
			if(arr2Param[i][0]==strParam)
			{
				strResult = arr2Param[i][1];
			}
		}
	}
	return strResult;
}


/*-------------------------------------------------------------------------------------------------+
>>  πˆ∆∞ ≈¨∏Ø ¿Ã∫•∆Æ
+-------------------------------------------------------------------------------------------------*/

function fcBtnOnClick(obj,nX,nY)
{
	switch ( obj.id )
    {
		case "btnFullScreen" :
			var sFormId 	= "frm_rsm::RSM2010P.xml";
			var sArgs 		= "";		
			var nCXScreen	= GetDeviceInfo("CXScreen");			
			var nCYScreen	= GetDeviceInfo("CYScreen");

			var strParam = "RACE_DAY="+calRaceDay.Value;
			strParam +=" MEET_CD="+rdoMeetCd.Value;
			GBL_RET_VALUE=strParam; // ∞™ ª˝º∫	

			Dialog(sFormId, GBL_RET_VALUE,nCXScreen,nCYScreen, "Title=true CloseFlag=true","1","1");
		break;

		case "btnMonitoring" :

			//if(gdsUser.GetColumn(0,"USER_ID") =="05071") {
				var sFormId 	= "frm_rsm::RSM2100.xml";


				var strParam = "RACE_DAY="+calRaceDay.Value;
				strParam +=" MEET_CD="+rdoMeetCd.Value;			

				GBL_RET_VALUE=strParam; // ∞™ ª˝º∫	

				var nCXScreen	= GetDeviceInfo("CXScreen");			
				var nCYScreen	= GetDeviceInfo("CYScreen");

				//Dialog(sFormId, GBL_RET_VALUE,nCXScreen,nCYScreen, "Title=true CloseFlag=true","1","1");
				Open(sFormId, GBL_RET_VALUE,nCXScreen,nCYScreen, "Title=true CloseFlag=true","1","1");
			//}
		break;		

		case "btnGraph" :

			/*	
			Miplatform »≠∏È¿ª »£√‚«ÿº≠ ±‚¥… ±∏«ˆ
			var sFormId 	= "frm_rsm::RSM2101.xml";

			var strParam = "RACE_DAY="+calRaceDay.Value;
			strParam +=" MEET_CD="+rdoMeetCd.Value;			

			GBL_RET_VALUE=strParam; // ∞™ ª˝º∫	

			var nCXScreen	= GetDeviceInfo("CXScreen");			
			var nCYScreen	= GetDeviceInfo("CYScreen");

			Open(sFormId, GBL_RET_VALUE, nCXScreen, nCYScreen, "Title=true CloseFlag=true","1","1");
			*/
			WB.PageUrl =GBL_SERVER_URL+ "/webpage/kspochart/openChart.jsp?RACE_DAY="+calRaceDay.Value+"&MEET_CD="+rdoMeetCd.Value+"&SERVICE=rsm2100";
			WB.Run();

		break;	
	}	
}

