// ∆Ø¡§ƒﬁ∆˜≥Õ∆Æ(±◊∏ÆµÂ) ∏ÆªÁ¿Ã¡Ó
function fcSetResize(obj,nX)
{	// «ÿ¥Á»≠∏È¿∫ ±‚¡ÿªÁ¿Ã¡Ó∫∏¥Ÿ Height∞° ≈©±‚∂ßπÆø° Ω∫≈©∑—πŸ ª˝º∫πﬂª˝.±◊ø° µ˚∂Û ±◊∏ÆµÂªÁ¿Ã¡Ó ¡∂¡§

	/*
	var arrObj = array(-1);
	arrObj[0] = "grdSales";
	arrObj[1] = "grdBranchSalesAggregate";
	arrObj[2] = "grdBranchSalesAggregate2";
	arrObj[3] = "grdBeforeCompare";
	arrObj[4] = "grdSaleGreenDiv";
	*/
	var nWidth = nX-28;	

	if( nX <= 830){
		/*
		for( var i=0; i<length(arrObj); i++){
			object(arrObj[i]).Autofit = false;		
			object(arrObj[i]).right = 788;			
		}
		*/
		grdSales.AutoFit = true;
		grdSales.right = 800;

//		grdBranchSalesAggregate.AutoFit = false;
//		grdBranchSalesAggregate.right 	= 800;

//		grdBranchSalesAggregate2.AutoFit = false;
//		grdBranchSalesAggregate2.right 	 = 788;

		grdBeforeCompare.AutoFit	= false;
		grdGreenSalesAmt1.AutoFit	= false;
		grdGreenSalesAmt2.AutoFit	= false;
		grdGreenSalesCnt1.AutoFit	= false;
		grdGreenSalesCnt2.AutoFit	= false;

		grdBeforeCompare.Right		= 800;
		grdGreenSalesAmt1.Right		= 800;
		grdGreenSalesAmt2.Right 	= 800;
		grdGreenSalesCnt1.Right		= 800;
		grdGreenSalesCnt2.Right 	= 800;

		Shape1.Width = 800;	
		divBtnLst.Left = this.width - 405;			
		Static0.Left = this.width - 111;


		if(rdoMeetCd.Value == "003") {
			grdSales.SetColProp(10,"width","0px");
			grdSales.SetColProp(11,"width","100px");
		} else {
			grdSales.SetColProp(10,"width","100px");
			grdSales.SetColProp(11,"width","0px");
		}
		grdGreenSalesAmt1.AutoFit = true;
		grdGreenSalesAmt2.AutoFit = true;
		grdGreenSalesCnt1.AutoFit = true;
		grdGreenSalesCnt2.AutoFit = true;

		/*
		if (ToNumber(dsBranchSalesAggregate.GetColumn(0,"COMM_NO_99")) > 0) {
			grdBranchSalesAggregate.SetColProp(9,"width",100);
			grdSalesTotalDiv.SetColProp(9,"width",100);
		} else {
			grdBranchSalesAggregate.SetColProp(9,"width",0);
			grdSalesTotalDiv.SetColProp(9,"width",0);
		}
		*/
		return;

	}else{

		grdSales.right = nWidth;

		if( nX < 1200) grdSales.AutoFit = true;
		else grdSales.AutoFit = true;

		grdGreenSalesAmt1.right = nWidth;
		grdGreenSalesAmt2.right = nWidth;
		grdGreenSalesCnt1.right = nWidth;
		grdGreenSalesCnt2.right = nWidth;

		if( nX < 831) {
			grdGreenSalesAmt1.AutoFit = false;
			grdGreenSalesAmt2.AutoFit = false;
			grdGreenSalesCnt1.AutoFit = false;
			grdGreenSalesCnt2.AutoFit = false;
		} else {
			grdGreenSalesAmt1.AutoFit = true;
			grdGreenSalesAmt2.AutoFit = true;
			grdGreenSalesCnt1.AutoFit = true;
			grdGreenSalesCnt2.AutoFit = true;
			/*
			if (ToNumber(dsBranchSalesAggregate.GetColumn(0,"COMM_NO_99")) > 0) {
				grdBranchSalesAggregate.SetColProp(9,"width",100);
				grdSalesTotalDiv.SetColProp(9,"width",100);
			} else {
				grdBranchSalesAggregate.SetColProp(9,"width",0);
				grdSalesTotalDiv.SetColProp(9,"width",0);
			}
			*/

		}

		grdBeforeCompare.Right	= nWidth;

		if( nX < 831) grdBeforeCompare.AutoFit	= false;
		else grdBeforeCompare.AutoFit	= true;
	}	


	// ∞¯≈Î
	Shape1.Width = nWidth-8;
	divBtnLst.Left = this.width - 405;	
	Static0.Left = this.width - 111;
}

// «ÿ¥Á ∆˚ªÁ¿Ã¡Ó ∫Ø∞ÊΩ√
function fcOnSize(obj,nCx,nCy,nState)
