	fcSetResize(obj,nCx);
}



function fcNextCommNo(colNm)
{	
	var retVal = "";
	if (fnc_isNull(colNm)) return retVal;

	retVal = "COMM_NO_"+ toString(toNumber(substr(colNm, 8,2))+1);
	return retVal;
}


function fcOnHeadClick(obj, nCell, nX, nY)
{
	trace("###########nCell:" +nCell);
	var COMM_NO   = "";
	var sBRNCH_NM = "";
	var TMS       = dsRaceInfo.GetColumn(0,"TMS");
	var DAY_ORD   = dsRaceInfo.GetColumn(0,"DAY_ORD");
	var RACE_YOIL = dsRaceInfo.GetColumn(0,"RACE_YOIL");
	switch ( obj.ID ) {	
		case "grdGreenSalesAmt1" :
			if(nCell%2 == 0) //«ˆ±›
			{
				var arrCell     = [ 12, 14, 16, 18, 20, 22, 24, 26, 28];
				var arrCellText = ["ºˆø¯","¡ﬂ∂˚","¿œªÍ","∫–¥Á","µø¥ÎπÆ","¿Âæ»","ªÍ∫ª","∫Œ√µ","∞¸æ«"];
				var arrCommNO 	= [11,12,13,14,15,16,17,18,19];
				var sSellCd     = Mid(dsRaceInfo.GetColumn(0, "MEET_CD"),1,2);

				for(var i=0;i<arrCellText.length;i++)
				{
					if(arrCell[i]==nCell)
					{				

						COMM_NO = arrCommNO[i]+"";
						sBRNCH_NM = arrCellText[i];
					}
				}

				var strParam = "RACE_DAY="+calRaceDay.Value;
				strParam +=" MEET_CD="+rdoMeetCd.Value;
				strParam +=" COMM_NO="+COMM_NO;
				strParam +=" BRNCH_NM="+sBRNCH_NM;
				strParam +=" SELL_CD="+sSellCd;

				strParam +=" TMS="+TMS;
				strParam +=" DAY_ORD="+DAY_ORD;	
				strParam +=" RACE_YOIL=" + RACE_YOIL;		

				GBL_RET_VALUE=strParam; // ∞™ ª˝º∫	

				global.FrameBottom.fcOpenMdiForm("RSM2011", "∏≈√‚æ◊∫Ò±≥", "frm_rsm::RSM2011.xml");			
			}	

			if(nCell%2 == 1) //±◊∏∞ƒ´µÂ
			{
				var sCellText = "";
				var arrCell     = [ 13, 15, 17, 19, 21, 23, 25, 27, 29];
				var arrGreenCellText = ["ºˆø¯","¡ﬂ∂˚","¿œªÍ","∫–¥Á","µø¥ÎπÆ","¿Âæ»","ªÍ∫ª","∫Œ√µ","∞¸æ«"];
				var arrGreenCellDesc = ["±◊∏∞ƒ´µÂ(ºˆø¯)","±◊∏∞ƒ´µÂ(¡ﬂ∂˚)","±◊∏∞ƒ´µÂ(¿œªÍ)",
										"±◊∏∞ƒ´µÂ(∫–¥Á)","±◊∏∞ƒ´µÂ(µø¥ÎπÆ)","±◊∏∞ƒ´µÂ(¿Âæ»)",
										"±◊∏∞ƒ´µÂ(ªÍ∫ª)","±◊∏∞ƒ´µÂ(∫Œ√µ)","±◊∏∞ƒ´µÂ(∞¸æ«)",
										];

				var arrGreenDivNO 	= ['01','02','03','05','04','06','07','08','09'];
				var sSellCd = Mid(dsRaceInfo.GetColumn(0, "MEET_CD"),1,2);

				var nCellCount = 0;

				// ±◊∏∞ƒ´µÂ ∞™¿œ ∂ß √º≈©
				var DIV_NO="";
				var nGreenCellCount = 0;
				for(var i=0;i<arrGreenCellText.length;i++)
				{
					if(arrCell[i]==nCell)
					{	
						nGreenCellCount ++;
						DIV_NO = arrGreenDivNO[i];
						sBRNCH_NM = arrGreenCellDesc[i];
						sCellText = arrGreenCellText[i];
					}
				}

				if (sCellText == "∫ª¿Â") {
					nGreenCellCount ++;
					DIV_NO = "00";
					sBRNCH_NM = "±◊∏∞ƒ´µÂ("+sCellText+")";
					if (sSellCd == "01") sSellCd = "03";
					else                 sSellCd = "01";  
				}


				// ±◊∏∞ƒ´µÂ º±≈√ «ﬂ¿ª ∂ß ¿Ãµø
				if(nGreenCellCount > 0)
				{
					var strParam = "RACE_DAY="+calRaceDay.Value;
					strParam +=" MEET_CD="+rdoMeetCd.Value;
					strParam +=" COMM_NO=06";
					strParam +=" DIV_NO="+DIV_NO;
					strParam +=" BRNCH_NM="+sBRNCH_NM;
					strParam +=" TMS="+TMS;
					strParam +=" DAY_ORD="+DAY_ORD;
					strParam +=" SELL_CD="+sSellCd;

					GBL_RET_VALUE=strParam; // ∞™ ª˝º∫	

					global.FrameBottom.fcOpenMdiForm("RSM2011", "∏≈√‚æ◊∫Ò±≥", "frm_rsm::RSM2011.xml");
					//Trace("strParam="+strParam);
				}		
			}
		break;
		case "grdGreenSalesAmt2" :
		//alert(nCell);
		//break;
		if(nCell%2 == 1) //«ˆ±›
			{
				var arrCell     = [ 11, 13, 15, 17, 19, 21, 23, 25, 27];
				var arrCellText = ["º∫∫œ","øµµÓ∆˜","¥Î¿¸","¿Œ√µ","Ω√»Ô","∞≠≥≤","√µæ»","¿«¡§∫Œ","∫ª¿Â"];
				var sSellCd     = Mid(dsRaceInfo.GetColumn(0, "MEET_CD"),1,2);

				var arrCommNO 	= "";
				if(dsRaceInfo.GetColumn(0, "MEET_CD") == "003") { //∞Ê¡§¿œ, πÃªÁ∏Æ
				  arrCommNO 	= ['20','21','22','23','24','25','26','28','03'];
				} else { //∞Ê∑˚¿œ, ±§∏Ì∫ª¿Â
				  arrCommNO 	= ['20','21','22','23','24','25','26','28','01'];
				}

				for(var i=0;i<arrCellText.length;i++)
				{
					if(arrCell[i]==nCell)
					{				

						COMM_NO = arrCommNO[i]+"";
						sBRNCH_NM = arrCellText[i];
					}
				}

				var strParam = "RACE_DAY="+calRaceDay.Value;
				strParam +=" MEET_CD="+rdoMeetCd.Value;
				strParam +=" COMM_NO="+COMM_NO;
				strParam +=" BRNCH_NM="+sBRNCH_NM;
				strParam +=" SELL_CD="+sSellCd;

				strParam +=" TMS="+TMS;
				strParam +=" DAY_ORD="+DAY_ORD;	
				strParam +=" RACE_YOIL=" + RACE_YOIL;		

				GBL_RET_VALUE=strParam; // ∞™ ª˝º∫	

				global.FrameBottom.fcOpenMdiForm("RSM2011", "∏≈√‚æ◊∫Ò±≥", "frm_rsm::RSM2011.xml");		
			}	

			if(nCell%2 == 0) //±◊∏∞ƒ´µÂ
			{
				var sCellText = "";
				var arrCell     = [ 12, 14, 16, 18, 20, 22, 24, 26, 28];
				var arrGreenCellText = ["ºˆø¯","¡ﬂ∂˚","¿œªÍ","∫–¥Á","µø¥ÎπÆ","¿Âæ»","ªÍ∫ª","∫Œ√µ","∞¸æ«"];
				var arrGreenCellDesc = ["±◊∏∞ƒ´µÂ(º∫∫œ)","±◊∏∞ƒ´µÂ(øµµÓ∆˜)","±◊∏∞ƒ´µÂ(¥Î¿¸)",
										"±◊∏∞ƒ´µÂ(¿Œ√µ)","±◊∏∞ƒ´µÂ(Ω√»Ô)","±◊∏∞ƒ´µÂ(∞≠≥≤)","±◊∏∞ƒ´µÂ(√µæ»)","±◊∏∞ƒ´µÂ(¿«¡§∫Œ)","±◊∏∞ƒ´µÂ(∫ª¿Â)"];

				var arrGreenDivNO 	= ['10','11','12','13','14','15','16','18','00'];
				var sSellCd = Mid(dsRaceInfo.GetColumn(0, "MEET_CD"),1,2);

				var nCellCount = 0;

				// ±◊∏∞ƒ´µÂ ∞™¿œ ∂ß √º≈©
				var DIV_NO="";
				var nGreenCellCount = 0;
				for(var i=0;i<arrGreenCellText.length;i++)
				{
					if(arrCell[i]==nCell)
					{	
						nGreenCellCount ++;
						DIV_NO = arrGreenDivNO[i];
						sBRNCH_NM = arrGreenCellDesc[i];
						sCellText = arrGreenCellText[i];
					}
				}

				if (sCellText == "∫ª¿Â") {
					nGreenCellCount ++;
					DIV_NO = "00";
					sBRNCH_NM = "±◊∏∞ƒ´µÂ("+sCellText+")";
					if (sSellCd == "01") sSellCd = "03";
					else                 sSellCd = "01";  
				}


				// ±◊∏∞ƒ´µÂ º±≈√ «ﬂ¿ª ∂ß ¿Ãµø
				if(nGreenCellCount > 0)
				{
					var strParam = "RACE_DAY="+calRaceDay.Value;
					strParam +=" MEET_CD="+rdoMeetCd.Value;
					strParam +=" COMM_NO=06";
					strParam +=" DIV_NO="+DIV_NO;
					strParam +=" BRNCH_NM="+sBRNCH_NM;
					strParam +=" TMS="+TMS;
					strParam +=" DAY_ORD="+DAY_ORD;
					strParam +=" SELL_CD="+sSellCd;

					GBL_RET_VALUE=strParam; // ∞™ ª˝º∫	

					global.FrameBottom.fcOpenMdiForm("RSM2011", "∏≈√‚æ◊∫Ò±≥", "frm_rsm::RSM2011.xml");
				}		
			}
		break;		

		case "grdBeforeCompare" :	
			var sCellText = grdBeforeCompare.GetCellText("HEAD",1,nCell);

			if(sCellText=='¿¸¡÷¥Î∫Ò')
			{
				var strParam = "RACE_DAY="+calRaceDay.Value;
				strParam +=" MEET_CD="+rdoMeetCd.Value;
				strParam +=" COMM_NO=00";
				strParam +=" BRNCH_NM=¿¸√º";

				strParam +=" TMS="+TMS;
				strParam +=" DAY_ORD="+DAY_ORD;			

				GBL_RET_VALUE=strParam; // ∞™ ª˝º∫	
				global.FrameBottom.fcOpenMdiForm("RSM2011", "∏≈√‚æ◊∫Ò±≥", "frm_rsm::RSM2011.xml");
			}

		break;

		case "grdSales" :	

			var sCellText = grdSales.GetCellText("HEAD",1,nCell);
			var arrCellText = ["±§∏Ì","πÃªÁ∏Æ","±◊∏∞ƒ´µÂ"];
			var arrCellDesc = ["±§∏Ì∫ª¿Â","πÃªÁ∏Æ∫ª¿Â","±◊∏∞ƒ´µÂ"];
			var arrCommNO 	= ["01","03","06"];

			var arrGreenCellText = ["∫ª¿Â","∞≠≥≤","ø√∏≤«»","µø¥ÎπÆ","ªÍ∫ª","¿«¡§∫Œ"];
			var arrGreenCellDesc = ["±◊∏∞ƒ´µÂ(∫ª¿Â)","±◊∏∞ƒ´µÂ(∞≠≥≤)","±◊∏∞ƒ´µÂ(ø√∏≤«»)","±◊∏∞ƒ´µÂ(µø¥ÎπÆ)","±◊∏∞ƒ´µÂ(ªÍ∫ª)","±◊∏∞ƒ´µÂ(¿«¡§∫Œ)"];
			var arrGreenDivNO 	= ["00","15","17","04","07","18"];

			var nCellCount = 0;

			// ±§∏Ì πÃªÁ∏Æ ±◊∏∞ƒ´µÂ ∞™¿œ ∂ß √º≈©
			for(var i=0;i<arrCellText.length;i++)
			{
				if(arrCellText[i]==sCellText)
				{	
					COMM_NO = arrCommNO[i]+"";
					sBRNCH_NM = arrCellDesc[i];
					nCellCount++;
				}
			}


			// ±◊∏∞ƒ´µÂ ∞™¿œ ∂ß √º≈©
			var DIV_NO="";
			var nGreenCellCount = 0;
			for(var i=0;i<arrGreenCellText.length;i++)
			{
				if(arrGreenCellText[i]==sCellText)
				{	
					nGreenCellCount ++;
					DIV_NO = arrGreenDivNO[i];
					sBRNCH_NM = arrGreenCellDesc[i];
				}
			}

			// ±§∏Ì, πÃªÁ∏Æ, ±◊∏∞ƒ´µÂø° «ÿ¥Á«“ ∂ß∏∏ ¿Ãµø«—¥Ÿ.			
			if(nCellCount>0) {	

				var strParam = "RACE_DAY="+calRaceDay.Value;
				strParam +=" MEET_CD="+rdoMeetCd.Value;
				strParam +=" COMM_NO="+COMM_NO;
				strParam +=" BRNCH_NM="+sBRNCH_NM;
				strParam +=" TMS="+TMS;
				strParam +=" DAY_ORD="+DAY_ORD;				
				strParam +=" RACE_YOIL=" + RACE_YOIL;

				GBL_RET_VALUE=strParam; // ∞™ ª˝º∫					

				global.FrameBottom.fcOpenMdiForm("RSM2011", "∏≈√‚æ◊∫Ò±≥", "frm_rsm::RSM2011.xml");

			} else if(nGreenCellCount > 0 && calRaceDay.Value <> today()) {	// ±◊∏∞ƒ´µÂ¥¬ ¥Á¿œ¿⁄∑·∞° æ¯¿Ω

				var strParam = "RACE_DAY="+calRaceDay.Value;
				strParam +=" MEET_CD="+rdoMeetCd.Value;
				strParam +=" COMM_NO=06";
				strParam +=" DIV_NO="+DIV_NO;
				strParam +=" BRNCH_NM="+sBRNCH_NM;
				strParam +=" TMS="+TMS;
				strParam +=" DAY_ORD="+DAY_ORD;
				strParam +=" RACE_YOIL=" + RACE_YOIL;

				GBL_RET_VALUE=strParam; // ∞™ ª˝º∫	

				global.FrameBottom.fcOpenMdiForm("RSM2011", "∏≈√‚æ◊∫Ò±≥", "frm_rsm::RSM2011.xml");
			}

		break;
	}
}

