/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

var ARRAY2_GBL_RET_VALUE = Array();	// 이전페이지에서 넘어온 변수
var sRetTms = "";
var sRetDayOrd = "";
function fcFormLoadSetting(obj) {  

	// 화면 컴포넌트 크기 초기화
	var nCXScreen	= GetDeviceInfo("CXScreen");			
	var nCYScreen	= GetDeviceInfo("CYScreen");

	RSM2020P.MoveWindow(0,0,nCXScreen,nCYScreen);
	divBtnLst.Left = nCXScreen-120;
	divFullScreen.Width = nCXScreen;
	divFullScreen.Height = nCYScreen-100;
	divFullScreen.stUnit.Left= nCXScreen-100;
	divFullScreen.stUnit1.Left= nCXScreen-100;
	divFullScreen.stUnit3.Left= nCXScreen-100;
	divFullScreen.stUnit4.Left= nCXScreen-100;


	divFullScreen.grdBFYYCompare.Width	= nCXScreen-30;
	divFullScreen.grdRaceSales.Width= nCXScreen-30;
	divFullScreen.grdEntPrsnNum.Width= nCXScreen-30;
	divFullScreen.grdStayMana2.Width= nCXScreen-30;
	divFullScreen.grdOutSales.Width= nCXScreen-30;
	divFullScreen.grdOutEntPrsnNum.Width= nCXScreen-30;
	divFullScreen.taEtc.Width= nCXScreen-30;

	dsBFYYCompare.ClearData();
    dsRaceSales.ClearData();
    dsEntPrsnNum.ClearData();
    dsStayMana2.ClearData();
    dsOutSales.ClearData();
    dsOutEntPrsnNum.ClearData();
    dsETC.ClearData();


	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	fcd_SetStndYear(dsStndYear,  dsCond.GetColumn(0,"MIN_STND_YEAR"), substr(fcd_GetSysdate("YYYYMMDD"), 0, 4)); 


	// 이전 페이지에서 파라미터 받음	
	var sRetValue = decode(GBL_RET_VALUE,null,'',GBL_RET_VALUE);
	if(sRetValue!='')
	{		
		// 이전 페이지에서 파라미터 받음	
		ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE," ","=");
		GBL_RET_VALUE="";
		cboStndYear.Value  = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"STND_YEAR");
		sRetTms				= fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"TMS");
		sRetDayOrd			= fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"DAY_ORD");
		fcSearchTms();
	}

}

