function fcInit(obj) {
    dsBFYYCompare.ClearData();
    dsRaceSales.ClearData();
    dsEntPrsnNum.ClearData();
    dsStayMana2.ClearData();
    dsOutSales.ClearData();
    dsOutEntPrsnNum.ClearData();
    dsETC.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리

	fcInit(obj);    //기존 데이터셋 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  경정 년도별 가까운 회차
+-------------------------------------------------------------------------------------------------*/
function fcSearchTms(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2020-service:searchTms"; // 최근 경주 결과 날짜
    var sInDataSet   = "";
    var sOutDataSet  = "dsTms=dsTms ";

	dsTms.ClearData();
	fcd_AddParam("MEET_CD",   "003");
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
>>  경정 년도별 가까운 회차별 일차
+-------------------------------------------------------------------------------------------------*/
function fcSearchDayOrd(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2020-service:searchDayOrd"; // 최근 경주 결과 날짜

    var sInDataSet   = "";
    var sOutDataSet  = "dsDayOrd=dsDayOrd ";

	dsDayOrd.ClearData();
	fcd_AddParam("MEET_CD",   "003");
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("TMS", cboTms.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2020-service:searchRaceDayInfo"; // 최근 경주 결과 날짜


    var sInDataSet   = "";
    var sOutDataSet  = "dsRaceDayInfo=dsRaceDayInfo ";    	// 전년대비 비교 ds
		sOutDataSet  += "dsBFYYCompare=dsBFYYCompare ";    	// 전년대비 비교 ds
		sOutDataSet  += "dsRaceSales=dsRaceSales ";    	// 경주별 매출현황
		sOutDataSet  += "dsEntPrsnNum=dsEntPrsnNum ";    	// 입장인원 1 ds
		sOutDataSet  += "dsStayMana2=dsStayMana2 ";    	// 입장인원 2 ds
		sOutDataSet  += "dsOutSales=dsOutSales ";    	// 장외 매출 현황 비교 ds
		sOutDataSet  += "dsOutBFSales=dsOutBFSales ";    	// 장외 전회 매출 현황 비교 ds
		sOutDataSet  += "dsOutEntPrsnNum=dsOutEntPrsnNum ";    	// 장외 입장 인원 현황 ds
		sOutDataSet  += "dsETC=dsETC ";    	// 기타 특이사항 ds

	var STND_YEAR  = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"STND_YEAR");
	var TMS  = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"TMS");
	var DAY_ORD  = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"DAY_ORD");
	var RACE_DAY	= fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"RACE_DAY"); // 경주일

	if(RACE_DAY == fcd_GetSysdate("YYYYMMDD"))
	{
		sServiceName = "rsm2020-service:searchRaceDayInfoToday"; // 최근 경주 결과 날짜
	}


	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("MEET_CD", "003");
	fcd_AddParam("TMS", cboTms.Value);
	fcd_AddParam("DAY_ORD", cboDayOrd.Value);	


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}





