// 자동 검색 시간 옵션
function fcOnTimer(obj,nEventID)
{
	if(rdoAutoOPT.Value=="2")
	{
		fcSearch();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/
function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	switch ( obj.ID ) {
		case "cboStndYear" : // 기준년도 변경
			fcSearchTms();
			break;

		case "cboTms" :		// 회차 변경
			fcSearchDayOrd();		
			break;

		case "cboDayOrd" :	// 일차 변경
			fcSearch();
			break;

		case "rdoAutoOPT" : // 수동 자동 구분
			if(strCode=="2")
			{
				cboTimeBox.Enable = true;
				SetTimer(1, parseInt(cboTimeBox.Value) *60 * 1000 ); // Timer구동
			}
			else
			{
				cboTimeBox.Enable = false;
				KillTimer(1); // Timer해제
			}

			break;

		case "cboTimeBox":
			KillTimer(1); // Timer해제
			SetTimer(1, parseInt(strCode) *60 * 1000 ); // Timer구동	
			break;



		case "rdoAutoOPT" : // 수동 자동 구분
			if(strCode=="2")
			{
				cboTimeBox.Enable = true;
				SetTimer(1, parseInt(cboTimeBox.Value) *60 * 1000 ); // Timer구동
			}
			else
			{
				cboTimeBox.Enable = false;
				KillTimer(1); // Timer해제
			}

			break;

		case "cboTimeBox":
			KillTimer(1); // Timer해제
			SetTimer(1, parseInt(strCode) *60 * 1000 ); // Timer구동	
			break;
	}
}



function fcOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	
	switch ( obj.id )
    {
		case "grdRaceSales" :

			if(nCell=='0')
			{
				var RACE_DAY = replace(dsRaceDayInfo.GetColumn(0,"RACE_DAY"),"-");

				var sFormId 	= "frm_rsm::RSM2031P.xml";
				var sArgs 		= "";		

				var sParam = " MEET_CD="+dsRaceDayInfo.GetColumn(0,"MEET_CD");
				sParam += " STND_YEAR="+cboStndYear.Value;
				sParam += " TMS="+cboTms.Value;
				sParam += " DAY_ORD="+cboDayOrd.Value;
				sParam += " RACE_DAY="+RACE_DAY;
				sParam += " RACE_NO="+dsOutSales.GetColumn(nRow,"RACE_NO");

				GBL_RET_VALUE=sParam; // 값 생성	

				Dialog(sFormId, GBL_RET_VALUE,704,344, "Title=true CloseFlag=true","1","1");
			}
		break;
	}

}
