/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
    if ( sServiceName == "rsm2020-service:searchTms" ) { // TMS °ª °Ë»ö

		if(dsTms.GetRowCount()==0)
		{		
			dsTms.SetColumn(0,"TMS","");
			dsTms.SetColumn(0,"TMS_DESC","¾øÀ½");
			cboTms.Index=0;
		}
		else
		{
			cboTms.Value = dsTms.GetColumn(0,"MAX_TMS");		
			if(sRetTms!='')
			{
				cboTms.Value = sRetTms;
				sRetTms="";
			}
		}
		fcSearchDayOrd();
		return;
	}
    if ( sServiceName == "rsm2020-service:searchDayOrd" ) {		

		if(dsDayOrd.GetRowCount()==0)
		{
			dsDayOrd.AddRow();
			dsDayOrd.SetColumn(0,"DAY_ORD","");
			dsDayOrd.SetColumn(0,"DAY_DESC","¾øÀ½");
			cboDayOrd.Index=0;
		}
		else
		{
			cboDayOrd.Value = dsDayOrd.GetColumn(0,"MAX_DAY_ORD");
			if(sRetDayOrd!='')
			{
				cboDayOrd.Value	= sRetDayOrd;
				sRetDayOrd		= "";
			}
			RACE_DAY		= dsDayOrd.GetColumn(0,"RACE_DAY"); // °æÁÖÀÏ
		}
		fcSearch();
		return;
	}

	if(sServiceName == "rsm2020-service:searchBFYYCompare" )
	{

	}

	divFullScreen.grdBFYYCompare.SetFocus();

	divFullScreen.stRaceDayInfo.Text = "¸ÅÃâÇöÈ² "+dsRaceDayInfo.GetColumn(0,"RACE_DAY_INFO");


	// ÀüÈ¸ ¸ÅÃâ ÇöÈ² summary µ¥ÀÌÅÍ »ý¼º
	fcGrdOutSaleSummary();	

}

