/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsTMS.ClearData();
    dsRaceYoil.ClearData();
    calRaceDaySt.Value = substr(fcd_GetSysdate("YYYYMMDD"),0,4)+"0101"; // 현재년도의 1월 1일
	calRaceDayEnd.Value	= fcd_GetSysdate("YYYYMMDD");						// 오늘이 기본값	
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리

	dsTMS.ClearData();
    dsRaceYoil.ClearData();

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm3040-service:searchTMS01";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTMS=dsTMS ";
		sOutDataSet += "dsRaceYoil=dsRaceYoil ";    

	fcd_AddParam("MEET_CD",       rdoMeetCd.Value);
	fcd_AddParam("RACE_DAY_ST",  calRaceDaySt.Value);
	fcd_AddParam("RACE_DAY_END", calRaceDayEnd.Value);
	fcd_AddParam("COMM_NO",      cboBrncCd.Value); // 지점코드
	fcd_AddParam("GREEN_YN",     chkIncGrnCd.Value);	// 그린카드 포함 여부

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


