/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if ( sServiceName == "common-service:searchCode" ) {
		filterBrncCd(rdoMeetCd.Value);
	}

	if ( sServiceName == "rsm3040-service:searchTMS01" ) {   
		var nMon = -1;  //월
		var nTue = -1;	// 화
		var nWed = -1;	// 수
		var nThu = -1;	// 목
		var nFri = -1;	// 금
		var nSat = -1;	// 토
		var nSun = -1;	// 일

		if( dsRaceYoil.rowcount > 0){
			nMon = dsRaceYoil.SearchRow("RACE_YOIL=='MON'");	// 월
			nTue = dsRaceYoil.SearchRow("RACE_YOIL=='TUE'");	// 화
			nWed = dsRaceYoil.SearchRow("RACE_YOIL=='WED'");	// 수
			nThu = dsRaceYoil.SearchRow("RACE_YOIL=='THU'");	// 목
			nFri = dsRaceYoil.SearchRow("RACE_YOIL=='FRI'");	// 금
			nSat = dsRaceYoil.SearchRow("RACE_YOIL=='SAT'");	// 토
			nSun = dsRaceYoil.SearchRow("RACE_YOIL=='SUN'");	// 일			
		}

//trace("#####cnt:"+dsRaceYoil.rowcount);					
//trace("#####day:"+nMon+"||"+nTue+"||"+nWed+"||"+nThu+"||"+nFri+"||"+nSat+"||"+nSun+"||");			
	    if( dsRaceYoil.rowcount == 2){

			// 수,목
			if( nWed > -1 && nThu > -1){			
				// 수 목				
				grdTMS.Format = "F2";
				grdTMS.BindDataset = "dsTms";
				return;
			}

	    }else if( dsRaceYoil.rowcount == 3){


			if( nFri > -1  && nSat > -1 && nSun > -1 ){

				// 금 토 일				
				grdTMS.Format = "F1";
				grdTMS.BindDataset = "dsTms";
				return;

			} else if( nTue > -1 && nWed > -1 && nThu > -1){

				// 화 수 목				
				grdTMS.Format = "F3";
				grdTMS.BindDataset = "dsTms";
				return;
			} else if( nWed > -1 && nThu > -1 && nFri > -1){

				// 화 수 금   
				grdTMS.Format = "F8";
				grdTMS.BindDataset = "dsTms";
				return;

			} else if( nWed > -1 && nThu > -1 && nSun){

				// 수 목 일
				grdTMS.Format = "F4";
				grdTMS.BindDataset = "dsTms";
				return;

			}
	    }else if( dsRaceYoil.rowcount == 4) {

			if( nTue > -1  && nWed > -1 && nThu > -1 && nSun){			
				//  화 수 목 일
				grdTMS.Format = "F5";
				grdTMS.BindDataset = "dsTms";
				return;
			}
			if( nMon > -1  && nFri > -1 && nSat > -1 && nSun){			
				//  월 금 토 일 
				grdTMS.Format = "F7";
				grdTMS.BindDataset = "dsTms";
				return;
			}
	    }else if( dsRaceYoil.rowcount == 5) {

			if( nWed > -1 && nThu > -1 && nFri > -1 && nSat > -1 && nSun > -1){			
				//  수 목 금 토 일
				grdTMS.Format = "F6";
				grdTMS.BindDataset = "dsTms";
				return;
			}
	    }
		// fnc_VisibleColumn(grdTMS,dsHeadYoil);	// 나타나는 요일 값만 보여줌
		// grdTMS.SetFocus();
		// return;
	}

}


/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/
function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "rdoMeetCd") filterBrncCd(rdoMeetCd.Value);
	else if(obj.id == "cboBrncCd") {
		if (cboBrncCd.Value == "00") chkIncGrnCd.Visible = false;
		else                         chkIncGrnCd.Visible = true;
	}
	fcSearch();
}

function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// 경주 검색 시작 일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDaySt" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;
		// 경주 검색 마지막 일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDayEnd" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;

	}
}



// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX,nY)
{	
	var arrObj = array(-1);
	arrObj[0] = "grdTMS";

	var nWidth = nX-8;
	if( nX <= 820){
		for( var i=0; i<length(arrObj); i++){
			object(arrObj[i]).Autofit = false;		
			object(arrObj[i]).width = 801;
			object(arrObj[i]).Redraw;
		}
		Shape1.Width 	= 801;	
		divBtnLst.Left 	= this.width - 393;			
		Static0.Left 	= this.width - 91;

	} else {

		for( var i=0; i<length(arrObj); i++){
			object(arrObj[i]).right = nWidth;
			object(arrObj[i]).Autofit = true;		
		}	

		// 공통
		Shape1.Width 	= nWidth-8;
		divBtnLst.Left 	= this.width - 393;	
		Static0.Left	= this.width - 91;
	}

	var nHeight = nY-96;
	if( nY <= 473){
		grdTMS.Height = 473;
	} else {
		grdTMS.Height = nHeight;
	}

}

function RSM3040_OnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy);
}

function filterBrncCd(sMeetCd)
{
	var filterStr = "";
	if (sMeetCd == "003") filterStr = "Substr(CD_TERM4,1,1)='1'";
	else                  filterStr = "Substr(CD_TERM4,0,1)='1'";
	dsBrncCd.Filter(filterStr);
}
