/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    // 메뉴리스트 조회 완료 후jsy0002-service:searchMenuGrpList
	if ( sServiceName == "rsm3060-service:searchDIVTMSCNT01" ) {     
		fcSetColVisible();
		if (tab0.TabIndex == 0)		tab0.tab1.grdDIVTMSCNT.SetFocus();
		else                        tab0.tab2.grdDIVTMSCNT.SetFocus();
	}


}

/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();	

	var headTitle = "미사리";
	var objgrd;

	if (strtext == "경정") 
		headTitle = "광명";		



	if (tab0.TabIndex == 0) {
		objgrd = tab0.tab1.grdDIVTMSCNT;			
		objgrd.SetCellProp("head", 21, "text", headTitle);
		tobjgrd.SetCellProp("head", 43, "text", headTitle);

	} else {
		objgrd = tab0.tab2.grdDIVTMSCNT;
		objgrd.SetCellProp("head", 26, "text", headTitle);
	}	
}

function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// 경주 검색 시작 일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDaySt" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;
		// 경주 검색 마지막 일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDayEnd" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;

	}
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX,nY)
{	
	var arrObj = array(-1);
	arrObj[0] = "grdDIVTMSCNT";

	var nWidth = nX-10;
	if( nX <= 820){

		tab0.Width = 809;
		tab0.tab1.grdDIVTMSCNT.Autofit = false;		
		tab0.tab1.grdDIVTMSCNT.width = 801;
		tab0.tab2.grdDIVTMSCNT.Autofit = false;		
		tab0.tab2.grdDIVTMSCNT.width = 801;

		Shape1.Width 	= 801;	
		divBtnLst.Left 	= this.width - 393;			
		stUnit.right 	= this.width - 10;

	} else {

		tab0.right =nWidth;
		tab0.tab1.grdDIVTMSCNT.right = nWidth -3;
		tab0.tab2.grdDIVTMSCNT.right = nWidth -3;
		if( nX > 2400 )	{
			tab0.tab1.grdDIVTMSCNT.Autofit = true;		
			tab0.tab2.grdDIVTMSCNT.Autofit = true;		
		}else{
			tab0.tab1.grdDIVTMSCNT.Autofit = false;	
			tab0.tab2.grdDIVTMSCNT.Autofit = false;	
		}

		// 공통
		Shape1.Width 	= nWidth-8;
		divBtnLst.Left 	= this.width - 393;	
		stUnit.right	= this.width - 10;
	}


	var nHeight = nY-77;
	if( nY <= 473){
		tab0.Height = 496;
		tab0.tab1.grdDIVTMSCNT.Height = 470;
		tab0.tab2.grdDIVTMSCNT.Height = 470;
	} else {
		tab0.Height = nHeight;
		tab0.tab1.grdDIVTMSCNT.Height = nHeight-26;
		tab0.tab2.grdDIVTMSCNT.Height = nHeight-26;
	}	
}

// 해당 폼사이즈 변경시
function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy);
}


function fcGetSummary(grdObj, nCell, sSetWidth)
{
	if (tab0.TabIndex == 1 && nCell > 21) return;

	var nColSum = 0;
	nColSum = grdObj.GetCellText("Summary", -1, nCell);
	if (sSetWidth == "Y") {
		if (nColSum == 0) grdObj.SetColProp(nCell, "width", 0);
		else              grdObj.SetColProp(nCell, "width", 60);
	}

}
function fcSetColVisible()
{
	var objGrd;
	objGrd.redraw = false;

	if (tab0.TabIndex == 0) {
		objGrd = tab0.tab1.grdDIVTMSCNT;	

		fcGetSummary(objGrd, 6, "Y");
		fcGetSummary(objGrd, 19, "Y");
		fcGetSummary(objGrd, 21, "Y");	
		fcGetSummary(objGrd, 24, "Y");
		fcGetSummary(objGrd, 28, "Y");
		if (rdoMeetCd.Value == "003") {	//경정
			fcGetSummary(objGrd, 3, "Y");
			fcGetSummary(objGrd, 6, "Y");
			fcGetSummary(objGrd, 9, "Y");
			fcGetSummary(objGrd, 10, "Y");
			fcGetSummary(objGrd, 13, "Y");
			fcGetSummary(objGrd, 26, "Y");
		}
	} else {
		objGrd = tab0.tab2.grdDIVTMSCNT;
		///

	}
	objGrd.redraw = true;
}

