function fcInit(obj) {
    dsDIVTMSCNT.ClearData();

	// ÇöÀç³¯Â¥ ¼ÂÆÃ
	calRaceDaySt.Value = substr(fcd_GetSysdate("YYYYMMDD"),0,4)+"0101"; // ÇöÀç³âµµÀÇ 1¿ù 1ÀÏ
	calRaceDayEnd.Value	= fcd_GetSysdate("YYYYMMDD");						// ¿À´ÃÀÌ ±âº»°ª	    
}

/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã¼Å© Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //Á¶È¸Á¶°Ç Ã¼Å©¹× trimÃ³¸®

	dsDIVTMSCNT.ClearData();  //±âÁ¸ µ¥ÀÌÅÍ¼Â ÃÊ±âÈ­
	dsDivTmsManCnt.ClearData();

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm3060-service:searchDIVTMSCNT01";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDIVTMSCNT=dsDIVTMSCNT dsDivTmsManCnt=dsDivTmsManCnt";

	fcd_AddParam("MEET_CD", rdoMeetCd.Value);
	fcd_AddParam("RACE_DAY_ST", calRaceDaySt.Value);
	fcd_AddParam("RACE_DAY_END", calRaceDayEnd.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


