function fcInit(obj) {
    dsDIVDay.ClearData();    

	calRaceDaySt.Value = substr(fcd_GetSysdate("YYYYMMDD"),0,4)+"0101"; // ÇöÀç³âµµÀÇ 1¿ù 1ÀÏ
	calRaceDayEnd.Value	= fcd_GetSysdate("YYYYMMDD");						// ¿À´ÃÀÌ ±âº»°ª		
}

/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã¼Å© Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //Á¶È¸Á¶°Ç Ã¼Å©¹× trimÃ³¸®

	dsDIVDay.ClearData();       //±âÁ¸ µ¥ÀÌÅÍ¼Â ÃÊ±âÈ­

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm3080-service:searchDIVDay01";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDIVDay=dsDIVDay ";

    fcd_AddParam("MEET_CD",      rdoMeetCd.Value);			// °æ·û±¸ºÐÄÚµå	
	fcd_AddParam("RACE_DAY_ST",  calRaceDaySt.Value);	// °Ë»ö ½ÃÀÛÀÏ
	fcd_AddParam("RACE_DAY_END", calRaceDayEnd.Value);	// °Ë»ö Á¾·áÀÏ	
	fcd_AddParam("OPT",          rdoOPT.Value);					// °æ·û±¸ºÐÄÚµå	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


