/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "rsm3080-service:searchDIVDay01" ) {

		fcSetColVisible();
	}

	if ( sServiceName == "rsm3080-service:search_DD_DIVDay01" ) {
	}

	// ÀÏÀÚ/¿ù ¿É¼Çº° º¯°æ
	if(rdoOPT.Value=="MM")
	{
		tabSales.tab1.grdDIVDay.SetCellProp("head",0,"text","¿ù");
		tabSales.tab2.grdDIVDayGrdCdDetail.SetCellProp("head",0,"text","¿ù");
	}
	else
	{
		tabSales.tab1.grdDIVDay.SetCellProp("head",0,"text","ÀÏÀÚ");
		tabSales.tab2.grdDIVDayGrdCdDetail.SetCellProp("head",0,"text","ÀÏÀÚ");
	}
	tabSales.tab1.grdDIVDay.SetFocus();
}

