function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();

	var headTitle = "¹Ì»ç¸®";
	if (strtext == "°æÁ¤") headTitle = "±¤¸í";

	TabSales.tab1.grdDIVDay.SetCellProp("head",        21, "text", headTitle);
	TabSales.tab2.grdDIVDayGrdCdDetail.SetCellProp("head",     21, "text", headTitle);
	TabSales.tab2.grdDIVDayGrdCdDetail.SetCellProp("head",     43, "text", headTitle);
}

function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// °æÁÖ °Ë»ö ½ÃÀÛ ÀÏÀÚ¿¡¼­ ENTER_KEY ´­·¶À» °æ¿ì
		case "calRaceDaySt" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;
		// °æÁÖ °Ë»ö ¸¶Áö¸· ÀÏÀÚ¿¡¼­ ENTER_KEY ´­·¶À» °æ¿ì
		case "calRaceDayEnd" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;

	}
}

// Æ¯Á¤ÄÞÆ÷³ÍÆ®(±×¸®µå) ¸®»çÀÌÁî
function fcSetResize(obj,nX,nY,nState)
{	
	var arrObj = array(-1);
	arrObj[0] = "grdDIVDay";
	arrObj[1] = "grdDIVDayGrdCdDetail";


	// ³ÐÀÌ *********************************************
	var nWidth  = nX - 12;
	var nHeight = nY - 70;
	if (nHeight < 0) nHeight=576;	
	if( nX <= 820){
		tabSales.tab1.grdDIVDay.Autofit = false;		
		tabSales.tab1.grdDIVDay.width = 801;
		tabSales.tab2.grdDIVDayGrdCdDetail.Autofit = false;		
		tabSales.tab2.grdDIVDayGrdCdDetail.width = 801;

		tabSales.width = 801;

		Shape1.Width 	= 801;	
		divBtnLst.Left 	= this.width - 393;			
		Static0.Left 	= this.width - 91;
	} else {	
		tabSales.width = nWidth - 13;
		tabSales.tab1.grdDIVDay.width = nWidth - 25;
		tabSales.tab2.grdDIVDayGrdCdDetail.width = nWidth - 25;
		if( nX > 2400 )	{
			tabSales.tab1.grdDIVDay.Autofit = true;		
			tabSales.tab2.grdDIVDayGrdCdDetail.Autofit = true;		
		}else{
			tabSales.tab1.grdDIVDay.Autofit = false;	
			tabSales.tab2.grdDIVDayGrdCdDetail.Autofit = false;	
		}

		// °øÅë
		Shape1.Width 	= nWidth-36;
		divBtnLst.Left 	= this.width - 385;	
		Static0.Left	= this.width - 95;
	}


	// ³ôÀÌ  **********************************************
	if (nHeight <= 576) {
		tabSales.Height = 496;
		tabSales.tab1.grdDIVDay.Height = 469;
		tabSales.tab2.grdDIVDayGrdCdDetail.Height = 469;
	} else {
		tabSales.Height = nHeight - 8;
		tabSales.tab1.grdDIVDay.Height = nHeight-40;
		tabSales.tab2.grdDIVDayGrdCdDetail.Height = nHeight-40;
	}
}

// ÇØ´ç Æû»çÀÌÁî º¯°æ½Ã
function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy,nState);
}

function fcGetSummary(grdObj, nCell, sSetWidth)
{
	var nColSum = 0;
	nColSum = grdObj.GetCellText("Summary", -1, nCell);	
	if (sSetWidth == "Y") {
Trace("nCell="+nCell +", nColSum="+nColSum);	
		if (nColSum == 0) grdObj.SetColProp(nCell, "width", 0);
		else              grdObj.SetColProp(nCell, "width", 100);
	}

}
function fcSetColVisible()
{
	tabSales.tab1.grdDIVDay.redraw = false;
	fcGetSummary(tabSales.tab1.grdDIVDay,  6, "Y");
	fcGetSummary(tabSales.tab1.grdDIVDay, 19, "Y");
	fcGetSummary(tabSales.tab1.grdDIVDay, 21, "Y");
	if (rdoMeetCd.Value == "003") {	//°æÁ¤
		fcGetSummary(tabSales.tab1.grdDIVDay, 3, "Y");
		fcGetSummary(tabSales.tab1.grdDIVDay, 6, "Y");
		fcGetSummary(tabSales.tab1.grdDIVDay, 9, "Y");
		fcGetSummary(tabSales.tab1.grdDIVDay, 10, "Y");
		fcGetSummary(tabSales.tab1.grdDIVDay, 13, "Y");
	}
	tabSales.tab1.grdDIVDay.redraw = true;

	tabSales.tab2.grdDIVDayGrdCdDetail.redraw = false;
	fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail,  6, "Y");
	fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 19, "Y");
	fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 21, "Y");
	fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 24, "Y");
	fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 28, "Y");
	if (rdoMeetCd.Value == "003") {	//°æÁ¤
		fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 3, "Y");
		fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 6, "Y");
		fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 9, "Y");
		fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 10, "Y");
		fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 13, "Y");
		fcGetSummary(tabSales.tab2.grdDIVDayGrdCdDetail, 26, "Y");
	}
	tabSales.tab2.grdDIVDayGrdCdDetail.redraw = true;
}
